#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <sys/stat.h>
#include <errno.h>

/* Probe logging helper: append human-readable logs to /tmp/logs/582.txt */
static void
probe_logf (const char *fmt, ...)
{
  FILE *f;
  va_list ap;

  /* try to create directory if it does not exist; ignore errors */
  (void) mkdir("/tmp/logs", 0777);

  f = fopen("/tmp/logs/582.txt", "a");
  if (NULL == f)
    return;

  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  fprintf (f, "\n");
  va_end (ap);

  fclose (f);
}

static bool
process_request_target (struct MHD_Connection *c)
{
#ifdef _DEBUG
  size_t params_len;
#endif /* _DEBUG */
  mhd_assert (MHD_CONNECTION_REQ_LINE_RECEIVING == c->state);
  mhd_assert (NULL == c->rq.url);
  mhd_assert (0 == c->rq.url_len);
  mhd_assert (NULL != c->rq.hdrs.rq_line.rq_tgt);
  mhd_assert ((NULL == c->rq.hdrs.rq_line.rq_tgt_qmark) || \
              (c->rq.hdrs.rq_line.rq_tgt <= c->rq.hdrs.rq_line.rq_tgt_qmark));
  mhd_assert ((NULL == c->rq.hdrs.rq_line.rq_tgt_qmark) || \
              (c->rq.req_target_len > \
               (size_t) (c->rq.hdrs.rq_line.rq_tgt_qmark \
                         - c->rq.hdrs.rq_line.rq_tgt)));

  /* Log entry and basic state */
  probe_logf ("ENTER process_request_target: state=%d, rq_tgt=%p, req_target_len=%zu, rq_tgt_qmark=%p",
              (int) c->state,
              (void *) c->rq.hdrs.rq_line.rq_tgt,
              c->rq.req_target_len,
              (void *) c->rq.hdrs.rq_line.rq_tgt_qmark);

  /* Log callback before the request-target is modified/decoded */
  if (NULL != c->daemon->uri_log_callback)
  {
    c->rq.client_aware = true;
    c->rq.client_context =
      c->daemon->uri_log_callback (c->daemon->uri_log_callback_cls,
                                   c->rq.hdrs.rq_line.rq_tgt,
                                   c);
    probe_logf ("uri_log_callback invoked: client_context=%p",
                (void *) c->rq.client_context);
  }

  if (NULL != c->rq.hdrs.rq_line.rq_tgt_qmark)
  {
#ifdef _DEBUG
    params_len =
      c->rq.req_target_len
      - (size_t) (c->rq.hdrs.rq_line.rq_tgt_qmark - c->rq.hdrs.rq_line.rq_tgt);
    probe_logf ("DEBUG: qmark present: params_len=%zu", params_len);
#else
    probe_logf ("RELEASE: qmark present");
#endif /* _DEBUG */
    c->rq.hdrs.rq_line.rq_tgt_qmark[0] = 0; /* Replace '?' with zero termination */
    if (MHD_NO == MHD_parse_arguments_ (c,
                                        MHD_GET_ARGUMENT_KIND,
                                        c->rq.hdrs.rq_line.rq_tgt_qmark + 1,
                                        &connection_add_header,
                                        c))
    {
      probe_logf ("MHD_parse_arguments_ failed; aborting request target processing");
      mhd_assert (MHD_CONNECTION_REQ_LINE_RECEIVING != c->state);
      return false;
    }
    probe_logf ("MHD_parse_arguments_ succeeded");
  }
#ifdef _DEBUG
  else
    params_len = 0;
#ifdef _DEBUG
  probe_logf ("DEBUG: no qmark: params_len=%zu", params_len);
#endif /* _DEBUG */
#endif /* _DEBUG */

  /* Capture the exact values used by the diagnostic assertion.  The
     strlen() evaluation performed here is only executed in debug builds
     to avoid changing release behaviour (the original assertion may be
     compiled out there). This provides evidence that strlen() is only
     evaluated for diagnostics. */
#ifdef _DEBUG
  {
    size_t tgt_strlen = strlen (c->rq.hdrs.rq_line.rq_tgt);
    probe_logf ("DEBUG: About to evaluate assertion: strlen(rq_tgt)=%zu, req_target_len=%zu, params_len=%zu",
                tgt_strlen,
                c->rq.req_target_len,
                params_len);
  }
#else
  probe_logf ("RELEASE: debug-only assertion evaluation skipped");
#endif /* _DEBUG */

  mhd_assert (strlen (c->rq.hdrs.rq_line.rq_tgt) == \
              c->rq.req_target_len - params_len);

  /* Finally unescape URI itself */
  c->rq.url_len =
    c->daemon->unescape_callback (c->daemon->unescape_callback_cls,
                                  c,
                                  c->rq.hdrs.rq_line.rq_tgt);
  c->rq.url = c->rq.hdrs.rq_line.rq_tgt;

  probe_logf ("EXIT process_request_target: url=%p, url_len=%zu, return=true",
              (void *) c->rq.url,
              c->rq.url_len);

  return true;
}
