#include <stdio.h>
#include <stdarg.h>

static void
log_msg(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/2357.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  fprintf(f, "\n");
  va_end(ap);
  fclose(f);
}

static void
process_value (struct MHD_PostProcessor *pp,
               const char *value_start,
               const char *value_end,
               const char *last_escape)
{
  char xbuf[XBUF_SIZE + 1];
  size_t xoff;

  log_msg("ENTER process_value: pp=%p, value_start=%p, value_end=%p, last_escape=%p, pp->xbuf_pos=%zu, pp->value_offset=%zu",
          (void *) pp,
          (const void *) value_start,
          (const void *) value_end,
          (const void *) last_escape,
          pp->xbuf_pos,
          pp->value_offset);

  mhd_assert (pp->xbuf_pos < sizeof (xbuf));
  /* 'value_start' and 'value_end' must be either both non-NULL or both NULL */
  mhd_assert ( (NULL == value_start) || (NULL != value_end) );
  mhd_assert ( (NULL != value_start) || (NULL == value_end) );
  mhd_assert ( (NULL == last_escape) || (NULL != value_start) );
  /* move remaining input from previous round into processing buffer */
  if (0 != pp->xbuf_pos)
    memcpy (xbuf,
            pp->xbuf,
            pp->xbuf_pos);
  xoff = pp->xbuf_pos;
  pp->xbuf_pos = 0;
  if ( (NULL != last_escape) &&
       (((size_t) (value_end - last_escape)) < sizeof (pp->xbuf)) )
  {
    mhd_assert (value_end >= last_escape);
    pp->xbuf_pos = (size_t) (value_end - last_escape);
    memcpy (pp->xbuf,
            last_escape,
            (size_t) (value_end - last_escape));
    value_end = last_escape;
    log_msg("Handled last_escape: new value_end=%p, pp->xbuf_pos=%zu",
            (const void *) value_end,
            pp->xbuf_pos);
  }
  while ( (value_start != value_end) ||
          (pp->must_ikvi) ||
          (xoff > 0) )
  {
    size_t delta = (size_t) (value_end - value_start);
    bool cut = false;
    size_t clen = 0;

    mhd_assert (value_end >= value_start);

    log_msg("LOOP START: value_start=%p, value_end=%p, raw_delta=%zu, xoff=%zu, must_ikvi=%d",
            (const void *) value_start,
            (const void *) value_end,
            delta,
            xoff,
            (int) pp->must_ikvi);

    if (delta > XBUF_SIZE - xoff)
      delta = XBUF_SIZE - xoff;
    /* move (additional) input into processing buffer */
    if (0 != delta)
    {
      log_msg("About to memcpy %zu bytes from value_start=%p into xbuf+%zu",
              delta,
              (const void *) value_start,
              xoff);
      memcpy (&xbuf[xoff],
              value_start,
              delta);
      xoff += delta;
      value_start += delta;
      log_msg("After memcpy: xoff=%zu, value_start (advanced)=%p",
              xoff,
              (const void *) value_start);
    }
    /* find if escape sequence is at the end of the processing buffer;
       if so, exclude those from processing (reduce delta to point at
       end of processed region) */
    if ( (xoff > 0) &&
         ('%' == xbuf[xoff - 1]) )
    {
      cut = (xoff != XBUF_SIZE);
      xoff--;
      log_msg("Escape at xbuf[xoff-1]: cut=%d, adjusted xoff=%zu",
              (int) cut,
              xoff);
      if (cut)
      {
        /* move escape sequence into buffer for next function invocation */
        pp->xbuf[0] = '%';
        pp->xbuf_pos = 1;
        log_msg("Stored single '%' into pp->xbuf for next invocation");
      }
      else
      {
        /* just skip escape sequence for next loop iteration */
        delta = xoff;
        clen = 1;
        log_msg("Skip escape for next iteration: delta=%zu, clen=%zu",
                delta,
                clen);
      }
    }
    else if ( (xoff > 1) &&
              ('%' == xbuf[xoff - 2]) )
    {
      cut = (xoff != XBUF_SIZE);
      xoff -= 2;
      log_msg("Escape at xbuf[xoff-2]: cut=%d, adjusted xoff=%zu",
              (int) cut,
              xoff);
      if (cut)
      {
        /* move escape sequence into buffer for next function invocation */
        memcpy (pp->xbuf,
                &xbuf[xoff],
                2);
        pp->xbuf_pos = 2;
        log_msg("Stored '%%' and following byte into pp->xbuf for next invocation");
      }
      else
      {
        /* just skip escape sequence for next loop iteration */
        delta = xoff;
        clen = 2;
        log_msg("Skip 2-byte escape for next iteration: delta=%zu, clen=%zu",
                delta,
                clen);
      }
    }
    mhd_assert (xoff < sizeof (xbuf));
    /* unescape */
    xbuf[xoff] = '\0';        /* 0-terminate in preparation */
    if (0 != xoff)
    {
      MHD_unescape_plus (xbuf);
      xoff = MHD_http_unescape (xbuf);
      log_msg("After unescape: xbuf (first bytes)='%c%c%c', xoff=%zu",
              xbuf[0] ? xbuf[0] : '?',
              xbuf[1] ? xbuf[1] : '?',
              xbuf[2] ? xbuf[2] : '?',
              xoff);
    }
    /* finally: call application! */
    if (pp->must_ikvi || (0 != xoff) )
    {
      pp->must_ikvi = false;
      log_msg("Calling ikvi: cls=%p, key=%p, value_offset=%zu, xoff=%zu",
              (void *) pp->cls,
              (const void *) &pp[1],
              pp->value_offset,
              xoff);
      if (MHD_NO == pp->ikvi (pp->cls,
                              MHD_POSTDATA_KIND,
                              (const char *) &pp[1],      /* key */
                              NULL,
                              NULL,
                              NULL,
                              xbuf,
                              pp->value_offset,
                              xoff))
      {
        pp->state = PP_Error;
        log_msg("ikvi returned MHD_NO -> setting state=PP_Error and returning. state=%d",
                (int) pp->state);
        return;
      }
      else
      {
        log_msg("ikvi returned success");
      }
    }
    pp->value_offset += xoff;
    log_msg("Updated pp->value_offset=%zu", pp->value_offset);
    if (cut)
    {
      log_msg("Breaking out of loop due to cut=true");
      break;
    }
    if (0 != clen)
    {
      xbuf[delta] = '%';        /* undo 0-termination */
      memmove (xbuf,
               &xbuf[delta],
               clen);
      log_msg("Prepared xbuf for next iteration: clen=%zu", clen);
    }
    xoff = clen;
  }

  log_msg("EXIT process_value: pp=%p, final value_start=%p, value_end=%p, pp->xbuf_pos=%zu, pp->value_offset=%zu",
          (void *) pp,
          (const void *) value_start,
          (const void *) value_end,
          pp->xbuf_pos,
          pp->value_offset);
}
