#include <stdio.h>
#include <stdarg.h>
#include <time.h>

/* Probe logging helper: append logs to /tmp/logs/1223.txt */
static void mhd_probe_logf(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/1223.txt", "a");
  if (NULL == f)
    return;
  time_t t = time(NULL);
  if (t != (time_t)(-1))
    fprintf(f, "[%ld] ", (long)t);
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fprintf(f, "\n");
  fclose(f);
}

static enum MHD_Result
resume_suspended_connections (struct MHD_Daemon *daemon)
{
  struct MHD_Connection *pos;
  struct MHD_Connection *prev = NULL;
  enum MHD_Result ret;
  const bool used_thr_p_c = (0 != (daemon->options
                                   & MHD_USE_THREAD_PER_CONNECTION));
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  mhd_assert (NULL == daemon->worker_pool);
  mhd_assert ( (! MHD_D_IS_USING_THREADS_ (daemon)) || \
               MHD_thread_handle_ID_is_current_thread_ (daemon->tid) );
#endif

  /* Log entry and important daemon state */
  mhd_probe_logf("enter resume_suspended_connections: daemon=%p used_thr_p_c=%d options=0x%08x resuming=%d shutdown=%d",
                 (void *)daemon, (int)used_thr_p_c, (unsigned)daemon->options,
                 (int)daemon->resuming, (int)daemon->shutdown);

  ret = MHD_NO;
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  MHD_mutex_lock_chk_ (&daemon->cleanup_connection_mutex);
#endif

  if (daemon->resuming)
  {
    prev = daemon->suspended_connections_tail;
    /* During shutdown check for resuming is forced. */
    mhd_assert ((NULL != prev) || (daemon->shutdown) || \
                (0 != (daemon->options & MHD_ALLOW_UPGRADE)));
    mhd_probe_logf("resuming true: suspended_tail=%p", (void *)prev);
  }

  daemon->resuming = false;

  while (NULL != (pos = prev))
  {
#ifdef UPGRADE_SUPPORT
    struct MHD_UpgradeResponseHandle *const urh = pos->urh;
#else  /* ! UPGRADE_SUPPORT */
    static const void *const urh = NULL;
#endif /* ! UPGRADE_SUPPORT */
    prev = pos->prev;

    /* Log per-connection info before decision */
#ifdef UPGRADE_SUPPORT
    if (NULL != urh)
    {
      mhd_probe_logf("conn pos=%p prev=%p urh=%p pos->resuming=%d urh->was_closed=%d urh->clean_ready=%d",
                     (void *)pos, (void *)prev, (void *)urh,
                     (int)pos->resuming, (int)urh->was_closed, (int)urh->clean_ready);
    }
    else
    {
      mhd_probe_logf("conn pos=%p prev=%p urh=NULL pos->resuming=%d",
                     (void *)pos, (void *)prev, (int)pos->resuming);
    }
#else
    mhd_probe_logf("conn pos=%p prev=%p UPGRADE_SUPPORT not compiled pos->resuming=%d",
                   (void *)pos, (void *)prev, (int)pos->resuming);
#endif

    if ( (! pos->resuming)
#ifdef UPGRADE_SUPPORT
         || ( (NULL != urh) &&
              ( (! urh->was_closed) ||
                (! urh->clean_ready) ) )
#endif /* UPGRADE_SUPPORT */
         )
    {
      /* Log why we continue (skip resume) */
#ifdef UPGRADE_SUPPORT
      if (!pos->resuming)
      {
        mhd_probe_logf("skip resume: pos=%p reason=not resuming", (void *)pos);
      }
      else if ((NULL != urh) && ((! urh->was_closed) || (! urh->clean_ready)))
      {
        mhd_probe_logf("skip resume: pos=%p reason=urh present and not closed/clean (was_closed=%d clean_ready=%d)",
                       (void *)pos, (int)urh->was_closed, (int)urh->clean_ready);
      }
      else
      {
        mhd_probe_logf("skip resume: pos=%p reason=other", (void *)pos);
      }
#else
      mhd_probe_logf("skip resume: pos=%p reason=not resuming (no upgrade support)", (void *)pos);
#endif
      continue;
    }

    ret = MHD_YES;
    mhd_assert (pos->suspended);
    mhd_probe_logf("resuming connection: pos=%p setting ret=MHD_YES", (void *)pos);
    DLL_remove (daemon->suspended_connections_head,
                daemon->suspended_connections_tail,
                pos);
    pos->suspended = false;
    if (NULL == urh)
    {
      mhd_probe_logf("inserting resumed connection into normal connections list: pos=%p", (void *)pos);
      DLL_insert (daemon->connections_head,
                  daemon->connections_tail,
                  pos);
      if (! used_thr_p_c)
      {
        /* Reset timeout timer on resume. */
        if (0 != pos->connection_timeout_ms)
          pos->last_activity = MHD_monotonic_msec_counter ();

        if (pos->connection_timeout_ms == daemon->connection_timeout_ms)
          XDLL_insert (daemon->normal_timeout_head,
                       daemon->normal_timeout_tail,
                       pos);
        else
          XDLL_insert (daemon->manual_timeout_head,
                       daemon->manual_timeout_tail,
                       pos);
      }
#ifdef EPOLL_SUPPORT
      if (MHD_D_IS_USING_EPOLL_ (daemon))
      {
        if (0 != (pos->epoll_state & MHD_EPOLL_STATE_IN_EREADY_EDLL))
          MHD_PANIC ("Resumed connection was already in EREADY set.\n");
        /* we always mark resumed connections as ready, as we
           might have missed the edge poll event during suspension */
        EDLL_insert (daemon->eready_head,
                     daemon->eready_tail,
                     pos);
        pos->epoll_state |= MHD_EPOLL_STATE_IN_EREADY_EDLL   \
                            | MHD_EPOLL_STATE_READ_READY
                            | MHD_EPOLL_STATE_WRITE_READY;
        pos->epoll_state &= ~((enum MHD_EpollState) MHD_EPOLL_STATE_SUSPENDED);
      }
#endif
    }
#ifdef UPGRADE_SUPPORT
    else
    {
      /* Data forwarding was finished (for TLS connections) AND
       * application was closed upgraded connection.
       * Insert connection into cleanup list. */
      mhd_probe_logf("inserting resumed upgraded connection into cleanup list: pos=%p urh=%p was_closed=%d clean_ready=%d",
                     (void *)pos, (void *)urh, (int)urh->was_closed, (int)urh->clean_ready);

      if ( (NULL != daemon->notify_completed) &&
           (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) &&
           (pos->rq.client_aware) )
      {
        mhd_probe_logf("calling notify_completed for pos=%p", (void *)pos);
        daemon->notify_completed (daemon->notify_completed_cls,
                                  pos,
                                  &pos->rq.client_context,
                                  MHD_REQUEST_TERMINATED_COMPLETED_OK);
        pos->rq.client_aware = false;
      }
      DLL_insert (daemon->cleanup_head,
                  daemon->cleanup_tail,
                  pos);
      daemon->data_already_pending = true;
    }
#endif /* UPGRADE_SUPPORT */
    pos->resuming = false;
    mhd_probe_logf("finished handling pos=%p resuming cleared", (void *)pos);
  }
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);
#endif
  if ( (used_thr_p_c) &&
       (MHD_NO != ret) )
  {   /* Wake up suspended connections. */
    if (! MHD_itc_activate_ (daemon->itc,
                             "w"))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Failed to signal resume of connection via " \
                   "inter-thread communication channel.\n"));
#endif
      mhd_probe_logf("failed to signal resume via itc for daemon=%p", (void *)daemon);
    }
    else
    {
      mhd_probe_logf("signalled resume via itc for daemon=%p", (void *)daemon);
    }
  }
  mhd_probe_logf("exit resume_suspended_connections: ret=%d", (int)ret);
  return ret;
}
