static void
mhd_log_epoll_state(const char *stage,
                    struct MHD_Daemon *daemon,
                    MHD_socket ls,
                    int epoll_fd,
                    void *data_ptr)
{
  FILE *f = fopen("/tmp/logs/1243.txt", "a");
  if (! f)
    return;

  /* Log basic control/state to help reproduce pointer-storage scenario. */
  fprintf(f, "[mhd_epoll_probe] stage=%s\n", stage);
  fprintf(f, "  daemon=%p\n", (void *) daemon);
  fprintf(f, "  epoll_fd=%d\n", epoll_fd);
  fprintf(f, "  listen_fd=%lld\n", (long long) ls);
  /* daemon->options may be of integer type; cast to unsigned long long for portability */
  fprintf(f, "  daemon->options=0x%llx\n", (unsigned long long) daemon->options);
  fprintf(f, "  event.data.ptr=%p\n", data_ptr);
  fprintf(f, "----------------------------------------------------\n");
  fclose(f);
}

static enum MHD_Result
setup_epoll_to_listen (struct MHD_Daemon *daemon)
{
  struct epoll_event event;
  MHD_socket ls;

  mhd_assert (MHD_D_IS_USING_EPOLL_ (daemon));
  mhd_assert (0 == (daemon->options & MHD_USE_THREAD_PER_CONNECTION));
  mhd_assert ( (! MHD_D_IS_USING_THREADS_ (daemon)) || \
               (MHD_INVALID_SOCKET != (ls = daemon->listen_fd)) || \
               MHD_ITC_IS_VALID_ (daemon->itc) );
  daemon->epoll_fd = setup_epoll_fd (daemon);
  if (! MHD_D_IS_USING_THREADS_ (daemon)
      && (0 != (daemon->options & MHD_USE_AUTO)))
  {
    /* Application requested "MHD_USE_AUTO", probably MHD_get_fdset() will be
       used.
       Make sure that epoll FD is suitable for fd_set.
       Actually, MHD_get_fdset() is allowed for MHD_USE_EPOLL direct,
       but most probably direct requirement for MHD_USE_EPOLL means that
       epoll FD will be used directly. This logic is fuzzy, but better
       than nothing with current MHD API. */
    if (! MHD_D_DOES_SCKT_FIT_FDSET_ (daemon->epoll_fd, daemon))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("The epoll FD is too large to be used with fd_set.\n"));
#endif /* HAVE_MESSAGES */
      return MHD_NO;
    }
  }
  if (-1 == daemon->epoll_fd)
    return MHD_NO;
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
  if (0 != (MHD_ALLOW_UPGRADE & daemon->options))
  {
    daemon->epoll_upgrade_fd = setup_epoll_fd (daemon);
    if (MHD_INVALID_SOCKET == daemon->epoll_upgrade_fd)
      return MHD_NO;
  }
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */
  if ( (MHD_INVALID_SOCKET != (ls = daemon->listen_fd)) &&
       (! daemon->was_quiesced) )
  {
    event.events = EPOLLIN | EPOLLRDHUP;

    /* Probe: log state before assigning the daemon pointer into event.data.ptr */
    mhd_log_epoll_state("before_assign_listen_marker", daemon, ls,
                        daemon->epoll_fd, NULL);

    event.data.ptr = daemon;

    /* Probe: log state after assignment so the pointer stored in the epoll
       event is captured for later analysis. */
    mhd_log_epoll_state("after_assign_listen_marker", daemon, ls,
                        daemon->epoll_fd, event.data.ptr);

    if (0 != epoll_ctl (daemon->epoll_fd,
                        EPOLL_CTL_ADD,
                        ls,
                        &event))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Call to epoll_ctl failed: %s\n"),
                MHD_socket_last_strerr_ ());
#endif
      /* Log the failure of epoll_ctl with state to help reproduce */
      mhd_log_epoll_state("epoll_ctl_failed_listen", daemon, ls,
                          daemon->epoll_fd, event.data.ptr);
      return MHD_NO;
    }
    daemon->listen_socket_in_epoll = true;
  }

  if (MHD_ITC_IS_VALID_ (daemon->itc))
  {
    event.events = EPOLLIN | EPOLLRDHUP;

    /* Probe: log the original const marker and the casted result */
    mhd_log_epoll_state("before_assign_itc_marker", daemon, ls,
                        daemon->epoll_fd, (void *) epoll_itc_marker);

    event.data.ptr = _MHD_DROP_CONST (epoll_itc_marker);

    mhd_log_epoll_state("after_assign_itc_marker", daemon, ls,
                        daemon->epoll_fd, event.data.ptr);

    if (0 != epoll_ctl (daemon->epoll_fd,
                        EPOLL_CTL_ADD,
                        MHD_itc_r_fd_ (daemon->itc),
                        &event))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Call to epoll_ctl failed: %s\n"),
                MHD_socket_last_strerr_ ());
#endif
      /* Log the failure of epoll_ctl for ITC marker as well */
      mhd_log_epoll_state("epoll_ctl_failed_itc", daemon, ls,
                          daemon->epoll_fd, event.data.ptr);
      return MHD_NO;
    }
  }
  return MHD_YES;
}
