static void write_log_header(FILE *f)
{
  if (!f) return;
  fprintf(f, "--- thread_main_connection_upgrade log ---\n");
}

static FILE *open_log_file(void)
{
  FILE *f = fopen("/tmp/logs/1253.txt", "a");
  return f;
}

static void probe_fdset_dump(FILE *f, const char *label, const fd_set *rs, const fd_set *ws, const fd_set *es, int max_fd)
{
  if (!f) return;
  int i;
  int limit = (max_fd >= 0) ? max_fd : FD_SETSIZE - 1;
  if (limit >= FD_SETSIZE) limit = FD_SETSIZE - 1;
  fprintf(f, "%s: max_fd=%d limit=%d\n", label, max_fd, limit);
  for (i = 0; i <= limit; ++i)
  {
    int r = rs ? (FD_ISSET(i, rs) ? 1 : 0) : 0;
    int w = ws ? (FD_ISSET(i, ws) ? 1 : 0) : 0;
    int e = es ? (FD_ISSET(i, es) ? 1 : 0) : 0;
    if (r || w || e)
      fprintf(f, " fd=%3d: R=%d W=%d E=%d\n", i, r, w, e);
  }
  fprintf(f, "--- end %s\n", label);
}

static void probe_urh_state(FILE *f, struct MHD_UpgradeResponseHandle *urh)
{
  if (!f || !urh) return;
  fprintf(f, "urh: in_buffer_size=%zu, out_buffer_size=%zu, in_buffer_used=%zu, out_buffer_used=%zu\n",
          urh->in_buffer_size, urh->out_buffer_size, urh->in_buffer_used, urh->out_buffer_used);
}

static void
thread_main_connection_upgrade (struct MHD_Connection *con)
{
#ifdef HTTPS_SUPPORT
  struct MHD_UpgradeResponseHandle *urh = con->urh;
  struct MHD_Daemon *daemon = con->daemon;

  mhd_assert ( (! MHD_D_IS_USING_THREADS_ (daemon)) || \
               MHD_thread_handle_ID_is_current_thread_ (con->tid) );
  /* Here, we need to bi-directionally forward
     until the application tells us that it is done
     with the socket; */
  if ( (0 != (daemon->options & MHD_USE_TLS)) &&
       MHD_D_IS_USING_SELECT_ (daemon))
  {
    FILE *logf = open_log_file();
    write_log_header(logf);
    while ( (0 != urh->in_buffer_size) ||
            (0 != urh->out_buffer_size) ||
            (0 != urh->in_buffer_used) ||
            (0 != urh->out_buffer_used) )
    {
      /* use select */
      fd_set rs;
      fd_set ws;
      fd_set es;
      MHD_socket max_fd;
      int num_ready;
      bool result;

      /* Log iteration entry and urh state */
      probe_urh_state(logf, urh);

      FD_ZERO (&rs);
      FD_ZERO (&ws);
      FD_ZERO (&es);
      max_fd = MHD_INVALID_SOCKET;
      result = urh_to_fdset (urh,
                             &rs,
                             &ws,
                             &es,
                             &max_fd,
                             FD_SETSIZE);
      if (! result)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (con->daemon,
                  _ ("Error preparing select.\n"));
#endif
        if (logf)
          fprintf(logf, "urh_to_fdset returned false\n");
        break;
      }

      /* Log fdset prepared by urh_to_fdset (before select) */
      probe_fdset_dump(logf, "fdsets_before_select", &rs, &ws, &es, (int)max_fd);

      /* FIXME: does this check really needed? */
      if (MHD_INVALID_SOCKET != max_fd)
      {
        struct timeval *tvp;
        struct timeval tv;
        if (((con->tls_read_ready) &&
             (urh->in_buffer_used < urh->in_buffer_size)) ||
            (daemon->shutdown))
        {         /* No need to wait if incoming data is already pending in TLS buffers. */
          tv.tv_sec = 0;
          tv.tv_usec = 0;
          tvp = &tv;
        }
        else
          tvp = NULL;
        if (logf)
          fprintf(logf, "calling select max_fd=%d tvp=%p tls_read_ready=%d shutdown=%d\n",
                  (int)max_fd, (void*)tvp, (int)con->tls_read_ready, (int)daemon->shutdown);
        num_ready = MHD_SYS_select_ (max_fd + 1,
                                     &rs,
                                     &ws,
                                     &es,
                                     tvp);
      }
      else
        num_ready = 0;

      if (logf)
        fprintf(logf, "select returned num_ready=%d\n", num_ready);

      if (num_ready < 0)
      {
        const int err = MHD_socket_get_error_ ();

        if (MHD_SCKT_ERR_IS_EINTR_ (err))
          continue;
#ifdef HAVE_MESSAGES
        MHD_DLOG (con->daemon,
                  _ ("Error during select (%d): `%s'\n"),
                  err,
                  MHD_socket_strerr_ (err));
#endif
        if (logf)
          fprintf(logf, "select error %d: %s\n", err, MHD_socket_strerr_(err));
        break;
      }

      /* Dump fdsets just before calling urh_from_fdset so we have a snapshot */
      probe_fdset_dump(logf, "fdsets_pre_urh_from_fdset", &rs, &ws, &es, FD_SETSIZE);

      urh_from_fdset (urh,
                      &rs,
                      &ws,
                      &es,
                      (int) FD_SETSIZE);

      /* Dump fdsets after the call to detect any modifications done by urh_from_fdset */
      probe_fdset_dump(logf, "fdsets_post_urh_from_fdset", &rs, &ws, &es, FD_SETSIZE);

      if (logf)
        fprintf(logf, "calling process_urh\n");
      process_urh (urh);
    }
    if (logf)
      fclose(logf);
  }
#ifdef HAVE_POLL
  else if (0 != (daemon->options & MHD_USE_TLS))
  {
    /* use poll() */
    struct pollfd p[2];
    memset (p,
            0,
            sizeof (p));
    p[0].fd = urh->connection->socket_fd;
    p[1].fd = urh->mhd.socket;

    while ( (0 != urh->in_buffer_size) ||
            (0 != urh->out_buffer_size) ||
            (0 != urh->in_buffer_used) ||
            (0 != urh->out_buffer_used) )
    {
      int timeout;

      urh_update_pollfd (urh, p);

      if (((con->tls_read_ready) &&
           (urh->in_buffer_used < urh->in_buffer_size)) ||
          (daemon->shutdown))
        timeout = 0;     /* No need to wait if incoming data is already pending in TLS buffers. */
      else
        timeout = -1;

      if (MHD_sys_poll_ (p,
                         2,
                         timeout) < 0)
      {
        const int err = MHD_socket_get_error_ ();

        if (MHD_SCKT_ERR_IS_EINTR_ (err))
          continue;
#ifdef HAVE_MESSAGES
        MHD_DLOG (con->daemon,
                  _ ("Error during poll: `%s'\n"),
                  MHD_socket_strerr_ (err));
#endif
        break;
      }
      urh_from_pollfd (urh,
                       p);
      process_urh (urh);
    }
  }
  /* end POLL */
#endif
  /* end HTTPS */
#endif /* HTTPS_SUPPORT */
  /* TLS forwarding was finished. Cleanup socketpair. */
  MHD_connection_finish_forward_ (con);
  /* Do not set 'urh->clean_ready' yet as 'urh' will be used
   * in connection thread for a little while. */
}
