#include <stdio.h>
#include <stdarg.h>
#include <time.h>
#include <unistd.h>

/* Probe logging helper: append human-readable events to /tmp/logs/1253.txt */
static void
log_event (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/1253.txt", "a");
  if (f == NULL)
    return;

  time_t t = time (NULL);
  fprintf (f, "[%ld][pid=%d] ", (long) t, (int) getpid ());

  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);

  fprintf (f, "\n");
  fclose (f);
}

static void
thread_main_connection_upgrade (struct MHD_Connection *con)
{
#ifdef HTTPS_SUPPORT
  struct MHD_UpgradeResponseHandle *urh = con->urh;
  struct MHD_Daemon *daemon = con->daemon;

  log_event ("enter thread_main_connection_upgrade: con=%p urh=%p daemon=%p",
             (void *) con, (void *) urh, (void *) daemon);

  mhd_assert ( (! MHD_D_IS_USING_THREADS_ (daemon)) || \
               MHD_thread_handle_ID_is_current_thread_ (con->tid) );
  /* Here, we need to bi-directionally forward
     until the application tells us that it is done
     with the socket; */
  if ( (0 != (daemon->options & MHD_USE_TLS)) &&
       MHD_D_IS_USING_SELECT_ (daemon))
  {
    while ( (0 != urh->in_buffer_size) ||
            (0 != urh->out_buffer_size) ||
            (0 != urh->in_buffer_used) ||
            (0 != urh->out_buffer_used) )
    {
      /* use select */
      fd_set rs;
      fd_set ws;
      fd_set es;
      MHD_socket max_fd;
      int num_ready;
      bool result;

      FD_ZERO (&rs);
      FD_ZERO (&ws);
      FD_ZERO (&es);
      max_fd = MHD_INVALID_SOCKET;
      result = urh_to_fdset (urh,
                             &rs,
                             &ws,
                             &es,
                             &max_fd,
                             FD_SETSIZE);
      log_event ("urh_to_fdset returned %d, max_fd=%d, in_used=%d out_used=%d",
                 (int) result,
                 (int) max_fd,
                 (int) urh->in_buffer_used,
                 (int) urh->out_buffer_used);

      if (! result)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (con->daemon,
                  _ ("Error preparing select.\n"));
#endif
        log_event ("urh_to_fdset failed, breaking out of loop");
        break;
      }
      /* FIXME: does this check really needed? */
      if (MHD_INVALID_SOCKET != max_fd)
      {
        struct timeval *tvp;
        struct timeval tv;
        if (((con->tls_read_ready) &&
             (urh->in_buffer_used < urh->in_buffer_size)) ||
            (daemon->shutdown))
        {         /* No need to wait if incoming data is already pending in TLS buffers. */
          tv.tv_sec = 0;
          tv.tv_usec = 0;
          tvp = &tv;
          log_event ("select: immediate timeout (tls_read_ready=%d, shutdown=%d)",
                     (int) con->tls_read_ready, (int) daemon->shutdown);
        }
        else
          tvp = NULL;
        num_ready = MHD_SYS_select_ (max_fd + 1,
                                     &rs,
                                     &ws,
                                     &es,
                                     tvp);
      }
      else
        num_ready = 0;
      log_event ("select returned num_ready=%d", num_ready);

      if (num_ready < 0)
      {
        const int err = MHD_socket_get_error_ ();

        log_event ("select error detected: err=%d", err);

        if (MHD_SCKT_ERR_IS_EINTR_ (err))
        {
          log_event ("select interrupted by EINTR, continuing");
          continue;
        }
#ifdef HAVE_MESSAGES
        MHD_DLOG (con->daemon,
                  _ ("Error during select (%d): `%s'\n"),
                  err,
                  MHD_socket_strerr_ (err));
#endif
        {
          /* Probe: capture the string returned by MHD_socket_strerr_ to record
             the pointer value and the pointed-to contents. This helps to
             demonstrate whether an immutable literal is being handed back via
             a non-const pointer (the violation scenario). */
          const char *estr = MHD_socket_strerr_ (err);
          if (estr)
            log_event ("MHD_socket_strerr_ returned ptr=%p content='%s'",
                       (void *) estr, estr);
          else
            log_event ("MHD_socket_strerr_ returned NULL ptr");
        }
        break;
      }
      urh_from_fdset (urh,
                      &rs,
                      &ws,
                      &es,
                      (int) FD_SETSIZE);
      process_urh (urh);
    }
  }
#ifdef HAVE_POLL
  else if (0 != (daemon->options & MHD_USE_TLS))
  {
    /* use poll() */
    struct pollfd p[2];
    memset (p,
            0,
            sizeof (p));
    p[0].fd = urh->connection->socket_fd;
    p[1].fd = urh->mhd.socket;

    while ( (0 != urh->in_buffer_size) ||
            (0 != urh->out_buffer_size) ||
            (0 != urh->in_buffer_used) ||
            (0 != urh->out_buffer_used) )
    {
      int timeout;

      urh_update_pollfd (urh, p);

      if (((con->tls_read_ready) &&
           (urh->in_buffer_used < urh->in_buffer_size)) ||
          (daemon->shutdown))
        timeout = 0;     /* No need to wait if incoming data is already pending in TLS buffers. */
      else
        timeout = -1;

      log_event ("poll about to run: timeout=%d p[0].fd=%d p[1].fd=%d",
                 timeout, (int) p[0].fd, (int) p[1].fd);

      if (MHD_sys_poll_ (p,
                         2,
                         timeout) < 0)
      {
        const int err = MHD_socket_get_error_ ();

        log_event ("poll error detected: err=%d", err);

        if (MHD_SCKT_ERR_IS_EINTR_ (err))
        {
          log_event ("poll interrupted by EINTR, continuing");
          continue;
        }
#ifdef HAVE_MESSAGES
        MHD_DLOG (con->daemon,
                  _ ("Error during poll: `%s'\n"),
                  MHD_socket_strerr_ (err));
#endif
        {
          const char *estr = MHD_socket_strerr_ (err);
          if (estr)
            log_event ("MHD_socket_strerr_ returned ptr=%p content='%s'",
                       (void *) estr, estr);
          else
            log_event ("MHD_socket_strerr_ returned NULL ptr");
        }
        break;
      }
      urh_from_pollfd (urh,
                       p);
      process_urh (urh);
    }
  }
  /* end POLL */
#endif
  /* end HTTPS */
#endif /* HTTPS_SUPPORT */
  /* TLS forwarding was finished. Cleanup socketpair. */
  log_event ("finishing TLS forwarding for con=%p, calling MHD_connection_finish_forward_", (void *) con);
  MHD_connection_finish_forward_ (con);
  /* Do not set 'urh->clean_ready' yet as 'urh' will be used
   * in connection thread for a little while. */
  log_event ("exit thread_main_connection_upgrade: con=%p", (void *) con);
}
