#include <stdio.h>
#include <stdarg.h>
#include <stdlib.h>
#include <sys/stat.h>
#include <errno.h>
#include <stdbool.h>

/* Probe logging helper: append human-readable logs to /tmp/logs/632.txt.
 * This helper is deliberately small and non-intrusive. It tries to create
 * the directory /tmp/logs (ignoring errors) and then appends the message.
 */
static void
mhd_probe_logf (const char *fmt, ...)
{
  FILE *f;
  va_list ap;

  /* Ensure /tmp/logs exists; ignore errors (directory may already exist). */
  (void) mkdir ("/tmp/logs", 0755);

  f = fopen ("/tmp/logs/632.txt", "a");
  if (NULL == f)
    return;

  va_start (ap, fmt);
  (void) vfprintf (f, fmt, ap);
  va_end (ap);
  (void) fprintf (f, "\n");
  (void) fclose (f);
}

/* Modified transmit_error_response_len with probes inserted. The original
 * logic is preserved; probes only append logs to /tmp/logs/632.txt and do
 * not change program control flow or returned values.
 */
static void
transmit_error_response_len (struct MHD_Connection *connection,
                             unsigned int status_code,
                             const char *message,
                             size_t message_len,
                             char *header_name,
                             size_t header_name_len,
                             char *header_value,
                             size_t header_value_len)
{
  struct MHD_Response *response;
  enum MHD_Result iret;

  /* Log entry and key connection/request state before doing anything. */
  mhd_probe_logf ("ENTER: transmit_error_response_len: connection=%p, status=%u, message_len=%zu, header_name=%p, header_name_len=%zu, header_value=%p, header_value_len=%zu, stop_with_error=%d, state=%u",
                   (void *) connection,
                   status_code,
                   message_len,
                   (void *) header_name,
                   header_name_len,
                   (void *) header_value,
                   header_value_len,
                   (int) connection->stop_with_error,
                   (unsigned int) connection->state);

  mhd_assert (! connection->stop_with_error); /* Do not send error twice */
  if (connection->stop_with_error)
  { /* Should not happen */
    mhd_probe_logf ("BRANCH: connection->stop_with_error true; closing. connection=%p, state(before)=%u",
                     (void *) connection,
                     (unsigned int) connection->state);
    if (MHD_CONNECTION_CLOSED > connection->state)
      connection->state = MHD_CONNECTION_CLOSED;
    free (header_name);
    free (header_value);
    return;
  }
  connection->stop_with_error = true;
  connection->discard_request = true;
#ifdef HAVE_MESSAGES
  MHD_DLOG (connection->daemon,
            _ ("Error processing request (HTTP response code is %u ('%s')). " \
               "Closing connection.\n"),
            status_code,
            message);
#endif

  if (MHD_CONNECTION_START_REPLY < connection->state)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Too late to send an error response, " \
                 "response is being sent already.\n"),
              status_code,
              message);
#endif
    mhd_probe_logf ("BRANCH: Too late to send error response; connection=%p, state=%u",
                     (void *) connection,
                     (unsigned int) connection->state);
    CONNECTION_CLOSE_ERROR (connection,
                            _ ("Too late for error response."));
    free (header_name);
    free (header_value);
    return;
  }
  /* TODO: remove when special error queue function is implemented */
  connection->state = MHD_CONNECTION_FULL_REQ_RECEIVED;
  if (0 != connection->read_buffer_size)
  {
    /* Read buffer is not needed anymore, discard it
     * to free some space for error response. */
    MHD_pool_deallocate (connection->pool,
                         connection->read_buffer,
                         connection->read_buffer_size);
    connection->read_buffer = NULL;
    connection->read_buffer_size = 0;
    connection->read_buffer_offset = 0;
  }
  if (NULL != connection->rp.response)
  {
    MHD_destroy_response (connection->rp.response);
    connection->rp.response = NULL;
  }
  response = MHD_create_response_from_buffer_static (message_len,
                                                     message);
  if (NULL == response)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Failed to create error response.\n"),
              status_code,
              message);
#endif
    mhd_probe_logf ("ERROR: Failed to create response; connection=%p",
                     (void *) connection);
    /* can't even send a reply, at least close the connection */
    connection->state = MHD_CONNECTION_CLOSED;
    free (header_name);
    free (header_value);
    return;
  }

  /* Probe the values involved in the assertions to provide evidence
   * about the assertion evaluation and the state that would be examined
   * by mhd_assert. This helps reproduce the scenario where the assertion
   * has no side-effects when the condition holds.
   */
  mhd_probe_logf ("ASSERT_PROBE_BEFORE: header_name=%p, header_name_len=%zu, header_value=%p, header_value_len=%zu",
                   (void *) header_name,
                   header_name_len,
                   (void *) header_value,
                   header_value_len);

  /* Evaluate key invariant expressions for logging only (do not alter logic). */
  {
    int a1 = ((0 == header_name_len) || (NULL != header_name)) ? 1 : 0;
    int a2 = ((NULL == header_name) || (0 != header_name_len)) ? 1 : 0;
    int a3 = ((0 == header_value_len) || (NULL != header_value)) ? 1 : 0;
    int a4 = ((NULL == header_value) || (0 != header_value_len)) ? 1 : 0;
    int a5 = ((NULL == header_name) || (NULL != header_value)) ? 1 : 0;
    int a6 = ((NULL != header_value) || (NULL == header_name)) ? 1 : 0;

    mhd_probe_logf ("ASSERT_EVALS: cond1=%d cond2=%d cond3=%d cond4=%d cond5=%d cond6=%d",
                     a1, a2, a3, a4, a5, a6);
  }

  /* Original assertions preserved. */
  mhd_assert ((0 == header_name_len) || (NULL != header_name));
  mhd_assert ((NULL == header_name) || (0 != header_name_len));
  mhd_assert ((0 == header_value_len) || (NULL != header_value));
  mhd_assert ((NULL == header_value) || (0 != header_value_len));
  mhd_assert ((NULL == header_name) || (NULL != header_value));
  mhd_assert ((NULL != header_value) || (NULL == header_name));

  mhd_probe_logf ("ASSERT_PROBE_AFTER: assertions executed (if compiled); header_name=%p, header_name_len=%zu",
                   (void *) header_name,
                   header_name_len);

  if (NULL != header_name)
  {
    iret = MHD_add_response_entry_no_alloc_ (response,
                                             MHD_HEADER_KIND,
                                             header_name, header_name_len,
                                             header_value, header_value_len);
    if (MHD_NO == iret)
    {
      mhd_probe_logf ("MHD_add_response_entry_no_alloc_ returned NO; freeing header_name/header_value; connection=%p",
                       (void *) connection);
      free (header_name);
      free (header_value);
    }
  }
  else
    iret = MHD_YES;

  if (MHD_NO != iret)
  {
    bool before = connection->in_access_handler;

    /* Fake the flag for the internal call */
    connection->in_access_handler = true;
    iret = MHD_queue_response (connection,
                               status_code,
                               response);
    connection->in_access_handler = before;
  }
  MHD_destroy_response (response);
  if (MHD_NO == iret)
  {
    /* can't even send a reply, at least close the connection */
    CONNECTION_CLOSE_ERROR (connection,
                            _ ("Closing connection " \
                               "(failed to queue error response)."));
    mhd_probe_logf ("ERROR: failed to queue response; connection=%p",
                     (void *) connection);
    return;
  }
  mhd_assert (NULL != connection->rp.response);
  /* Do not reuse this connection. */
  connection->keepalive = MHD_CONN_MUST_CLOSE;
  if (MHD_NO == build_header_response (connection))
  {
    /* No memory. Release everything. */
    connection->rq.version = NULL;
    connection->rq.method = NULL;
    connection->rq.url = NULL;
    connection->rq.url_len = 0;
    connection->rq.headers_received = NULL;
    connection->rq.headers_received_tail = NULL;
    connection->write_buffer = NULL;
    connection->write_buffer_size = 0;
    connection->write_buffer_send_offset = 0;
    connection->write_buffer_append_offset = 0;
    connection->read_buffer
      = MHD_pool_reset (connection->pool,
                        NULL,
                        0,
                        0);
    connection->read_buffer_size = 0;

    /* Retry with empty buffer */
    if (MHD_NO == build_header_response (connection))
    {
      CONNECTION_CLOSE_ERROR (connection,
                              _ ("Closing connection " \
                                 "(failed to create error response header)."));
      mhd_probe_logf ("ERROR: failed to build header response twice; connection=%p",
                       (void *) connection);
      return;
    }
  }
  connection->state = MHD_CONNECTION_HEADERS_SENDING;

  mhd_probe_logf ("EXIT: transmit_error_response_len: queued response, connection=%p, state=%u",
                   (void *) connection,
                   (unsigned int) connection->state);
}
