static void
log_try_ready_normal_body_state(const char *stage,
                                 struct MHD_Connection *connection,
                                 struct MHD_Response *response,
                                 size_t copy_size,
                                 int overflow)
{
  FILE *f = fopen("/tmp/logs/669.txt", "a");
  if (!f)
    return;
  fprintf(f,
          "[try_ready_normal_body] %s: connection=%p response=%p total_size=%llu rsp_write_position=%zd send_reply_body=%d\n",
          stage,
          (void *) connection,
          (void *) response,
          (unsigned long long) response->total_size,
          (ssize_t) connection->rp.rsp_write_position,
          (int) (connection->rp.props.send_reply_body));
  if (response != NULL)
  {
    fprintf(f,
            "  data_iov=%p data_iovcnt=%zu resp_iov.iov=%p resp_iov.cnt=%zu resp_iov.sent=%zu\n",
            (void *) response->data_iov,
            (size_t) response->data_iovcnt,
            (void *) connection->rp.resp_iov.iov,
            (size_t) connection->rp.resp_iov.cnt,
            (size_t) connection->rp.resp_iov.sent);
  }
  if (copy_size)
  {
    fprintf(f, "  computed copy_size=%zu overflow=%d\n", copy_size, overflow);
  }
  fclose(f);
}

static enum MHD_Result
try_ready_normal_body (struct MHD_Connection *connection)
{
  ssize_t ret;
  struct MHD_Response *response;

  response = connection->rp.response;
  mhd_assert (connection->rp.props.send_reply_body);

  /* log entry */
  log_try_ready_normal_body_state("entry", connection, response, 0, 0);

  if ( (0 == response->total_size) ||
                     /* TODO: replace the next check with assert */
       (connection->rp.rsp_write_position == response->total_size) )
    return MHD_YES;  /* 0-byte response is always ready */
  if (NULL != response->data_iov)
  {
    size_t copy_size;

    if (NULL != connection->rp.resp_iov.iov)
      return MHD_YES;

    /* compute copy_size and log potential overflow without changing logic */
    copy_size = response->data_iovcnt * sizeof(MHD_iovec_);

    /* detect multiplication overflow: if data_iovcnt > (size_t)-1 / sizeof(MHD_iovec_) */
    {
      size_t max_sz = (size_t) -1;
      int overflow = (response->data_iovcnt > max_sz / sizeof(MHD_iovec_));
      log_try_ready_normal_body_state("before_alloc", connection, response, copy_size, overflow);
    }

    connection->rp.resp_iov.iov = MHD_connection_alloc_memory_ (connection,
                                                                copy_size);
    if (NULL == connection->rp.resp_iov.iov)
    {
      log_try_ready_normal_body_state("alloc_failed", connection, response, copy_size, 0);
      MHD_mutex_unlock_chk_ (&response->mutex);
      /* not enough memory */
      CONNECTION_CLOSE_ERROR (connection,
                              _ ("Closing connection (out of memory)."));
      return MHD_NO;
    }

    log_try_ready_normal_body_state("before_memcpy", connection, response, copy_size, 0);
    memcpy (connection->rp.resp_iov.iov,
            response->data_iov,
            copy_size);
    log_try_ready_normal_body_state("after_memcpy", connection, response, copy_size, 0);

    connection->rp.resp_iov.cnt = response->data_iovcnt;
    connection->rp.resp_iov.sent = 0;
    return MHD_YES;
  }
  if (NULL == response->crc)
    return MHD_YES;
  if ( (response->data_start <=
        connection->rp.rsp_write_position) &&
       (response->data_size + response->data_start >
        connection->rp.rsp_write_position) )
    return MHD_YES; /* response already ready */
#if defined(_MHD_HAVE_SENDFILE)
  if (MHD_resp_sender_sendfile == connection->rp.resp_sender)
  {
    /* will use sendfile, no need to bother response crc */
    return MHD_YES;
  }
#endif /* _MHD_HAVE_SENDFILE */

  ret = response->crc (response->crc_cls,
                       connection->rp.rsp_write_position,
                       (char *) response->data,
                       (size_t) MHD_MIN ((uint64_t) response->data_buffer_size,
                                         response->total_size
                                         - connection->rp.rsp_write_position));
  if (0 > ret)
  {
    /* either error or http 1.0 transfer, close socket! */
    /* TODO: do not update total size, check whether response
     * was really with unknown size */
    response->total_size = connection->rp.rsp_write_position;
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_unlock_chk_ (&response->mutex);
#endif
    if (MHD_CONTENT_READER_END_OF_STREAM == ret)
      MHD_connection_close_ (connection,
                             MHD_REQUEST_TERMINATED_COMPLETED_OK);
    else
      CONNECTION_CLOSE_ERROR (connection,
                              _ ("Closing connection (application reported " \
                                 "error generating data)."));
    log_try_ready_normal_body_state("crc_error", connection, response, 0, 0);
    return MHD_NO;
  }
  response->data_start = connection->rp.rsp_write_position;
  response->data_size = (size_t) ret;
  if (0 == ret)
  {
    connection->state = MHD_CONNECTION_NORMAL_BODY_UNREADY;
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_unlock_chk_ (&response->mutex);
#endif
    log_try_ready_normal_body_state("crc_zero_ret", connection, response, 0, 0);
    return MHD_NO;
  }
  log_try_ready_normal_body_state("exit_ready", connection, response, 0, 0);
  return MHD_YES;
}
