#include <stdio.h>
#include <stdarg.h>
#include <errno.h>
#include <string.h>

static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/29.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

int metal_bus_find(const char *name, struct metal_bus **result)
{
    struct metal_list *node;
    struct metal_bus *bus;

    probe_log("metal_bus_find ENTER: name=%p", (const void *)name);

    metal_list_for_each(&_metal.common.bus_list, node) {
        bus = metal_container_of(node, struct metal_bus, node);

        /* Log pointers and context before calling strcmp to capture state */
        probe_log("iter: node=%p, bus=%p, bus->name=%p, name=%p, result_ptr=%p",
                  (const void *)node, (const void *)bus,
                  (const void *)((bus) ? (void *)bus->name : NULL),
                  (const void *)name, (const void *)result);

        /* Preserve original logic: compare strings and return on match */
        if (strcmp(bus->name, name) == 0 && result) {
            probe_log("match found: bus=%p, setting *result=%p and returning 0", (const void *)bus, (const void *)bus);
            *result = bus;
            return 0;
        } else {
            probe_log("no match on this iteration: strcmp(bus->name=%p, name=%p)", (const void *)((bus) ? (void *)bus->name : NULL), (const void *)name);
        }
    }

    probe_log("metal_bus_find EXIT: not found, returning -ENOENT");
    return -ENOENT;
}
