static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/452.txt", "a");
    if (f != NULL) {
        va_list ap;
        va_start(ap, fmt);
        vfprintf(f, fmt, ap);
        va_end(ap);
        fclose(f);
    }
}

static int metal_init_page_sizes(void)
{
    const int max_sizes = MAX_PAGE_SIZES - 1;
    long sizes[max_sizes];

    /* Determine system page size. */
    sizes[0] = getpagesize();
    probe_log("metal_init_page_sizes: getpagesize() -> %ld\n", sizes[0]);
    if (sizes[0] <= 0) {
        metal_log(METAL_LOG_ERROR, "failed to get page size\n");
        probe_log("metal_init_page_sizes: failed to get page size (<=0)\n");
        return -EINVAL;
    }
    _metal.page_size  = sizes[0];
    _metal.page_shift = metal_log2(sizes[0]);
    metal_add_page_size(_metal.tmp_path, _metal.page_shift, 0);
    probe_log("metal_init_page_sizes: base page_size=%ld, page_shift=%d\n",
              _metal.page_size, _metal.page_shift);

#ifdef HAVE_HUGETLBFS_H
#ifndef MAP_HUGE_SHIFT
    /* System does not support multiple huge page sizes. */
    sizes[0] = gethugepagesize();
    probe_log("metal_init_page_sizes: gethugepagesize() -> %ld (single-size path)\n", sizes[0]);
    if (sizes[0] > 0) {
        metal_add_page_size(hugetlbfs_find_path(),
                    metal_log2(sizes[0]),
                    MAP_HUGETLB);
        probe_log("metal_init_page_sizes: added hugetlbfs size=%ld, shift=%d\n",
                  sizes[0], metal_log2(sizes[0]));
    } else {
        probe_log("metal_init_page_sizes: no hugetlbfs size available (<=0)\n");
    }
#else
    /* When MAP_HUGE_SHIFT is defined we may have multiple sizes. */
    {
        long gh_val = gethugepagesize();
        probe_log("metal_init_page_sizes: gethugepagesize() -> %ld (multi-size guard)\n", gh_val);
        if (gh_val >= 0) {
            int i, count;

            /* System supports multiple huge page sizes. */
            count = gethugepagesizes(sizes, max_sizes);
            probe_log("metal_init_page_sizes: gethugepagesizes() -> %d\n", count);
            for (i = 0; i < count; i++) {
                int shift = metal_log2(sizes[i]);

                probe_log("metal_init_page_sizes: sizes[%d]=%ld, shift=%d\n", i, sizes[i], shift);

                if ((shift & MAP_HUGE_MASK) != shift) {
                    probe_log("metal_init_page_sizes: sizes[%d] shift masked out (shift & MAP_HUGE_MASK) != shift\n", i);
                    continue;
                }
                metal_add_page_size(
                    hugetlbfs_find_path_for_size(sizes[i]),
                    shift, (MAP_HUGETLB |
                    (shift << MAP_HUGE_SHIFT)));
                probe_log("metal_init_page_sizes: added hugetlbfs size=%ld, shift=%d, flags=0x%x\n",
                          sizes[i], shift, (unsigned)(MAP_HUGETLB | (shift << MAP_HUGE_SHIFT)));
            }
        } else {
            probe_log("metal_init_page_sizes: gethugepagesize() < 0, skipping multi-size handling\n");
        }
    }
#endif
#endif

    /* Finally sort the resulting array by size. */
    probe_log("metal_init_page_sizes: qsort start, num_page_sizes=%d\n", _metal.num_page_sizes);
    qsort(_metal.page_sizes, _metal.num_page_sizes,
          sizeof(struct metal_page_size), metal_pagesize_compare);
    probe_log("metal_init_page_sizes: qsort done\n");

    return 0;
}
