#include <stdio.h>
#include <stdarg.h>

/* Simple logging helper that appends probes to /tmp/logs/153.txt */
static void irq_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/153.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fclose(f);
}

static int metal_irq_allocate(int irq_base, int irq_num)
{
    struct metal_list *node;
    struct metal_irq_controller *cntr;
    int irq_tocheck = irq_base, irq_end_tocheck;

    irq_log("enter metal_irq_allocate: irq_base=%d, irq_num=%d\n", irq_base, irq_num);

    if (irq_num == 0) {
        irq_log("irq_num == 0 => return METAL_IRQ_ANY\n");
        return METAL_IRQ_ANY;
    }
    if (irq_tocheck == METAL_IRQ_ANY) {
        irq_tocheck = 0;
        irq_log("irq_base was METAL_IRQ_ANY -> irq_tocheck set to 0\n");
    }
    irq_end_tocheck = irq_tocheck + irq_num;
    irq_log("initial range to check: [%d, %d) (irq_tocheck=%d, irq_end_tocheck=%d)\n",
            irq_tocheck, irq_end_tocheck, irq_tocheck, irq_end_tocheck);

    metal_list_for_each(&irq_cntrs, node) {
        int cntr_irq_base, cntr_irq_end;

        cntr = metal_container_of(node,
                      struct metal_irq_controller, node);
        irq_log("iter: found controller at %p\n", (void *)cntr);

        cntr_irq_base = cntr->irq_base;
        cntr_irq_end = cntr_irq_base + cntr->irq_num;
        irq_log("controller range: [%d, %d) (irq_base=%d, irq_num=%d)\n",
                cntr_irq_base, cntr_irq_end, cntr_irq_base, cntr->irq_num);

        if (irq_tocheck < cntr_irq_end &&
            irq_end_tocheck > cntr_irq_base) {
            irq_log("overlap detected between [%d, %d) and [%d, %d)\n",
                    irq_tocheck, irq_end_tocheck, cntr_irq_base, cntr_irq_end);
            if (irq_base != METAL_IRQ_ANY) {
                /* IRQ has been allocated */
                irq_log("requested irq_base is specific and overlap -> return METAL_IRQ_ANY\n");
                return METAL_IRQ_ANY;
            }
            irq_tocheck = cntr_irq_end;
            irq_end_tocheck = irq_tocheck + irq_num;
            irq_log("adjusted irq_tocheck to %d, irq_end_tocheck to %d\n",
                    irq_tocheck, irq_end_tocheck);
        } else {
            irq_log("no overlap with this controller\n");
        }
    }
    irq_log("exit metal_irq_allocate -> returning %d\n", irq_tocheck);
    return irq_tocheck;
}
