#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <errno.h>
#include <time.h>
#include <sys/stat.h>
#include <fcntl.h>
#include <unistd.h>
#include <stdint.h>

/* Probe helper: append a concise log line to /tmp/logs/460.txt. */
static void metal_log_probe(const char *tag, const char *detail, int errnum)
{
    /* Ensure directory exists; ignore failures. */
    (void)mkdir("/tmp/logs", 0777);

    FILE *f = fopen("/tmp/logs/460.txt", "a");
    if (!f)
        return;

    time_t t = time(NULL);
    /* Print timestamp, tag, detail and errno string if provided. */
    if (errnum)
        fprintf(f, "%ld | %s | %s | errno=%d (%s)\n",
                (long)t, tag, detail ? detail : "", errnum, strerror(errnum));
    else
        fprintf(f, "%ld | %s | %s\n",
                (long)t, tag, detail ? detail : "");

    fclose(f);
}

int metal_sys_init(const struct metal_init_params *params)
{
    const char *tmp_path;
    unsigned int seed;
    FILE *urandom;
    int result;

    metal_log_probe("enter", "metal_sys_init entered", 0);

    /* Find the temporary directory location. */
    tmp_path = getenv("TMPDIR");
    if (!tmp_path)
        tmp_path = "/tmp";
    _metal.tmp_path = tmp_path;

    {
        char buf[128];
        snprintf(buf, sizeof(buf), "tmp_path=%s", tmp_path);
        metal_log_probe("tmp_path", buf, 0);
    }

    /* Initialize the pseudo-random number generator. */
    urandom = fopen("/dev/urandom", "r");
    if (!urandom) {
        metal_log_probe("urandom_open_failed", strerror(errno), errno);
        metal_log(METAL_LOG_ERROR, "failed to open /dev/urandom (%s)\n",
                  strerror(errno));
        return -errno;
    }

    if (fread(&seed, 1, sizeof(seed), urandom) <= 0) {
        metal_log_probe("urandom_fread_failed", "Failed fread /dev/urandom", errno);
        metal_log(METAL_LOG_DEBUG, "Failed fread /dev/urandom\n");
    }
    fclose(urandom);

    {
        char buf[64];
        snprintf(buf, sizeof(buf), "seed=%u", seed);
        metal_log_probe("seed_initialized", buf, 0);
    }

    srand(seed);

    result = metal_init_page_sizes();
    metal_log_probe("metal_init_page_sizes_return", (result < 0) ? "error" : "ok", (result < 0) ? -result : 0);
    if (result < 0)
        return result;

    result = metal_linux_bus_init();
    metal_log_probe("metal_linux_bus_init_return", (result < 0) ? "error" : "ok", (result < 0) ? -result : 0);
    if (result < 0)
        return result;

    result = open("/proc/self/pagemap", O_RDONLY | O_CLOEXEC);
    if (result < 0) {
        metal_log_probe("pagemap_open_failed", strerror(errno), errno);
        metal_log(METAL_LOG_DEBUG, "Failed pagemap open - %s\n",
                  strerror(errno));
    } else {
        char buf[64];
        snprintf(buf, sizeof(buf), "pagemap_fd=%d", result);
        metal_log_probe("pagemap_open", buf, 0);
    }
    _metal.pagemap_fd = result;

    metal_unused(params);

    /* Initialize IRQ handling */
    metal_log_probe("irq_init", "calling metal_linux_irq_init", 0);
    metal_linux_irq_init();

    metal_log_probe("exit", "metal_sys_init returning 0", 0);
    return 0;
}
