int metal_tests_run(struct metal_init_params *params)
{
	struct metal_init_params dparams = METAL_INIT_DEFAULTS;
	struct metal_test_case *test_case;
	struct metal_list *node;
	int error, errors = 0;
	const char *dots = "..................................";
	const char *pad;
	FILE *fp = NULL;

	/* Open probe log file (append). This is only for diagnostic logging. */
	fp = fopen("/tmp/logs/744.txt", "a");
	if (fp)
		fprintf(fp, "metal_tests_run: entry params=%p\n", (void *)params);

	if (!params)
		params = &dparams;

	params->log_level = METAL_LOG_DEBUG;
	error = metal_init(params);
	if (fp)
		fprintf(fp, "metal_tests_run: metal_init returned %d\n", error);
	if (error) {
		if (fp) fclose(fp);
		return error;
	}

	if (fp)
		fprintf(fp, "metal_tests_run: starting iteration &test_cases=%p\n", (void *)&test_cases);

	metal_list_for_each(&test_cases, node) {
		/* Log entry to each iteration: node pointer and derived test_case. */
		test_case = metal_container_of(node, struct metal_test_case,
				       node);
		pad = dots + strlen(test_case->name);

		if (fp) {
			/* Log the pointers and names to help detect modifications in the loop body. */
			fprintf(fp, "ITER START: node=%p test_case=%p name=\"%s\" test_fn=%p node->next=%p test_cases.next=%p\n",
				(void *)node,
				(void *)test_case,
				test_case->name ? test_case->name : "(null)",
				(void *)test_case->test,
				(void *)node->next,
				(void *)test_cases.next);
		}

		metal_log(METAL_LOG_INFO,"running [%s]\n", test_case->name);

		error = test_case->test();

		if (fp) {
			/* Log after invocation to capture any modifications made by the test. */
			fprintf(fp, "ITER AFTER: node=%p test_case=%p name=\"%s\" node->next=%p test_cases.next=%p error=%d\n",
				(void *)node,
				(void *)test_case,
				test_case->name ? test_case->name : "(null)",
				(void *)node->next,
				(void *)test_cases.next,
				error);
		}

		metal_log(METAL_LOG_INFO,"result [%s]%s %s%s%s\n",
		       test_case->name, pad,
		       error ? "fail" : "pass",
		       error ? " - error: " : "",
		       error ? strerror(-error) : "");
		if (error)
			errors++;
	}

	if (fp) {
		fprintf(fp, "metal_tests_run: finished iteration, calling metal_finish()\n");
	}

	metal_finish();

	if (fp) {
		fprintf(fp, "metal_tests_run: metal_finish() done, total errors=%d\n", errors);
		fclose(fp);
	}

	return errors;
}
