#include <stdio.h>
#include <stdarg.h>
#include <stddef.h>

static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/620.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fflush(f);
    fclose(f);
}

static void *shmem_child(void *arg)
{
    const char *name = arg;
    struct {
        metal_mutex_t	mutex;
        int			counter;
    } *virt;
    struct metal_io_region *io;
    unsigned long phys;
    size_t size = 1 * 1024 * 1024;
    int error;

    probe_log("shmem_child: entry name=%s, size=%zu\n", name, size);

    error = metal_shmem_open(name, size, &io);
    probe_log("metal_shmem_open returned error=%d, io=%p\n", error, (void *)io);

    if (error) {
        probe_log("shmem_child: error path - logging and incrementing nb_err (error=%d)\n", error);
        metal_log(METAL_LOG_ERROR, "Failed shmem_open: %d.\n", error);
        atomic_fetch_add(&nb_err, 1);
        probe_log("shmem_child: returning NULL due to shmem_open error\n");
        return NULL;
    }

    virt = metal_io_virt(io, 0);
    phys = metal_io_phys(io, 0);
    probe_log("after mapping: io=%p, virt=%p, phys=0x%lx\n", (void *)io, (void *)virt, phys);

    if (phys != METAL_BAD_OFFSET) {
        probe_log("phys != METAL_BAD_OFFSET (phys=0x%lx). Performing consistency checks.\n", phys);
        if (virt != metal_io_phys_to_virt(io, phys)) {
            probe_log("consistency check failed: virt (%p) != metal_io_phys_to_virt(io, phys) (%p)\n",
                      (void *)virt, (void *)metal_io_phys_to_virt(io, phys));
            atomic_fetch_add(&nb_err, 1);
            metal_log(METAL_LOG_ERROR, "Failed virt != phys.\n");
        } else {
            probe_log("consistency check passed: virt == metal_io_phys_to_virt(io, phys)\n");
        }

        if (phys != metal_io_virt_to_phys(io, virt)) {
            probe_log("consistency check failed: phys (0x%lx) != metal_io_virt_to_phys(io, virt) (0x%lx)\n",
                      phys, (unsigned long)metal_io_virt_to_phys(io, virt));
            atomic_fetch_add(&nb_err, 1);
            metal_log(METAL_LOG_ERROR, "Failed phys != virt.\n");
        } else {
            probe_log("consistency check passed: phys == metal_io_virt_to_phys(io, virt)\n");
        }
    } else {
        probe_log("phys == METAL_BAD_OFFSET, skipping consistency checks.\n");
    }

    probe_log("Calling metal_io_finish(io=%p)\n", (void *)io);
    metal_io_finish(io);
    probe_log("metal_io_finish returned, shmem_child returning NULL\n");

    return NULL;
}
