_MHD_EXTERN enum MHD_Result
MHD_add_connection (struct MHD_Daemon *daemon,
                    MHD_socket client_socket,
                    const struct sockaddr *addr,
                    socklen_t addrlen)
{
  bool sk_nonbl;
  bool sk_spipe_supprs;
  struct sockaddr_storage addrstorage;
  FILE *mhd_log = fopen("/tmp/logs/689.txt", "a");

  if (mhd_log)
  {
    fprintf(mhd_log, "MHD_add_connection ENTER: daemon=%p client_socket=%d addr=%p addrlen=%zu\n",
            (void *) daemon, (int) client_socket, (const void *) addr, (size_t) addrlen);
  }

  /* TODO: fix atomic value reading */
  if ((! MHD_D_IS_THREAD_SAFE_ (daemon)) &&
      (daemon->connection_limit <= daemon->connections))
    MHD_cleanup_connections (daemon);

#ifdef HAVE_MESSAGES
  if (MHD_D_IS_USING_THREADS_ (daemon) &&
      (0 == (daemon->options & MHD_USE_ITC)))
  {
    MHD_DLOG (daemon,
              _ ("MHD_add_connection() has been called for daemon started"
                 " without MHD_USE_ITC flag.\nDaemon will not process newly"
                 " added connection until any activity occurs in already"
                 " added sockets.\n"));
  }
#endif /* HAVE_MESSAGES */
  if (0 != addrlen)
  {
    if (mhd_log)
    {
      int sa_family = -1;
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
      sa_family = (int) addr->sa_family;
      fprintf(mhd_log, "addr non-zero: addr->sa_family=%d\n", sa_family);
#else
      /* If sa_len not present, still try to print sa_family safely. */
      sa_family = (int) addr->sa_family;
      fprintf(mhd_log, "addr non-zero: addr->sa_family=%d\n", sa_family);
#endif
    }

    if (AF_INET == addr->sa_family)
    {
      if (sizeof(struct sockaddr_in) > (size_t) addrlen)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with "
                     "incorrect 'addrlen' value.\n"));
#endif /* HAVE_MESSAGES */
        if (mhd_log)
        {
          fprintf(mhd_log, "EARLY_RETURN: AF_INET but addrlen (%zu) < sizeof(sockaddr_in) (%zu)\n",
                  (size_t) addrlen, (size_t) sizeof(struct sockaddr_in));
          fclose(mhd_log);
        }
        return MHD_NO;
      }
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
      if ((0 != addr->sa_len) &&
          (sizeof(struct sockaddr_in) > (size_t) addr->sa_len) )
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with " \
                     "non-zero value of 'sa_len' member of " \
                     "'struct sockaddr' which does not match 'sa_family'.\n"));
#endif /* HAVE_MESSAGES */
        if (mhd_log)
        {
          fprintf(mhd_log, "EARLY_RETURN: AF_INET but addr->sa_len (%u) < sizeof(sockaddr_in) (%zu)\n",
                  (unsigned) addr->sa_len, (size_t) sizeof(struct sockaddr_in));
          fclose(mhd_log);
        }
        return MHD_NO;
      }
#endif /* HAVE_STRUCT_SOCKADDR_SA_LEN */
    }
#ifdef HAVE_INET6
    if (AF_INET6 == addr->sa_family)
    {
      if (sizeof(struct sockaddr_in6) > (size_t) addrlen)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with "
                     "incorrect 'addrlen' value.\n"));
#endif /* HAVE_MESSAGES */
        if (mhd_log)
        {
          fprintf(mhd_log, "EARLY_RETURN: AF_INET6 but addrlen (%zu) < sizeof(sockaddr_in6) (%zu)\n",
                  (size_t) addrlen, (size_t) sizeof(struct sockaddr_in6));
          fclose(mhd_log);
        }
        return MHD_NO;
      }
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
      if ((0 != addr->sa_len) &&
          (sizeof(struct sockaddr_in6) > (size_t) addr->sa_len) )
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with " \
                     "non-zero value of 'sa_len' member of " \
                     "'struct sockaddr' which does not match 'sa_family'.\n"));
#endif /* HAVE_MESSAGES */
        if (mhd_log)
        {
          fprintf(mhd_log, "EARLY_RETURN: AF_INET6 but addr->sa_len (%u) < sizeof(sockaddr_in6) (%zu)\n",
                  (unsigned) addr->sa_len, (size_t) sizeof(struct sockaddr_in6));
          fclose(mhd_log);
        }
        return MHD_NO;
      }
#endif /* HAVE_STRUCT_SOCKADDR_SA_LEN */
    }
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
    if ((0 != addr->sa_len) &&
        (addrlen > addr->sa_len))
      addrlen = (socklen_t) addr->sa_len;   /* Use safest value */
#endif /* HAVE_STRUCT_SOCKADDR_SA_LEN */
#endif /* HAVE_INET6 */
  }

  if (! MHD_socket_nonblocking_ (client_socket))
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to set nonblocking mode on new client socket: %s\n"),
              MHD_socket_last_strerr_ ());
#endif
    sk_nonbl = false;
    if (mhd_log)
      fprintf(mhd_log, "socket nonblocking failed: sk_nonbl=false\n");
  }
  else
  {
    sk_nonbl = true;
    if (mhd_log)
      fprintf(mhd_log, "socket nonblocking succeeded: sk_nonbl=true\n");
  }

#ifndef MHD_WINSOCK_SOCKETS
  sk_spipe_supprs = false;
#else  /* MHD_WINSOCK_SOCKETS */
  sk_spipe_supprs = true; /* Nothing to suppress on W32 */
#endif /* MHD_WINSOCK_SOCKETS */
#if defined(MHD_socket_nosignal_)
  if (! sk_spipe_supprs)
    sk_spipe_supprs = MHD_socket_nosignal_ (client_socket);
  if (! sk_spipe_supprs)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to suppress SIGPIPE on new client socket: %s\n"),
              MHD_socket_last_strerr_ ());
#else  /* ! HAVE_MESSAGES */
    (void) 0; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */
#ifndef MSG_NOSIGNAL
    /* Application expects that SIGPIPE will be suppressed,
     * but suppression failed and SIGPIPE cannot be suppressed with send(). */
    if (! daemon->sigpipe_blocked)
    {
      int err = MHD_socket_get_error_ ();
      MHD_socket_close_ (client_socket);
      MHD_socket_fset_error_ (err);
      if (mhd_log)
      {
        fprintf(mhd_log, "EARLY_RETURN: SIGPIPE suppression failed and sigpipe not blocked; closing socket and returning MHD_NO\n");
        fclose(mhd_log);
      }
      return MHD_NO;
    }
#endif /* MSG_NOSIGNAL */
  }
#endif /* MHD_socket_nosignal_ */

  if ( (0 != (daemon->options & MHD_USE_TURBO)) &&
       (! MHD_socket_noninheritable_ (client_socket)) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to set noninheritable mode on new client socket.\n"));
#endif
    if (mhd_log)
      fprintf(mhd_log, "warning: failed to set noninheritable mode\n");
  }

  /* Copy to sockaddr_storage structure to avoid alignment problems */
  if (0 < addrlen)
  {
    if (mhd_log)
    {
      size_t ss_size = sizeof(struct sockaddr_storage);
      fprintf(mhd_log, "About to memcpy to addrstorage: addr=%p addrlen=%zu sizeof(sockaddr_storage)=%zu\n",
              (const void *) addr, (size_t) addrlen, ss_size);
      if ((size_t) addrlen > ss_size)
        fprintf(mhd_log, "POTENTIAL_OVERFLOW: addrlen (%zu) > sizeof(sockaddr_storage) (%zu)\n",
                (size_t) addrlen, ss_size);
    }

    memcpy (&addrstorage, addr, (size_t) addrlen);

    if (mhd_log)
      fprintf(mhd_log, "memcpy completed: copied %zu bytes into addrstorage at %p\n",
              (size_t) addrlen, (void *) &addrstorage);
  }
#ifdef HAVE_STRUCT_SOCKADDR_STORAGE_SS_LEN
  addrstorage.ss_len = addrlen; /* Force set the right length */
#endif /* HAVE_STRUCT_SOCKADDR_STORAGE_SS_LEN */

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  if (NULL != daemon->worker_pool)
  {
    unsigned int i;
    /* have a pool, try to find a pool with capacity; we use the
       socket as the initial offset into the pool for load
       balancing */
    for (i = 0; i < daemon->worker_pool_size; ++i)
    {
      struct MHD_Daemon *const worker =
        &daemon->worker_pool[(i + (unsigned int) client_socket)
                             % daemon->worker_pool_size];
      if (worker->connections < worker->connection_limit)
      {
        if (mhd_log)
        {
          fprintf(mhd_log, "delegating to worker %p: worker->connections=%u worker->connection_limit=%u\n",
                  (void *) worker, (unsigned) worker->connections, (unsigned) worker->connection_limit);
          fclose(mhd_log);
        }
        return internal_add_connection (worker,
                                        client_socket,
                                        &addrstorage,
                                        addrlen,
                                        true,
                                        sk_nonbl,
                                        sk_spipe_supprs,
                                        _MHD_UNKNOWN);
      }
    }
    /* all pools are at their connection limit, must refuse */
    MHD_socket_close_chk_ (client_socket);
#if defined(ENFILE) && (ENFILE + 0 != 0)
    errno = ENFILE;
#endif
    if (mhd_log)
    {
      fprintf(mhd_log, "all worker pools full: closing client socket and returning MHD_NO\n");
      fclose(mhd_log);
    }
    return MHD_NO;
  }
#endif /* MHD_USE_POSIX_THREADS || MHD_USE_W32_THREADS */

  if (mhd_log)
  {
    fprintf(mhd_log, "calling internal_add_connection for daemon %p\n", (void *) daemon);
    fclose(mhd_log);
  }

  return internal_add_connection (daemon,
                                  client_socket,
                                  &addrstorage,
                                  addrlen,
                                  true,
                                  sk_nonbl,
                                  sk_spipe_supprs,
                                  _MHD_UNKNOWN);
}
