#include <stdio.h>

static void
mhd_probe_log (const char *site,
               struct MHD_Response *response,
               const char *header,
               const char *content)
{
  FILE *f = fopen("/tmp/logs/1949.txt", "a");
  if (NULL == f)
    return;

  void *resp_ptr = (void *) response;
  void *hdr_ptr = (void *) header;
  void *cont_ptr = (void *) content;
  unsigned long flags = 0UL;
  unsigned long flags_auto = 0UL;
  if (NULL != response)
  {
    /* Accessing response fields only when response is non-NULL. */
    flags = (unsigned long) response->flags;
    flags_auto = (unsigned long) response->flags_auto;
  }

  /* Do NOT dereference 'header' or 'content' here to avoid introducing
   * any additional out-of-bounds reads. Log only pointer values and
   * comparisons by pointer equality to well-known header tokens. */
  int eq_conn = (header == MHD_HTTP_HEADER_CONNECTION);
  int eq_te = (header == MHD_HTTP_HEADER_TRANSFER_ENCODING);
  int eq_date = (header == MHD_HTTP_HEADER_DATE);
  int eq_cl = (header == MHD_HTTP_HEADER_CONTENT_LENGTH);

  fprintf (f, "%s: response=%p, header=%p, content=%p, header_is_null=%d, eq_conn=%d, eq_te=%d, eq_date=%d, eq_cl=%d, flags=0x%lx, flags_auto=0x%lx\n",
           site,
           resp_ptr,
           hdr_ptr,
           cont_ptr,
           (header == NULL),
           eq_conn,
           eq_te,
           eq_date,
           eq_cl,
           flags,
           flags_auto);
  fclose (f);
}

_MHD_EXTERN enum MHD_Result
MHD_add_response_header (struct MHD_Response *response,
                         const char *header,
                         const char *content)
{
  /* Probe before comparisons to capture the control path and state. */
  mhd_probe_log ("before_CONNECTION", response, header, content);
  if (MHD_str_equal_caseless_ (header, MHD_HTTP_HEADER_CONNECTION))
    return add_response_header_connection (response, content);

  mhd_probe_log ("before_TRANSFER_ENCODING", response, header, content);
  if (MHD_str_equal_caseless_ (header,
                               MHD_HTTP_HEADER_TRANSFER_ENCODING))
  {
    if (! MHD_str_equal_caseless_ (content, "chunked"))
      return MHD_NO;   /* Only "chunked" encoding is allowed */
    if (0 != (response->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED))
      return MHD_YES;  /* Already has "chunked" encoding header */
    if ( (0 != (response->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH)) &&
         (0 == (MHD_RF_INSANITY_HEADER_CONTENT_LENGTH & response->flags)) )
      return MHD_NO; /* Has "Content-Length" header and no "Insanity" flag */
    if (MHD_NO != add_response_entry (response,
                                      MHD_HEADER_KIND,
                                      header,
                                      content))
    {
      response->flags_auto |= MHD_RAF_HAS_TRANS_ENC_CHUNKED;
      return MHD_YES;
    }
    return MHD_NO;
  }

  mhd_probe_log ("before_DATE", response, header, content);
  if (MHD_str_equal_caseless_ (header,
                               MHD_HTTP_HEADER_DATE))
  {
    if (0 != (response->flags_auto & MHD_RAF_HAS_DATE_HDR))
    {
      struct MHD_HTTP_Res_Header *hdr;
      hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                         MHD_HTTP_HEADER_DATE,
                                         MHD_STATICSTR_LEN_ ( \
                                           MHD_HTTP_HEADER_DATE));
      mhd_assert (NULL != hdr);
      _MHD_remove_header (response, hdr);
      if (NULL != hdr->value)
        free (hdr->value);
      free (hdr->header);
      free (hdr);
    }
    if (MHD_NO != add_response_entry (response,
                                      MHD_HEADER_KIND,
                                      header,
                                      content))
    {
      response->flags_auto |= MHD_RAF_HAS_DATE_HDR;
      return MHD_YES;
    }
    return MHD_NO;
  }

  mhd_probe_log ("before_CONTENT_LENGTH", response, header, content);
  if (MHD_str_equal_caseless_ (header,
                               MHD_HTTP_HEADER_CONTENT_LENGTH))
  {
    /* Generally MHD sets automatically correct "Content-Length" always when
     * needed.
     * Custom "Content-Length" header is allowed only in special cases. */
    if ( (0 != (MHD_RF_INSANITY_HEADER_CONTENT_LENGTH & response->flags)) ||
         ((0 != (MHD_RF_HEAD_ONLY_RESPONSE & response->flags)) &&
          (0 == (response->flags_auto & (MHD_RAF_HAS_TRANS_ENC_CHUNKED
                                         | MHD_RAF_HAS_CONTENT_LENGTH)))) )
    {
      if (MHD_NO != add_response_entry (response,
                                        MHD_HEADER_KIND,
                                        header,
                                        content))
      {
        response->flags_auto |= MHD_RAF_HAS_CONTENT_LENGTH;
        return MHD_YES;
      }
    }
    return MHD_NO;
  }

  mhd_probe_log ("before_default_add_response_entry", response, header, content);
  return add_response_entry (response,
                             MHD_HEADER_KIND,
                             header,
                             content);
}
