#include <stdio.h>
#include <stdint.h>

static void
probe_log_connection_state (struct MHD_Connection *connection, const char *location)
{
  FILE *f = fopen("/tmp/logs/82.txt", "a");
  if (NULL == f)
    return;

  /* Attempt to log a set of relevant fields. Use casts to avoid format
     mismatches for unknown types. Keep the probe non-fatal if fields are
     not available at compile time. */
  fprintf (f,
           "%s: conn=%p state=%d tls_state=%d suspended=%d event_loop_info=%d read_buffer_offset=%zu read_buffer_size=%zu rq_some_payload_processed=%d have_chunked_upload=%d remaining_upload_size=%llu\n",
           location,
           (void *) connection,
           (int) connection->state,
           (int) connection->tls_state,
           (int) connection->suspended,
           (int) connection->event_loop_info,
           (size_t) connection->read_buffer_offset,
           (size_t) connection->read_buffer_size,
           (int) connection->rq.some_payload_processed,
           (int) connection->rq.have_chunked_upload,
           (unsigned long long) connection->rq.remaining_upload_size);
  fflush (f);
  fclose (f);
}

static void
MHD_connection_update_event_loop_info (struct MHD_Connection *connection)
{
  /* Probe: entry */
  probe_log_connection_state (connection, "enter MHD_connection_update_event_loop_info");

  /* Do not update states of suspended connection */
  if (connection->suspended)
  {
    probe_log_connection_state (connection, "suspended - returning without update");
    return; /* States will be updated after resume. */
  }
#ifdef HTTPS_SUPPORT
  if (MHD_TLS_CONN_NO_TLS != connection->tls_state)
  {   /* HTTPS connection. */
    switch (connection->tls_state)
    {
    case MHD_TLS_CONN_INIT:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      probe_log_connection_state (connection, "TLS INIT -> READ");
      return;
    case MHD_TLS_CONN_HANDSHAKING:
    case MHD_TLS_CONN_WR_CLOSING:
      if (0 == gnutls_record_get_direction (connection->tls_session))
        connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      else
        connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      probe_log_connection_state (connection, "TLS HANDSHAKING/WR_CLOSING -> set READ/WRITE");
      return;
    case MHD_TLS_CONN_CONNECTED:
      break; /* Do normal processing */
    case MHD_TLS_CONN_WR_CLOSED:
    case MHD_TLS_CONN_TLS_FAILED:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_CLEANUP;
      probe_log_connection_state (connection, "TLS WR_CLOSED/TLS_FAILED -> CLEANUP");
      return;
    case MHD_TLS_CONN_TLS_CLOSING:  /* Not implemented yet */
    case MHD_TLS_CONN_TLS_CLOSED:   /* Not implemented yet */
    case MHD_TLS_CONN_INVALID_STATE:
    case MHD_TLS_CONN_NO_TLS: /* Not possible */
    default:
      probe_log_connection_state (connection, "Invalid TLS state - panic about to be raised");
      MHD_PANIC (_ ("Invalid TLS state value.\n"));
    }
  }
#endif /* HTTPS_SUPPORT */
  while (1)
  {
#if DEBUG_STATES
    MHD_DLOG (connection->daemon,
              _ ("In function %s handling connection at state: %s\n"),
              MHD_FUNC_,
              MHD_state_to_string (connection->state));
#endif
    switch (connection->state)
    {
    case MHD_CONNECTION_INIT:
    case MHD_CONNECTION_REQ_LINE_RECEIVING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      probe_log_connection_state (connection, "case: REQ_LINE_RECEIVING -> READ");
      break;
    case MHD_CONNECTION_REQ_LINE_RECEIVED:
      probe_log_connection_state (connection, "case: REQ_LINE_RECEIVED (assert about to run)");
      mhd_assert (0);
      break;
    case MHD_CONNECTION_REQ_HEADERS_RECEIVING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      probe_log_connection_state (connection, "case: REQ_HEADERS_RECEIVING -> READ");
      break;
    case MHD_CONNECTION_HEADERS_RECEIVED:
    case MHD_CONNECTION_HEADERS_PROCESSED:
      probe_log_connection_state (connection, "case: HEADERS_RECEIVED/PROCESSED (assert about to run)");
      mhd_assert (0);
      break;
    case MHD_CONNECTION_CONTINUE_SENDING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      probe_log_connection_state (connection, "case: CONTINUE_SENDING -> WRITE");
      break;
    case MHD_CONNECTION_BODY_RECEIVING:
      if ((connection->rq.some_payload_processed) &&
          has_unprocessed_upload_body_data_in_buffer (connection))
      {
        /* Some data was processed, the buffer must have some free space */
        mhd_assert (connection->read_buffer_offset < \
                    connection->read_buffer_size);
        if (! connection->rq.have_chunked_upload)
        {
          /* Not a chunked upload. Do not read more than necessary to
             process the current request. */
          if (connection->rq.remaining_upload_size >=
              connection->read_buffer_offset)
            connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
          else
            connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS_READ;
        }
        else
        {
          /* Chunked upload. The size of the current request is unknown.
             Continue reading as the space in the read buffer is available. */
          connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS_READ;
        }
        probe_log_connection_state (connection, "case: BODY_RECEIVING (processed payload) -> PROCESS/PROCESS_READ");
      }
      else
      {
        connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
        probe_log_connection_state (connection, "case: BODY_RECEIVING -> READ");
      }
      break;
    case MHD_CONNECTION_BODY_RECEIVED:
      probe_log_connection_state (connection, "case: BODY_RECEIVED (assert about to run)");
      mhd_assert (0);
      break;
    case MHD_CONNECTION_FOOTERS_RECEIVING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      probe_log_connection_state (connection, "case: FOOTERS_RECEIVING -> READ");
      break;
    case MHD_CONNECTION_FOOTERS_RECEIVED:
      probe_log_connection_state (connection, "case: FOOTERS_RECEIVED (assert about to run)");
      mhd_assert (0);
      break;
    case MHD_CONNECTION_FULL_REQ_RECEIVED:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
      probe_log_connection_state (connection, "case: FULL_REQ_RECEIVED -> PROCESS");
      break;
    case MHD_CONNECTION_START_REPLY:
      probe_log_connection_state (connection, "case: START_REPLY (assert about to run)");
      mhd_assert (0);
      break;
    case MHD_CONNECTION_HEADERS_SENDING:
      /* headers in buffer, keep writing */
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      probe_log_connection_state (connection, "case: HEADERS_SENDING -> WRITE");
      break;
    case MHD_CONNECTION_HEADERS_SENT:
      probe_log_connection_state (connection, "case: HEADERS_SENT (assert about to run)");
      mhd_assert (0);
      break;
    case MHD_CONNECTION_NORMAL_BODY_UNREADY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
      probe_log_connection_state (connection, "case: NORMAL_BODY_UNREADY -> PROCESS");
      break;
    case MHD_CONNECTION_NORMAL_BODY_READY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      probe_log_connection_state (connection, "case: NORMAL_BODY_READY -> WRITE");
      break;
    case MHD_CONNECTION_CHUNKED_BODY_UNREADY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
      probe_log_connection_state (connection, "case: CHUNKED_BODY_UNREADY -> PROCESS");
      break;
    case MHD_CONNECTION_CHUNKED_BODY_READY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      probe_log_connection_state (connection, "case: CHUNKED_BODY_READY -> WRITE");
      break;
    case MHD_CONNECTION_CHUNKED_BODY_SENT:
      probe_log_connection_state (connection, "case: CHUNKED_BODY_SENT (assert about to run)");
      mhd_assert (0);
      break;
    case MHD_CONNECTION_FOOTERS_SENDING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      probe_log_connection_state (connection, "case: FOOTERS_SENDING -> WRITE");
      break;
    case MHD_CONNECTION_FULL_REPLY_SENT:
      probe_log_connection_state (connection, "case: FULL_REPLY_SENT (assert about to run)");
      mhd_assert (0);
      break;
    case MHD_CONNECTION_CLOSED:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_CLEANUP;
      probe_log_connection_state (connection, "case: CLOSED -> CLEANUP (returning)");
      return;           /* do nothing, not even reading */
#ifdef UPGRADE_SUPPORT
    case MHD_CONNECTION_UPGRADE:
      probe_log_connection_state (connection, "case: UPGRADE (assert about to run)");
      mhd_assert (0);
      break;
#endif /* UPGRADE_SUPPORT */
    default:
      probe_log_connection_state (connection, "case: DEFAULT (assert about to run)");
      mhd_assert (0);
    }

    if (0 != (MHD_EVENT_LOOP_INFO_READ & connection->event_loop_info))
    {
      /* Check whether the space is available to receive data */
      if (! check_and_grow_read_buffer_space (connection))
      {
        mhd_assert (connection->discard_request);
        probe_log_connection_state (connection, "check_and_grow_read_buffer_space failed - continue");
        continue;
      }
    }
    probe_log_connection_state (connection, "about to break out of while - everything processed");
    break; /* Everything was processed. */
  }

  probe_log_connection_state (connection, "exit MHD_connection_update_event_loop_info");
}
