#include <stdio.h>
#include <time.h>

/* Probe logging helper. Keeps side effects minimal: appends human-readable
   information about the connection and control location to the log file. */
static void
mhd_probe_log_conn (const struct MHD_Connection *connection,
                     const char *where,
                     const char *note)
{
  FILE *f = fopen ("/tmp/logs/82.txt", "a");
  if (NULL == f)
    return;
  time_t t = time (NULL);
  /* Print relevant connection fields that help reproduce the violation
     scenario. Keep calls simple so we don't depend on other library
     functions that might not be available at this point. */
  fprintf (f, "[%ld] probe: %s\n", (long)t, where);
  if (connection)
    {
      fprintf (f, "  conn=%p state=%d tls_state=%d event_loop_info=0x%x\n",
               (void *) connection,
               (int) connection->state,
               (int) connection->tls_state,
               (unsigned) connection->event_loop_info);
      fprintf (f, "  read_buffer_offset=%zu read_buffer_size=%zu discard_request=%d\n",
               (size_t) connection->read_buffer_offset,
               (size_t) connection->read_buffer_size,
               (int) connection->discard_request);
      /* rq fields (may be a sub-struct) */
      fprintf (f, "  rq.some_payload_processed=%d rq.have_chunked_upload=%d rq.remaining_upload_size=%zu\n",
               (int) connection->rq.some_payload_processed,
               (int) connection->rq.have_chunked_upload,
               (size_t) connection->rq.remaining_upload_size);
    }
  if (note)
    fprintf (f, "  note: %s\n", note);
  fprintf (f, "\n");
  fclose (f);
}

static void
MHD_connection_update_event_loop_info (struct MHD_Connection *connection)
{
  mhd_probe_log_conn (connection, "enter MHD_connection_update_event_loop_info", NULL);

  /* Do not update states of suspended connection */
  if (connection->suspended)
    {
      mhd_probe_log_conn (connection, "suspended - returning early", NULL);
      return; /* States will be updated after resume. */
    }
#ifdef HTTPS_SUPPORT
  if (MHD_TLS_CONN_NO_TLS != connection->tls_state)
  {   /* HTTPS connection. */
    mhd_probe_log_conn (connection, "HTTPS branch entered", NULL);
    switch (connection->tls_state)
    {
    case MHD_TLS_CONN_INIT:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      mhd_probe_log_conn (connection, "tls init -> set READ and return", NULL);
      return;
    case MHD_TLS_CONN_HANDSHAKING:
    case MHD_TLS_CONN_WR_CLOSING:
      if (0 == gnutls_record_get_direction (connection->tls_session))
        connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      else
        connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      mhd_probe_log_conn (connection, "tls handshaking/wr_closing -> set READ/WRITE and return", NULL);
      return;
    case MHD_TLS_CONN_CONNECTED:
      mhd_probe_log_conn (connection, "tls connected -> normal processing", NULL);
      break; /* Do normal processing */
    case MHD_TLS_CONN_WR_CLOSED:
    case MHD_TLS_CONN_TLS_FAILED:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_CLEANUP;
      mhd_probe_log_conn (connection, "tls failed/wr_closed -> set CLEANUP and return", NULL);
      return;
    case MHD_TLS_CONN_TLS_CLOSING:  /* Not implemented yet */
    case MHD_TLS_CONN_TLS_CLOSED:   /* Not implemented yet */
    case MHD_TLS_CONN_INVALID_STATE:
    case MHD_TLS_CONN_NO_TLS: /* Not possible */
    default:
      mhd_probe_log_conn (connection, "tls invalid state -> panic", NULL);
      MHD_PANIC (_ ("Invalid TLS state value.\n"));
    }
  }
#endif /* HTTPS_SUPPORT */
  while (1)
  {
#if DEBUG_STATES
    MHD_DLOG (connection->daemon,
              _ ("In function %s handling connection at state: %s\n"),
              MHD_FUNC_,
              MHD_state_to_string (connection->state));
#endif
    switch (connection->state)
    {
    case MHD_CONNECTION_INIT:
    case MHD_CONNECTION_REQ_LINE_RECEIVING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      break;
    case MHD_CONNECTION_REQ_LINE_RECEIVED:
      mhd_probe_log_conn (connection, "about to hit mhd_assert(0) for REQ_LINE_RECEIVED", NULL);
      mhd_assert (0);
      break;
    case MHD_CONNECTION_REQ_HEADERS_RECEIVING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      break;
    case MHD_CONNECTION_HEADERS_RECEIVED:
    case MHD_CONNECTION_HEADERS_PROCESSED:
      mhd_probe_log_conn (connection, "about to hit mhd_assert(0) for HEADERS_RECEIVED/PROCESSED", NULL);
      mhd_assert (0);
      break;
    case MHD_CONNECTION_CONTINUE_SENDING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      break;
    case MHD_CONNECTION_BODY_RECEIVING:
      if ((connection->rq.some_payload_processed) &&
          has_unprocessed_upload_body_data_in_buffer (connection))
      {
        /* Some data was processed, the buffer must have some free space */
        mhd_probe_log_conn (connection, "BODY_RECEIVING: some_payload_processed && has_unprocessed_upload_body_data_in_buffer", NULL);
        mhd_assert (connection->read_buffer_offset < \
                    connection->read_buffer_size);
        if (! connection->rq.have_chunked_upload)
        {
          /* Not a chunked upload. Do not read more than necessary to
             process the current request. */
          if (connection->rq.remaining_upload_size >=
              connection->read_buffer_offset)
            connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
          else
            connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS_READ;
        }
        else
        {
          /* Chunked upload. The size of the current request is unknown.
             Continue reading as the space in the read buffer is available. */
          connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS_READ;
        }
      }
      else
        connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      break;
    case MHD_CONNECTION_BODY_RECEIVED:
      mhd_probe_log_conn (connection, "about to hit mhd_assert(0) for BODY_RECEIVED", NULL);
      mhd_assert (0);
      break;
    case MHD_CONNECTION_FOOTERS_RECEIVING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      break;
    case MHD_CONNECTION_FOOTERS_RECEIVED:
      mhd_probe_log_conn (connection, "about to hit mhd_assert(0) for FOOTERS_RECEIVED", NULL);
      mhd_assert (0);
      break;
    case MHD_CONNECTION_FULL_REQ_RECEIVED:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
      break;
    case MHD_CONNECTION_START_REPLY:
      mhd_probe_log_conn (connection, "about to hit mhd_assert(0) for START_REPLY", NULL);
      mhd_assert (0);
      break;
    case MHD_CONNECTION_HEADERS_SENDING:
      /* headers in buffer, keep writing */
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      break;
    case MHD_CONNECTION_HEADERS_SENT:
      mhd_probe_log_conn (connection, "about to hit mhd_assert(0) for HEADERS_SENT", NULL);
      mhd_assert (0);
      break;
    case MHD_CONNECTION_NORMAL_BODY_UNREADY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
      break;
    case MHD_CONNECTION_NORMAL_BODY_READY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      break;
    case MHD_CONNECTION_CHUNKED_BODY_UNREADY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
      break;
    case MHD_CONNECTION_CHUNKED_BODY_READY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      break;
    case MHD_CONNECTION_CHUNKED_BODY_SENT:
      mhd_probe_log_conn (connection, "about to hit mhd_assert(0) for CHUNKED_BODY_SENT", NULL);
      mhd_assert (0);
      break;
    case MHD_CONNECTION_FOOTERS_SENDING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      break;
    case MHD_CONNECTION_FULL_REPLY_SENT:
      mhd_probe_log_conn (connection, "about to hit mhd_assert(0) for FULL_REPLY_SENT", NULL);
      mhd_assert (0);
      break;
    case MHD_CONNECTION_CLOSED:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_CLEANUP;
      mhd_probe_log_conn (connection, "CONNECTION_CLOSED -> set CLEANUP and return", NULL);
      return;           /* do nothing, not even reading */
#ifdef UPGRADE_SUPPORT
    case MHD_CONNECTION_UPGRADE:
      mhd_probe_log_conn (connection, "about to hit mhd_assert(0) for UPGRADE", NULL);
      mhd_assert (0);
      break;
#endif /* UPGRADE_SUPPORT */
    default:
      mhd_probe_log_conn (connection, "about to hit mhd_assert(0) for DEFAULT", NULL);
      mhd_assert (0);
    }

    if (0 != (MHD_EVENT_LOOP_INFO_READ & connection->event_loop_info))
    {
      /* Check whether the space is available to receive data */
      if (! check_and_grow_read_buffer_space (connection))
      {
        /* Log the failing condition and the value of discard_request so
           that when mhd_assert is compiled out it is still possible to
           observe what would have been asserted. This provides evidence
           for the diagnostic-only nature of that assertion. */
        mhd_probe_log_conn (connection, "check_and_grow_read_buffer_space returned FALSE", "about to mhd_assert(connection->discard_request)");
        mhd_assert (connection->discard_request);
        continue;
      }
    }
    mhd_probe_log_conn (connection, "leaving loop - everything processed", NULL);
    break; /* Everything was processed. */
  }
  mhd_probe_log_conn (connection, "exit MHD_connection_update_event_loop_info", NULL);
}
