#include <stdio.h>
#include <time.h>

/* Probe helper to log control flow and connection state to /tmp/logs/82.txt */
static void
mhd_probe_log (const char *event, struct MHD_Connection *connection)
{
  FILE *f = fopen("/tmp/logs/82.txt", "a");
  if (!f)
    return;
  /* Log a compact snapshot of relevant fields used in the function. Casts to
     unsigned long are used to avoid format warnings for size types. */
  fprintf (f,
           "%lu: %s: state=%d, event_loop_info=0x%x, suspended=%d, tls_state=%d, read_buffer_offset=%lu, read_buffer_size=%lu, rq.some_payload_processed=%d, rq.have_chunked_upload=%d, rq.remaining_upload_size=%lu, discard_request=%d\n",
           (unsigned long) time (NULL),
           event,
           (int) connection->state,
           (unsigned int) connection->event_loop_info,
           (int) connection->suspended,
           (int) connection->tls_state,
           (unsigned long) connection->read_buffer_offset,
           (unsigned long) connection->read_buffer_size,
           (int) connection->rq.some_payload_processed,
           (int) connection->rq.have_chunked_upload,
           (unsigned long) connection->rq.remaining_upload_size,
           (int) connection->discard_request);
  fclose (f);
}

static void
MHD_connection_update_event_loop_info (struct MHD_Connection *connection)
{
  /* Log entry into the function and the initial state. */
  mhd_probe_log ("enter MHD_connection_update_event_loop_info", connection);

  /* Do not update states of suspended connection */
  if (connection->suspended)
  {
    mhd_probe_log ("suspended - early return", connection);
    return; /* States will be updated after resume. */
  }
#ifdef HTTPS_SUPPORT
  if (MHD_TLS_CONN_NO_TLS != connection->tls_state)
  {   /* HTTPS connection. */
    switch (connection->tls_state)
    {
    case MHD_TLS_CONN_INIT:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      mhd_probe_log ("tls: INIT -> READ", connection);
      return;
    case MHD_TLS_CONN_HANDSHAKING:
    case MHD_TLS_CONN_WR_CLOSING:
      if (0 == gnutls_record_get_direction (connection->tls_session))
        connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      else
        connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      mhd_probe_log ("tls: HANDSHAKING/WR_CLOSING -> set READ/WRITE", connection);
      return;
    case MHD_TLS_CONN_CONNECTED:
      mhd_probe_log ("tls: CONNECTED -> continue normal processing", connection);
      break; /* Do normal processing */
    case MHD_TLS_CONN_WR_CLOSED:
    case MHD_TLS_CONN_TLS_FAILED:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_CLEANUP;
      mhd_probe_log ("tls: WR_CLOSED/TLS_FAILED -> CLEANUP", connection);
      return;
    case MHD_TLS_CONN_TLS_CLOSING:  /* Not implemented yet */
    case MHD_TLS_CONN_TLS_CLOSED:   /* Not implemented yet */
    case MHD_TLS_CONN_INVALID_STATE:
    case MHD_TLS_CONN_NO_TLS: /* Not possible */
    default:
      mhd_probe_log ("tls: invalid/default -> panic", connection);
      MHD_PANIC (_ ("Invalid TLS state value.\n"));
    }
  }
#endif /* HTTPS_SUPPORT */
  while (1)
  {
#if DEBUG_STATES
    MHD_DLOG (connection->daemon,
              _ ("In function %s handling connection at state: %s\n"),
              MHD_FUNC_,
              MHD_state_to_string (connection->state));
#endif
    switch (connection->state)
    {
    case MHD_CONNECTION_INIT:
    case MHD_CONNECTION_REQ_LINE_RECEIVING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      mhd_probe_log ("case: INIT/REQ_LINE_RECEIVING -> READ", connection);
      break;
    case MHD_CONNECTION_REQ_LINE_RECEIVED:
      mhd_probe_log ("case: REQ_LINE_RECEIVED -> assertion point", connection);
      mhd_assert (0);
      break;
    case MHD_CONNECTION_REQ_HEADERS_RECEIVING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      mhd_probe_log ("case: REQ_HEADERS_RECEIVING -> READ", connection);
      break;
    case MHD_CONNECTION_HEADERS_RECEIVED:
    case MHD_CONNECTION_HEADERS_PROCESSED:
      mhd_probe_log ("case: HEADERS_RECEIVED/PROCESSED -> assertion point", connection);
      mhd_assert (0);
      break;
    case MHD_CONNECTION_CONTINUE_SENDING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      mhd_probe_log ("case: CONTINUE_SENDING -> WRITE", connection);
      break;
    case MHD_CONNECTION_BODY_RECEIVING:
      mhd_probe_log ("case: BODY_RECEIVING -> evaluating buffer state", connection);
      if ((connection->rq.some_payload_processed) &&
          has_unprocessed_upload_body_data_in_buffer (connection))
      {
        /* Some data was processed, the buffer must have some free space */
        mhd_probe_log ("BODY_RECEIVING: some_payload_processed && has_unprocessed -> check buffer bounds", connection);
        mhd_assert (connection->read_buffer_offset < \
                    connection->read_buffer_size);
        if (! connection->rq.have_chunked_upload)
        {
          /* Not a chunked upload. Do not read more than necessary to
             process the current request. */
          if (connection->rq.remaining_upload_size >=
              connection->read_buffer_offset)
            connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
          else
            connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS_READ;
          mhd_probe_log ("BODY_RECEIVING: non-chunked -> set PROCESS/PROCESS_READ", connection);
        }
        else
        {
          /* Chunked upload. The size of the current request is unknown.
             Continue reading as the space in the read buffer is available. */
          connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS_READ;
          mhd_probe_log ("BODY_RECEIVING: chunked -> PROCESS_READ", connection);
        }
      }
      else
      {
        connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
        mhd_probe_log ("BODY_RECEIVING: default -> READ", connection);
      }
      break;
    case MHD_CONNECTION_BODY_RECEIVED:
      mhd_probe_log ("case: BODY_RECEIVED -> assertion point", connection);
      mhd_assert (0);
      break;
    case MHD_CONNECTION_FOOTERS_RECEIVING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      mhd_probe_log ("case: FOOTERS_RECEIVING -> READ", connection);
      break;
    case MHD_CONNECTION_FOOTERS_RECEIVED:
      mhd_probe_log ("case: FOOTERS_RECEIVED -> assertion point", connection);
      mhd_assert (0);
      break;
    case MHD_CONNECTION_FULL_REQ_RECEIVED:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
      mhd_probe_log ("case: FULL_REQ_RECEIVED -> PROCESS", connection);
      break;
    case MHD_CONNECTION_START_REPLY:
      mhd_probe_log ("case: START_REPLY -> assertion point", connection);
      mhd_assert (0);
      break;
    case MHD_CONNECTION_HEADERS_SENDING:
      /* headers in buffer, keep writing */
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      mhd_probe_log ("case: HEADERS_SENDING -> WRITE", connection);
      break;
    case MHD_CONNECTION_HEADERS_SENT:
      mhd_probe_log ("case: HEADERS_SENT -> assertion point", connection);
      mhd_assert (0);
      break;
    case MHD_CONNECTION_NORMAL_BODY_UNREADY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
      mhd_probe_log ("case: NORMAL_BODY_UNREADY -> PROCESS", connection);
      break;
    case MHD_CONNECTION_NORMAL_BODY_READY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      mhd_probe_log ("case: NORMAL_BODY_READY -> WRITE", connection);
      break;
    case MHD_CONNECTION_CHUNKED_BODY_UNREADY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
      mhd_probe_log ("case: CHUNKED_BODY_UNREADY -> PROCESS", connection);
      break;
    case MHD_CONNECTION_CHUNKED_BODY_READY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      mhd_probe_log ("case: CHUNKED_BODY_READY -> WRITE", connection);
      break;
    case MHD_CONNECTION_CHUNKED_BODY_SENT:
      mhd_probe_log ("case: CHUNKED_BODY_SENT -> assertion point", connection);
      mhd_assert (0);
      break;
    case MHD_CONNECTION_FOOTERS_SENDING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      mhd_probe_log ("case: FOOTERS_SENDING -> WRITE", connection);
      break;
    case MHD_CONNECTION_FULL_REPLY_SENT:
      mhd_probe_log ("case: FULL_REPLY_SENT -> assertion point", connection);
      mhd_assert (0);
      break;
    case MHD_CONNECTION_CLOSED:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_CLEANUP;
      mhd_probe_log ("case: CLOSED -> CLEANUP and return", connection);
      return;           /* do nothing, not even reading */
#ifdef UPGRADE_SUPPORT
    case MHD_CONNECTION_UPGRADE:
      mhd_probe_log ("case: UPGRADE -> assertion point", connection);
      mhd_assert (0);
      break;
#endif /* UPGRADE_SUPPORT */
    default:
      mhd_probe_log ("case: default -> assertion point", connection);
      mhd_assert (0);
    }

    if (0 != (MHD_EVENT_LOOP_INFO_READ & connection->event_loop_info))
    {
      /* Check whether the space is available to receive data */
      mhd_probe_log ("checking read buffer space before potential read", connection);
      if (! check_and_grow_read_buffer_space (connection))
      {
        mhd_probe_log ("check_and_grow_read_buffer_space failed - discard_request expected", connection);
        mhd_assert (connection->discard_request);
        continue;
      }
    }
    mhd_probe_log ("leaving loop - everything processed", connection);
    break; /* Everything was processed. */
  }
  mhd_probe_log ("exit MHD_connection_update_event_loop_info", connection);
}
