#include <stdio.h>
#include <stdarg.h>
#include <stddef.h>

/* Probe logging helper: append human-readable logs to /tmp/logs/2089.txt */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/2089.txt", "a");
    if (NULL == f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

_MHD_EXTERN enum MHD_Result
MHD_del_response_header (struct MHD_Response *response,
                         const char *header,
                         const char *content)
{
  struct MHD_HTTP_Res_Header *pos;
  size_t header_len;
  size_t content_len;

  /* Log entry and input pointer values */
  probe_log("MHD_del_response_header: entry; response=%p, header=%p, content=%p",
            (void *)response, (void *)header, (void *)content);

  if ( (NULL == header) ||
       (NULL == content) )
  {
    probe_log("MHD_del_response_header: NULL check failed; header=%p, content=%p -> returning MHD_NO",
              (void *)header, (void *)content);
    return MHD_NO;
  }

  /* Compute header length (this is where a non-NUL-terminated header would already be problematic) */
  header_len = strlen (header);
  probe_log("MHD_del_response_header: computed header_len=%zu for header@%p", header_len, (void *)header);

  if ((0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR)) &&
      (MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONNECTION) == header_len) &&
      MHD_str_equal_caseless_bin_n_ (header, MHD_HTTP_HEADER_CONNECTION,
                                     header_len))
  {
    probe_log("MHD_del_response_header: special-case CONNECTION header matched; delegating to del_response_header_connection");
    return del_response_header_connection (response, content);
  }

  /* Log that we are about to compute content_len using strlen().
     Note: if content is not NUL-terminated within its bounds, this call
     may scan past the object's end — this is the scenario under test. */
  probe_log("MHD_del_response_header: about to call strlen(content) for content@%p", (void *)content);
  content_len = strlen (content);
  probe_log("MHD_del_response_header: computed content_len=%zu for content@%p", content_len, (void *)content);

  pos = response->first_header;
  while (NULL != pos)
  {
    probe_log("MHD_del_response_header: examining header node pos=%p header_size=%zu value_size=%zu",
              (void *)pos, pos->header_size, pos->value_size);

    if ((header_len == pos->header_size) &&
        (content_len == pos->value_size) &&
        (0 == memcmp (header,
                      pos->header,
                      header_len)) &&
        (0 == memcmp (content,
                      pos->value,
                      content_len)))
    {
      probe_log("MHD_del_response_header: match found at pos=%p; removing header", (void *)pos);
      _MHD_remove_header (response, pos);
      free (pos->header);
      free (pos->value);
      free (pos);

      if ( (MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_TRANSFER_ENCODING) ==
            header_len) &&
           MHD_str_equal_caseless_bin_n_ (header,
                                          MHD_HTTP_HEADER_TRANSFER_ENCODING,
                                          header_len) )
      {
        probe_log("MHD_del_response_header: matched TRANSFER-ENCODING header; clearing RAF_HAS_TRANS_ENC_CHUNKED flag");
        response->flags_auto &=
          ~((enum MHD_ResponseAutoFlags) MHD_RAF_HAS_TRANS_ENC_CHUNKED);
      }
      else if ( (MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_DATE) ==
                 header_len) &&
                MHD_str_equal_caseless_bin_n_ (header,
                                               MHD_HTTP_HEADER_DATE,
                                               header_len) )
      {
        probe_log("MHD_del_response_header: matched DATE header; clearing RAF_HAS_DATE_HDR flag");
        response->flags_auto &=
          ~((enum MHD_ResponseAutoFlags) MHD_RAF_HAS_DATE_HDR);
      }
      else if ( (MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONTENT_LENGTH) ==
                 header_len) &&
                MHD_str_equal_caseless_bin_n_ (header,
                                               MHD_HTTP_HEADER_CONTENT_LENGTH,
                                               header_len) )
      {
        probe_log("MHD_del_response_header: matched CONTENT-LENGTH header; checking for remaining content-length entries");
        if (NULL == MHD_get_response_element_n_ (response,
                                                 MHD_HEADER_KIND,
                                                 MHD_HTTP_HEADER_CONTENT_LENGTH,
                                                 header_len))
        {
          probe_log("MHD_del_response_header: no other CONTENT-LENGTH header found; clearing RAF_HAS_CONTENT_LENGTH flag");
          response->flags_auto &=
            ~((enum MHD_ResponseAutoFlags) MHD_RAF_HAS_CONTENT_LENGTH);
        }
        else
        {
          probe_log("MHD_del_response_header: additional CONTENT-LENGTH header(s) present; leaving flag set");
        }
      }
      probe_log("MHD_del_response_header: header removed successfully; returning MHD_YES");
      return MHD_YES;
    }
    pos = pos->next;
  }

  probe_log("MHD_del_response_header: no matching header found; returning MHD_NO");
  return MHD_NO;
}
