#include <stdio.h>

void
MHD_init (void)
{
#if defined(MHD_WINSOCK_SOCKETS)
  WSADATA wsd;
#endif /* MHD_WINSOCK_SOCKETS */

  /* Probe log: note entry to MHD_init */
  {
    FILE *mhd_log_file = fopen("/tmp/logs/815.txt", "a");
    if (mhd_log_file)
      {
        fprintf (mhd_log_file, "MHD_init: entered\n");
        fclose (mhd_log_file);
      }
  }

  MHD_set_panic_func (NULL, NULL);

#if defined(MHD_WINSOCK_SOCKETS)
  if (0 != WSAStartup (MAKEWORD (2, 2), &wsd))
    MHD_PANIC (_ ("Failed to initialize winsock.\n"));
  if ((2 != LOBYTE (wsd.wVersion)) && (2 != HIBYTE (wsd.wVersion)))
    MHD_PANIC (_ ("Winsock version 2.2 is not available.\n"));
#endif /* MHD_WINSOCK_SOCKETS */
#ifdef HTTPS_SUPPORT
#ifdef MHD_HTTPS_REQUIRE_GCRYPT
#if GCRYPT_VERSION_NUMBER < 0x010600
#if GNUTLS_VERSION_NUMBER <= 0x020b00
#if defined(MHD_USE_POSIX_THREADS)
  if (0 != gcry_control (GCRYCTL_SET_THREAD_CBS,
                         &gcry_threads_pthread))
    MHD_PANIC (_ ("Failed to initialise multithreading in libgcrypt.\n"));
#elif defined(MHD_W32_MUTEX_)
  if (0 != gcry_control (GCRYCTL_SET_THREAD_CBS,
                         &gcry_threads_w32))
    MHD_PANIC (_ ("Failed to initialise multithreading in libgcrypt.\n"));
#endif /* defined(MHD_W32_MUTEX_) */
#endif /* GNUTLS_VERSION_NUMBER <= 0x020b00 */
  gcry_check_version (NULL);
#else
  if (NULL == gcry_check_version ("1.6.0"))
    MHD_PANIC (_ ("libgcrypt is too old. MHD was compiled for " \
                  "libgcrypt 1.6.0 or newer.\n"));
#endif
#endif /* MHD_HTTPS_REQUIRE_GCRYPT */
  gnutls_global_init ();
#endif /* HTTPS_SUPPORT */
  MHD_monotonic_sec_counter_init ();
  MHD_send_init_static_vars_ ();
  MHD_init_mem_pools_ ();
  /* Check whether sizes were correctly detected by configure */
#ifdef _DEBUG
  if (1)
  {
    struct timeval tv;
    /* Probe log: record that the invariant controlling expression "if (1)" was evaluated and taken. */
    {
      FILE *mhd_log_file = fopen ("/tmp/logs/815.txt", "a");
      if (mhd_log_file)
        {
          fprintf (mhd_log_file, "DEBUG: evaluating controlling expression '1' in if (1) - this is invariant and always true\n");
          fprintf (mhd_log_file, "DEBUG: sizeof(tv.tv_sec) = %zu, SIZEOF_STRUCT_TIMEVAL_TV_SEC = %zu\n", (size_t)sizeof (tv.tv_sec), (size_t) SIZEOF_STRUCT_TIMEVAL_TV_SEC);
          fclose (mhd_log_file);
        }
    }

    mhd_assert (sizeof(tv.tv_sec) == SIZEOF_STRUCT_TIMEVAL_TV_SEC);

    /* Probe log: record result of the assertion check (best-effort; assertion may abort in debug builds) */
    {
      FILE *mhd_log_file = fopen ("/tmp/logs/815.txt", "a");
      if (mhd_log_file)
        {
          fprintf (mhd_log_file, "DEBUG: completed size assertion check for tv.tv_sec\n");
          fclose (mhd_log_file);
        }
    }
  }
#endif /* _DEBUG */
  mhd_assert (sizeof(uint64_t) == SIZEOF_UINT64_T);

  /* Probe log: leaving MHD_init */
  {
    FILE *mhd_log_file = fopen ("/tmp/logs/815.txt", "a");
    if (mhd_log_file)
      {
        fprintf (mhd_log_file, "MHD_init: exit\n");
        fclose (mhd_log_file);
      }
  }
}
