#include <stdio.h>
#include <stdarg.h>
#include <inttypes.h>
#include <stdint.h>
#include <time.h>
#include <sys/time.h>

/* Simple probe logger that appends logs to /tmp/logs/1405.txt. */
static void
mhd_log (const char *fmt, ...)
{
  FILE *f = fopen ("/tmp/logs/1405.txt", "a");
  if (f)
    {
      va_list ap;
      va_start (ap, fmt);
      vfprintf (f, fmt, ap);
      va_end (ap);
      fclose (f);
    }
}

uint64_t
MHD_monotonic_msec_counter (void)
{
#if defined(HAVE_CLOCK_GETTIME) || defined(HAVE_TIMESPEC_GET)
  struct timespec ts;
#endif /* HAVE_CLOCK_GETTIME || HAVE_TIMESPEC_GET */

#ifdef HAVE_CLOCK_GETTIME
  if ( (_MHD_UNWANTED_CLOCK != mono_clock_id) &&
       (0 == clock_gettime (mono_clock_id,
                            &ts)) )
    {
      uint64_t retval = (uint64_t) (((uint64_t) (ts.tv_sec - mono_clock_start)) * 1000
                                    + (uint64_t) (ts.tv_nsec / 1000000));
      mhd_log ("[MHD] branch: HAVE_CLOCK_GETTIME taken\n");
      mhd_log ("[MHD] ts.tv_sec=%lld ts.tv_nsec=%lld mono_clock_start=%" PRIu64 " retval=%" PRIu64 "\n",
               (long long) ts.tv_sec, (long long) ts.tv_nsec, (uint64_t) mono_clock_start, retval);
      return retval;
    }
#endif /* HAVE_CLOCK_GETTIME */
#ifdef HAVE_CLOCK_GET_TIME
  if (_MHD_INVALID_CLOCK_SERV != mono_clock_service)
  {
    mach_timespec_t cur_time;

    if (KERN_SUCCESS == clock_get_time (mono_clock_service,
                                        &cur_time))
      {
        uint64_t retval = (uint64_t) (((uint64_t) (cur_time.tv_sec - mono_clock_start))
                                      * 1000 + (uint64_t) (cur_time.tv_nsec / 1000000));
        mhd_log ("[MHD] branch: HAVE_CLOCK_GET_TIME taken\n");
        mhd_log ("[MHD] cur_time.tv_sec=%lld cur_time.tv_nsec=%lld mono_clock_start=%" PRIu64 " retval=%" PRIu64 "\n",
                 (long long) cur_time.tv_sec, (long long) cur_time.tv_nsec, (uint64_t) mono_clock_start, retval);
        return retval;
      }
  }
#endif /* HAVE_CLOCK_GET_TIME */
#if defined(_WIN32)
#if _WIN32_WINNT >= 0x0600
  if (1)
    {
      uint64_t retval = (uint64_t) (GetTickCount64 () - tick_start);
      /* This is the invariant controlling expression case reported by the other agent (if (1)). */
      mhd_log ("[MHD] branch: _WIN32 && _WIN32_WINNT>=0x0600 taken (if (1))\n");
      mhd_log ("[MHD] GetTickCount64()-tick_start retval=%" PRIu64 " tick_start=%" PRIu64 "\n",
               retval, (uint64_t) tick_start);
      return retval;
    }
#else  /* _WIN32_WINNT < 0x0600 */
  if (0 != perf_freq)
  {
    LARGE_INTEGER perf_counter;
    uint64_t num_ticks;

    QueryPerformanceCounter (&perf_counter);   /* never fail on XP and later */
    num_ticks = (uint64_t) (perf_counter.QuadPart - perf_start);
    {
      uint64_t retval = ((num_ticks / perf_freq) * 1000)
                        + ((num_ticks % perf_freq) / (perf_freq / 1000));
      mhd_log ("[MHD] branch: _WIN32 perf counter taken\n");
      mhd_log ("[MHD] perf_counter.QuadPart=%lld perf_start=%lld perf_freq=%u num_ticks=%" PRIu64 " retval=%" PRIu64 "\n",
               (long long) perf_counter.QuadPart, (long long) perf_start, (unsigned) perf_freq, num_ticks, retval);
      return retval;
    }
  }
#endif /* _WIN32_WINNT < 0x0600 */
#endif /* _WIN32 */
#ifdef HAVE_GETHRTIME
  if (1)
    {
      uint64_t retval = ((uint64_t) (gethrtime () - hrtime_start)) / 1000000;
      mhd_log ("[MHD] branch: HAVE_GETHRTIME taken (if (1))\n");
      mhd_log ("[MHD] gethrtime()-hrtime_start retval=%" PRIu64 " hrtime_start=%" PRIu64 "\n",
               retval, (uint64_t) hrtime_start);
      return retval;
    }
#endif /* HAVE_GETHRTIME */

  /* Fallbacks, affected by system time change */
#ifdef HAVE_TIMESPEC_GET
  if (TIME_UTC == timespec_get (&ts, TIME_UTC))
    {
      uint64_t retval = (uint64_t) (((uint64_t) (ts.tv_sec - gettime_start)) * 1000
                                    + (uint64_t) (ts.tv_nsec / 1000000));
      mhd_log ("[MHD] branch: HAVE_TIMESPEC_GET taken\n");
      mhd_log ("[MHD] ts.tv_sec=%lld ts.tv_nsec=%lld gettime_start=%" PRIu64 " retval=%" PRIu64 "\n",
               (long long) ts.tv_sec, (long long) ts.tv_nsec, (uint64_t) gettime_start, retval);
      return retval;
    }
#elif defined(HAVE_GETTIMEOFDAY)
  if (1)
  {
    struct timeval tv;
    if (0 == gettimeofday (&tv, NULL))
      {
        uint64_t retval = (uint64_t) (((uint64_t) (tv.tv_sec - gettime_start)) * 1000
                                      + (uint64_t) (tv.tv_usec / 1000));
        mhd_log ("[MHD] branch: HAVE_GETTIMEOFDAY taken (if (1))\n");
        mhd_log ("[MHD] tv.tv_sec=%lld tv.tv_usec=%ld gettime_start=%" PRIu64 " retval=%" PRIu64 "\n",
                 (long long) tv.tv_sec, (long) tv.tv_usec, (uint64_t) gettime_start, retval);
        return retval;
      }
  }
#endif /* HAVE_GETTIMEOFDAY */

  /* The last resort fallback with very low resolution */
  {
    uint64_t retval = (uint64_t) (time (NULL) - sys_clock_start) * 1000;
    mhd_log ("[MHD] branch: fallback time() taken\n");
    mhd_log ("[MHD] time()-sys_clock_start retval=%" PRIu64 " sys_clock_start=%" PRIu64 "\n",
             retval, (uint64_t) sys_clock_start);
    return retval;
  }
}
