#include <stdio.h>
#include <stddef.h>

/* Probe helper: log pool state to a file for reproducing the violation scenario. */
static void
mhd_log_pool_state(const struct MemoryPool *pool, const char *stage, size_t free_before, size_t result)
{
    FILE *f = fopen("/tmp/logs/1353.txt", "a");
    if (f == NULL)
    {
        /* Best-effort logging only; do not alter program flow if logging fails. */
        return;
    }

    fprintf(f, "MHD_pool_get_free: stage=%s pool=%p pos=%zu end=%zu size=%zu free_before=%zu result=%zu\n",
            stage,
            (void *) pool,
            (size_t) pool->pos,
            (size_t) pool->end,
            (size_t) pool->size,
            free_before,
            result);

    fclose(f);
}

size_t
MHD_pool_get_free (struct MemoryPool *pool)
{
  mhd_assert (pool->end >= pool->pos);
  mhd_assert (pool->size >= pool->end - pool->pos);
  mhd_assert (pool->pos == ROUND_TO_ALIGN (pool->pos));

  /* Capture state before any branching. */
  size_t free_before = pool->end - pool->pos;

#ifdef MHD_ASAN_POISON_ACTIVE
  if (free_before <= _MHD_RED_ZONE_SIZE)
  {
    /* Log the control path and state that leads to the early return 0. */
    mhd_log_pool_state(pool, "asan_return_zero", free_before, 0);
    return 0;
  }
#endif /* MHD_ASAN_POISON_ACTIVE */

  size_t result = free_before - _MHD_RED_ZONE_SIZE;

  /* Log the normal return path and the computed values. */
  mhd_log_pool_state(pool, "normal_return", free_before, result);

  return result;
}
