#include <stdio.h>

/* Probe logging helpers - write sparse, non-intrusive logs to /tmp/logs/157.txt */
static void mhd_probe_log_entry(const void *connection, unsigned int status_code)
{
    FILE *f = fopen("/tmp/logs/157.txt", "a");
    if (f)
    {
        fprintf(f, "MHD_queue_response: ENTRY conn=%p status_code=%u\n", connection, status_code);
        fclose(f);
    }
}

static void mhd_probe_log_event(const char *event, const void *connection, unsigned int status_code)
{
    FILE *f = fopen("/tmp/logs/157.txt", "a");
    if (f)
    {
        fprintf(f, "MHD_queue_response: %s conn=%p status_code=%u\n", event, connection, status_code);
        fclose(f);
    }
}

static void mhd_probe_log_after_mask(const void *connection, unsigned int before, unsigned int after, int reply_icy)
{
    FILE *f = fopen("/tmp/logs/157.txt", "a");
    if (f)
    {
        fprintf(f, "MHD_queue_response: MASK_APPLIED conn=%p before=%u after=%u reply_icy=%d\n",
                connection, before, after, reply_icy);
        fclose(f);
    }
}

_MHD_EXTERN enum MHD_Result
MHD_queue_response (struct MHD_Connection *connection,
                    unsigned int status_code,
                    struct MHD_Response *response)
{
  struct MHD_Daemon *daemon;
  bool reply_icy;

  /* Log entry */
  mhd_probe_log_entry(connection, status_code);

  if ((NULL == connection) || (NULL == response))
  {
    mhd_probe_log_event("NULL_PARAM", connection, status_code);
    return MHD_NO;
  }

  daemon = connection->daemon;
  if ((! connection->in_access_handler) && (! connection->suspended) &&
      MHD_D_IS_USING_THREADS_ (daemon))
  {
    mhd_probe_log_event("REJECT_NOT_IN_HANDLER_OR_SUSPENDED_THREADS", connection, status_code);
    return MHD_NO;
  }

  /* Log status before modifying the parameter (the reported violation point) */
  mhd_probe_log_event("BEFORE_ICY_MASK", connection, status_code);

  reply_icy = (0 != (status_code & MHD_ICY_FLAG));
  status_code &= ~MHD_ICY_FLAG; /* <-- original code: parameter modified in-place */

  /* Log change after mask operation to show the parameter was modified in-place */
  mhd_probe_log_after_mask(connection, (unsigned int)(reply_icy ? (status_code | MHD_ICY_FLAG) : status_code), status_code, reply_icy);

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  if ( (! connection->suspended) &&
       MHD_D_IS_USING_THREADS_ (daemon) &&
       (! MHD_thread_handle_ID_is_current_thread_ (connection->tid)) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Attempted to queue response on wrong thread!\n"));
#endif
    mhd_probe_log_event("REJECT_WRONG_THREAD", connection, status_code);
    return MHD_NO;
  }
#endif

  if (NULL != connection->rp.response)
  {
    mhd_probe_log_event("REJECT_RESPONSE_ALREADY_SET", connection, status_code);
    return MHD_NO; /* The response was already set */
  }

  if ( (MHD_CONNECTION_HEADERS_PROCESSED != connection->state) &&
       (MHD_CONNECTION_FULL_REQ_RECEIVED != connection->state) )
  {
    mhd_probe_log_event("REJECT_WRONG_CONNECTION_STATE", connection, status_code);
    return MHD_NO; /* Wrong connection state */
  }

  if (daemon->shutdown)
  {
    mhd_probe_log_event("REJECT_DAEMON_SHUTDOWN", connection, status_code);
    return MHD_NO;
  }

#ifdef UPGRADE_SUPPORT
  if (NULL != response->upgrade_handler)
  {
    struct MHD_HTTP_Res_Header *conn_header;
    if (0 == (daemon->options & MHD_ALLOW_UPGRADE))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Attempted 'upgrade' connection on daemon without" \
                   " MHD_ALLOW_UPGRADE option!\n"));
#endif
      mhd_probe_log_event("REJECT_UPGRADE_NOT_ALLOWED", connection, status_code);
      return MHD_NO;
    }
    /* Log status_code value used in upgrade check */
    mhd_probe_log_event("UPGRADE_HANDLER_PRESENT_CHECK_STATUS", connection, status_code);
    if (MHD_HTTP_SWITCHING_PROTOCOLS != status_code)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used invalid status code for" \
                   " 'upgrade' response!\n"));
#endif
      mhd_probe_log_event("REJECT_INVALID_STATUS_FOR_UPGRADE", connection, status_code);
      return MHD_NO;
    }
    if (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used invalid response" \
                   " without \"Connection\" header!\n"));
#endif
      mhd_probe_log_event("REJECT_UPGRADE_NO_CONNECTION_HEADER", connection, status_code);
      return MHD_NO;
    }
    conn_header = response->first_header;
    mhd_assert (NULL != conn_header);
    mhd_assert (MHD_str_equal_caseless_ (conn_header->header,
                                         MHD_HTTP_HEADER_CONNECTION));
    if (! MHD_str_has_s_token_caseless_ (conn_header->value,
                                         "upgrade"))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used invalid response" \
                   " without \"upgrade\" token in" \
                   " \"Connection\" header!\n"));
#endif
      mhd_probe_log_event("REJECT_UPGRADE_NO_UPGRADE_TOKEN", connection, status_code);
      return MHD_NO;
    }
    if (! MHD_IS_HTTP_VER_1_1_COMPAT (connection->rq.http_ver))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Connection \"Upgrade\" can be used only " \
                   "with HTTP/1.1 connections!\n"));
#endif
      mhd_probe_log_event("REJECT_UPGRADE_HTTP_VER", connection, status_code);
      return MHD_NO;
    }
  }
#endif /* UPGRADE_SUPPORT */
  if (MHD_HTTP_SWITCHING_PROTOCOLS == status_code)
  {
#ifdef UPGRADE_SUPPORT
    if (NULL == response->upgrade_handler)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used status code 101 \"Switching Protocols\" " \
                   "with non-'upgrade' response!\n"));
#endif /* HAVE_MESSAGES */
      mhd_probe_log_event("REJECT_STATUS_101_WITHOUT_UPGRADE_HANDLER", connection, status_code);
      return MHD_NO;
    }
#else  /* ! UPGRADE_SUPPORT */
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Application used status code 101 \"Switching Protocols\", " \
                 "but this MHD was built without \"Upgrade\" support!\n"));
#endif /* HAVE_MESSAGES */
    mhd_probe_log_event("REJECT_STATUS_101_NO_UPGRADE_SUPPORT", connection, status_code);
    return MHD_NO;
#endif /* ! UPGRADE_SUPPORT */
  }
  if ( (100 > status_code) ||
       (999 < status_code) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Refused wrong status code (%u). " \
                 "HTTP requires three digits status code!\n"),
              status_code);
#endif
    mhd_probe_log_event("REJECT_STATUS_OUT_OF_RANGE", connection, status_code);
    return MHD_NO;
  }
  if (200 > status_code)
  {
    if (MHD_HTTP_VER_1_0 == connection->rq.http_ver)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Wrong status code (%u) refused. " \
                   "HTTP/1.0 clients do not support 1xx status codes!\n"),
                (status_code));
#endif
      mhd_probe_log_event("REJECT_1XX_ON_HTTP_1_0", connection, status_code);
      return MHD_NO;
    }
    if (0 != (response->flags & (MHD_RF_HTTP_1_0_COMPATIBLE_STRICT
                                 | MHD_RF_HTTP_1_0_SERVER)))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Wrong status code (%u) refused. " \
                   "HTTP/1.0 reply mode does not support 1xx status codes!\n"),
                (status_code));
#endif
      mhd_probe_log_event("REJECT_1XX_IN_HTTP_1_0_REPLY_MODE", connection, status_code);
      return MHD_NO;
    }
  }
  if ( (MHD_HTTP_MTHD_CONNECT == connection->rq.http_mthd) &&
       (2 == status_code / 100) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Successful (%u) response code cannot be used to answer " \
                 "\"CONNECT\" request!\n"),
              (status_code));
#endif
    mhd_probe_log_event("REJECT_CONNECT_WITH_SUCCESS_STATUS", connection, status_code);
    return MHD_NO;
  }

  if ( (0 != (MHD_RF_HEAD_ONLY_RESPONSE & response->flags)) &&
       (RP_BODY_HEADERS_ONLY < is_reply_body_needed (connection, status_code)) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("HEAD-only response cannot be used when the request requires "
                 "reply body to be sent!\n"));
#endif
    mhd_probe_log_event("REJECT_HEAD_ONLY_WHEN_BODY_NEEDED", connection, status_code);
    return MHD_NO;
  }

#ifdef HAVE_MESSAGES
  if ( (0 != (MHD_RF_INSANITY_HEADER_CONTENT_LENGTH & response->flags)) &&
       (0 != (MHD_RAF_HAS_CONTENT_LENGTH & response->flags_auto)) )
  {
    MHD_DLOG (daemon,
              _ ("The response has application-defined \"Content-Length\" " \
                 "header. The reply to the request will be not " \
                 "HTTP-compliant and may result in hung connection or " \
                 "other problems!\n"));
  }
#endif

  MHD_increment_response_rc (response);
  connection->rp.response = response;
  connection->rp.responseCode = status_code;
  connection->rp.responseIcy = reply_icy;

  /* Log the values stored into connection after queuing the response */
  mhd_probe_log_event("RESPONSE_QUEUED", connection, status_code);

#if defined(_MHD_HAVE_SENDFILE)
  if ( (response->fd == -1) ||
       (response->is_pipe) ||
       (0 != (connection->daemon->options & MHD_USE_TLS))
#if defined(MHD_SEND_SPIPE_SUPPRESS_NEEDED) && \
       defined(MHD_SEND_SPIPE_SUPPRESS_POSSIBLE)
       || (! daemon->sigpipe_blocked && ! connection->sk_spipe_suppress)
#endif /* MHD_SEND_SPIPE_SUPPRESS_NEEDED &&
          MHD_SEND_SPIPE_SUPPRESS_POSSIBLE */
       )
    connection->rp.resp_sender = MHD_resp_sender_std;
  else
    connection->rp.resp_sender = MHD_resp_sender_sendfile;
#endif /* _MHD_HAVE_SENDFILE */
  /* FIXME: if 'is_pipe' is set, TLS is off, and we have *splice*, we could use splice()
     to avoid two user-space copies... */

  if ( (MHD_HTTP_MTHD_HEAD == connection->rq.http_mthd) ||
       (MHD_HTTP_OK > status_code) ||
       (MHD_HTTP_NO_CONTENT == status_code) ||
       (MHD_HTTP_NOT_MODIFIED == status_code) )
  {
    /* if this is a "HEAD" request, or a status code for
       which a body is not allowed, pretend that we
       have already sent the full message body. */
    /* TODO: remove the next assignment, use 'rp_props.send_reply_body' in
     * checks */
    connection->rp.rsp_write_position = response->total_size;
    mhd_probe_log_event("ADJUST_RSP_WRITE_POSITION", connection, status_code);
  }
  if (MHD_CONNECTION_HEADERS_PROCESSED == connection->state)
  {
    /* response was queued "early", refuse to read body / footers or
       further requests! */
    connection->discard_request = true;
    connection->state = MHD_CONNECTION_START_REPLY;
    connection->rq.remaining_upload_size = 0;
    mhd_probe_log_event("EARLY_RESPONSE_QUEUED", connection, status_code);
  }
  if (! connection->in_idle)
    (void) MHD_connection_handle_idle (connection);
  MHD_update_last_activity_ (connection);

  mhd_probe_log_event("EXIT_SUCCESS", connection, status_code);
  return MHD_YES;
}
