#include <stdio.h>
#include <stdarg.h>
#include <stdbool.h>

/* Probe logging helper - append-only to a fixed file for reproducibility. */
static void mhd_probe_log (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/153.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  (void) vfprintf (f, fmt, ap);
  (void) fprintf (f, "\n");
  va_end (ap);
  (void) fclose (f);
}

_MHD_EXTERN enum MHD_Result
MHD_queue_response (struct MHD_Connection *connection,
                    unsigned int status_code,
                    struct MHD_Response *response)
{
  struct MHD_Daemon *daemon;
  bool reply_icy;

  /* Log entry and basic pointers (safe to print addresses even if NULL). */
  mhd_probe_log ("enter MHD_queue_response: connection=%p response=%p status_code=%u",
                 (void *) connection, (void *) response, status_code);

  if ((NULL == connection) || (NULL == response))
  {
    mhd_probe_log ("early return: NULL connection or response");
    return MHD_NO;
  }

  daemon = connection->daemon;
  mhd_probe_log ("daemon=%p in_access_handler=%d suspended=%d state=%d",
                 (void *) daemon,
                 (int) connection->in_access_handler,
                 (int) connection->suspended,
                 (int) connection->state);

  if ((! connection->in_access_handler) && (! connection->suspended) &&
      MHD_D_IS_USING_THREADS_ (daemon))
    return MHD_NO;

  reply_icy = (0 != (status_code & MHD_ICY_FLAG));
  status_code &= ~MHD_ICY_FLAG;

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  if ( (! connection->suspended) &&
       MHD_D_IS_USING_THREADS_ (daemon) &&
       (! MHD_thread_handle_ID_is_current_thread_ (connection->tid)) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Attempted to queue response on wrong thread!\n"));
#endif
    mhd_probe_log ("Attempted to queue response on wrong thread (tid mismatch)");
    return MHD_NO;
  }
#endif

  if (NULL != connection->rp.response)
    return MHD_NO; /* The response was already set */

  if ( (MHD_CONNECTION_HEADERS_PROCESSED != connection->state) &&
       (MHD_CONNECTION_FULL_REQ_RECEIVED != connection->state) )
    return MHD_NO; /* Wrong connection state */

  if (daemon->shutdown)
    return MHD_NO;

#ifdef UPGRADE_SUPPORT
  if (NULL != response->upgrade_handler)
  {
    struct MHD_HTTP_Res_Header *conn_header;
    mhd_probe_log ("upgrade_handler present: daemon->options=0x%08x response->flags_auto=0x%08x status_code=%u",
                   (unsigned int) daemon->options,
                   (unsigned int) response->flags_auto,
                   status_code);
    if (0 == (daemon->options & MHD_ALLOW_UPGRADE))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Attempted 'upgrade' connection on daemon without" \
                   " MHD_ALLOW_UPGRADE option!\n"));
#endif
      mhd_probe_log ("upgrade not allowed by daemon options -> return MHD_NO");
      return MHD_NO;
    }
    if (MHD_HTTP_SWITCHING_PROTOCOLS != status_code)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used invalid status code for" \
                   " 'upgrade' response!\n"));
#endif
      mhd_probe_log ("invalid status code for upgrade: %u", status_code);
      return MHD_NO;
    }
    if (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used invalid response" \
                   " without \"Connection\" header!\n"));
#endif
      mhd_probe_log ("response missing Connection header flag -> return MHD_NO");
      return MHD_NO;
    }
    conn_header = response->first_header;
    mhd_probe_log ("conn_header assigned: %p", (void *) conn_header);
    mhd_assert (NULL != conn_header);
    /* Log header and value contents if available (do not alter them). */
    if (NULL != conn_header)
    {
      const char *h = conn_header->header ? conn_header->header : "(NULL)";
      const char *v = conn_header->value ? conn_header->value : "(NULL)";
      mhd_probe_log ("conn_header->header=%s conn_header->value=%s",
                     h, v);
    }
    mhd_assert (MHD_str_equal_caseless_ (conn_header->header,
                                         MHD_HTTP_HEADER_CONNECTION));
    /* Re-evaluate the caseless equality to log the boolean result. */
    if (NULL != conn_header)
    {
      int eq = MHD_str_equal_caseless_ (conn_header->header,
                                        MHD_HTTP_HEADER_CONNECTION) ? 1 : 0;
      mhd_probe_log ("MHD_str_equal_caseless_(header, \"Connection\") = %d", eq);
    }
    if (! MHD_str_has_s_token_caseless_ (conn_header->value,
                                         "upgrade"))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used invalid response" \
                   " without \"upgrade\" token in" \
                   " \"Connection\" header!\n"));
#endif
      mhd_probe_log ("Connection header does not contain 'upgrade' token -> return MHD_NO");
      return MHD_NO;
    }
    {
      int has_token = MHD_str_has_s_token_caseless_ (conn_header->value,
                                                     "upgrade") ? 1 : 0;
      mhd_probe_log ("MHD_str_has_s_token_caseless_(value, 'upgrade') = %d", has_token);
    }
    if (! MHD_IS_HTTP_VER_1_1_COMPAT (connection->rq.http_ver))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Connection \"Upgrade\" can be used only " \
                   "with HTTP/1.1 connections!\n"));
#endif
      mhd_probe_log ("HTTP version not 1.1 compatible -> return MHD_NO (http_ver=%d)",
                     (int) connection->rq.http_ver);
      return MHD_NO;
    }
    mhd_probe_log ("upgrade checks passed; conn_header was used in read-only manner (header/value checked)");
  }
#endif /* UPGRADE_SUPPORT */
  if (MHD_HTTP_SWITCHING_PROTOCOLS == status_code)
  {
#ifdef UPGRADE_SUPPORT
    if (NULL == response->upgrade_handler)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used status code 101 \"Switching Protocols\" " \
                   "with non-'upgrade' response!\n"));
#endif /* HAVE_MESSAGES */
      mhd_probe_log ("101 status but no upgrade_handler -> return MHD_NO");
      return MHD_NO;
    }
#else  /* ! UPGRADE_SUPPORT */
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Application used status code 101 \"Switching Protocols\", " \
                 "but this MHD was built without \"Upgrade\" support!\n"));
#endif /* HAVE_MESSAGES */
    mhd_probe_log ("101 status but UPGRADE_SUPPORT not enabled -> return MHD_NO");
    return MHD_NO;
#endif /* ! UPGRADE_SUPPORT */
  }
  if ( (100 > status_code) ||
       (999 < status_code) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Refused wrong status code (%u). " \
                 "HTTP requires three digits status code!\n"),
              status_code);
#endif
    mhd_probe_log ("Refused wrong status code (%u)", status_code);
    return MHD_NO;
  }
  if (200 > status_code)
  {
    if (MHD_HTTP_VER_1_0 == connection->rq.http_ver)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Wrong status code (%u) refused. " \
                   "HTTP/1.0 clients do not support 1xx status codes!\n"),
                (status_code));
#endif
      mhd_probe_log ("1xx status code refused for HTTP/1.0 client -> return MHD_NO");
      return MHD_NO;
    }
    if (0 != (response->flags & (MHD_RF_HTTP_1_0_COMPATIBLE_STRICT
                                 | MHD_RF_HTTP_1_0_SERVER)))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Wrong status code (%u) refused. " \
                   "HTTP/1.0 reply mode does not support 1xx status codes!\n"),
                (status_code));
#endif
      mhd_probe_log ("1xx status code refused due to response flags -> return MHD_NO");
      return MHD_NO;
    }
  }
  if ( (MHD_HTTP_MTHD_CONNECT == connection->rq.http_mthd) &&
       (2 == status_code / 100) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Successful (%u) response code cannot be used to answer " \
                 "\"CONNECT\" request!\n"),
              (status_code));
#endif
    mhd_probe_log ("CONNECT method cannot be answered with 2xx -> return MHD_NO");
    return MHD_NO;
  }

  if ( (0 != (MHD_RF_HEAD_ONLY_RESPONSE & response->flags)) &&
       (RP_BODY_HEADERS_ONLY < is_reply_body_needed (connection, status_code)) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("HEAD-only response cannot be used when the request requires "
                 "reply body to be sent!\n"));
#endif
    mhd_probe_log ("HEAD-only response incompatible with required reply body -> return MHD_NO");
    return MHD_NO;
  }

#ifdef HAVE_MESSAGES
  if ( (0 != (MHD_RF_INSANITY_HEADER_CONTENT_LENGTH & response->flags)) &&
       (0 != (MHD_RAF_HAS_CONTENT_LENGTH & response->flags_auto)) )
  {
    MHD_DLOG (daemon,
              _ ("The response has application-defined \"Content-Length\" " \
                 "header. The reply to the request will be not " \
                 "HTTP-compliant and may result in hung connection or " \
                 "other problems!\n"));
    mhd_probe_log ("warning: application-defined Content-Length present");
  }
#endif

  MHD_increment_response_rc (response);
  mhd_probe_log ("response refcount incremented: response=%p total_size=%llu fd=%d",
                 (void *) response,
                 (unsigned long long) response->total_size,
                 (int) response->fd);
  connection->rp.response = response;
  connection->rp.responseCode = status_code;
  connection->rp.responseIcy = reply_icy;
#if defined(_MHD_HAVE_SENDFILE)
  if ( (response->fd == -1) ||
       (response->is_pipe) ||
       (0 != (connection->daemon->options & MHD_USE_TLS))
#if defined(MHD_SEND_SPIPE_SUPPRESS_NEEDED) && \
       defined(MHD_SEND_SPIPE_SUPPRESS_POSSIBLE)
       || (! daemon->sigpipe_blocked && ! connection->sk_spipe_suppress)
#endif /* MHD_SEND_SPIPE_SUPPRESS_NEEDED &&
          MHD_SEND_SPIPE_SUPPRESS_POSSIBLE */
       )
    connection->rp.resp_sender = MHD_resp_sender_std;
  else
    connection->rp.resp_sender = MHD_resp_sender_sendfile;
#endif /* _MHD_HAVE_SENDFILE */
  /* FIXME: if 'is_pipe' is set, TLS is off, and we have *splice*, we could use splice()
     to avoid two user-space copies... */

  if ( (MHD_HTTP_MTHD_HEAD == connection->rq.http_mthd) ||
       (MHD_HTTP_OK > status_code) ||
       (MHD_HTTP_NO_CONTENT == status_code) ||
       (MHD_HTTP_NOT_MODIFIED == status_code) )
  {
    /* if this is a "HEAD" request, or a status code for
       which a body is not allowed, pretend that we
       have already sent the full message body. */
    /* TODO: remove the next assignment, use 'rp_props.send_reply_body' in
     * checks */
    connection->rp.rsp_write_position = response->total_size;
    mhd_probe_log ("rsp_write_position set to total_size=%llu",
                   (unsigned long long) response->total_size);
  }
  if (MHD_CONNECTION_HEADERS_PROCESSED == connection->state)
  {
    /* response was queued "early", refuse to read body / footers or
       further requests! */
    connection->discard_request = true;
    connection->state = MHD_CONNECTION_START_REPLY;
    connection->rq.remaining_upload_size = 0;
    mhd_probe_log ("response queued early: state changed to START_REPLY discard_request=true");
  }
  if (! connection->in_idle)
    (void) MHD_connection_handle_idle (connection);
  MHD_update_last_activity_ (connection);
  mhd_probe_log ("exit MHD_queue_response: queued response=%p code=%u reply_icy=%d",
                 (void *) connection->rp.response,
                 connection->rp.responseCode,
                 (int) connection->rp.responseIcy);
  return MHD_YES;
}
