#include <stdio.h>
#include <stdarg.h>
#include <stddef.h>
#include <limits.h>

/* Probe logging helper: append formatted messages to /tmp/logs/963.txt */
static void MHD_probe_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/963.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fprintf(f, "\n");
  fclose(f);
}

_MHD_EXTERN enum MHD_Result
MHD_run_from_select2 (struct MHD_Daemon *daemon,
                      const fd_set *read_fd_set,
                      const fd_set *write_fd_set,
                      const fd_set *except_fd_set,
                      unsigned int fd_setsize)
{
  /* Log entry and key parameters */
  MHD_probe_log("ENTER MHD_run_from_select2: daemon=%p read_fd_set=%p write_fd_set=%p except_fd_set=%p fd_setsize=%u",
                (void *) daemon,
                (void *) read_fd_set,
                (void *) write_fd_set,
                (void *) except_fd_set,
                fd_setsize);

  if (MHD_D_IS_USING_POLL_ (daemon) ||
      MHD_D_IS_USING_THREADS_ (daemon))
  {
    MHD_probe_log("BRANCH: Using poll or threads - early return MHD_NO");
    return MHD_NO;
  }
  if ((NULL == read_fd_set) || (NULL == write_fd_set))
  {
    MHD_probe_log("BRANCH: read_fd_set or write_fd_set is NULL - early return MHD_NO");
    return MHD_NO;
  }
#ifdef HAVE_MESSAGES
  if (NULL == except_fd_set)
  {
    MHD_probe_log("NOTICE: except_fd_set is NULL (deprecated usage)");
    MHD_DLOG (daemon,
              _ ("MHD_run_from_select() called with except_fd_set "
                 "set to NULL. Such behavior is deprecated.\n"));
  }
#endif /* HAVE_MESSAGES */

#ifdef HAS_FD_SETSIZE_OVERRIDABLE
  if (0 == fd_setsize)
  {
    MHD_probe_log("BRANCH: fd_setsize == 0 - early return MHD_NO");
    return MHD_NO;
  }
  else if (((unsigned int) INT_MAX) < fd_setsize)
    fd_setsize = (unsigned int) INT_MAX;
#ifdef HAVE_MESSAGES
  else if (daemon->fdset_size > ((int) fd_setsize))
  {
    if (daemon->fdset_size_set_by_app)
    {
      MHD_DLOG (daemon,
                _ ("%s() called with fd_setsize (%u) " \
                   "less than value set by MHD_OPTION_APP_FD_SETSIZE (%d). " \
                   "Some socket FDs may be not processed. " \
                   "Use MHD_OPTION_APP_FD_SETSIZE with the correct value.\n"),
                "MHD_run_from_select2", fd_setsize, daemon->fdset_size);
    }
    else
    {
      MHD_DLOG (daemon,
                _ ("%s() called with fd_setsize (%u) " \
                   "less than FD_SETSIZE used by MHD (%d). " \
                   "Some socket FDs may be not processed. " \
                   "Consider using MHD_OPTION_APP_FD_SETSIZE option.\n"),
                "MHD_run_from_select2", fd_setsize, daemon->fdset_size);
    }
  }
#endif /* HAVE_MESSAGES */
#else  /* ! HAS_FD_SETSIZE_OVERRIDABLE */
  if (((unsigned int) FD_SETSIZE) > fd_setsize)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("%s() called with fd_setsize (%u) " \
                 "less than fixed FD_SETSIZE value (%d) used on the " \
                 "platform.\n"),
              "MHD_run_from_select2", fd_setsize, (int) FD_SETSIZE);
#endif /* HAVE_MESSAGES */
    MHD_probe_log("BRANCH: fd_setsize less than FD_SETSIZE - early return MHD_NO (fd_setsize=%u FD_SETSIZE=%d)",
                  fd_setsize, (int) FD_SETSIZE);
    return MHD_NO;
  }
#endif /* ! HAS_FD_SETSIZE_OVERRIDABLE */

  if (MHD_D_IS_USING_EPOLL_ (daemon))
  {
#ifdef EPOLL_SUPPORT
    enum MHD_Result ret = MHD_epoll (daemon,
                                     0);

    MHD_cleanup_connections (daemon);
    MHD_probe_log("BRANCH: Using epoll - delegated to MHD_epoll, result=%d", (int) ret);
    return ret;
#else  /* ! EPOLL_SUPPORT */
    MHD_probe_log("BRANCH: Using epoll but no EPOLL_SUPPORT - return MHD_NO");
    return MHD_NO;
#endif /* ! EPOLL_SUPPORT */
  }

  /* Resuming external connections when using an extern mainloop  */
  if (0 != (daemon->options & MHD_TEST_ALLOW_SUSPEND_RESUME))
  {
    MHD_probe_log("ACTION: resume_suspended_connections about to be called (daemon=%p)", (void *) daemon);
    resume_suspended_connections (daemon);
    MHD_probe_log("ACTION: resume_suspended_connections returned (daemon=%p)", (void *) daemon);
  }

  /*
   * Log the fact that we're about to call internal_run_from_select with
   * const-qualified fd_set pointers. If internal_run_from_select is declared
   * to accept non-const fd_set * parameters, this call would discard the
   * const qualifier and therefore violate MISRA C 2023 Rule 8.13.
   * Recording the pointer values and fd_setsize helps reproduce the
   * scenario and provides evidence for analysis.
   */
  MHD_probe_log("CALL: internal_run_from_select(daemon=%p, read_fd_set=%p, write_fd_set=%p, except_fd_set=%p, fd_setsize=%d)",
                (void *) daemon,
                (void *) read_fd_set,
                (void *) write_fd_set,
                (void *) except_fd_set,
                (int) fd_setsize);

  return internal_run_from_select (daemon,
                                   read_fd_set,
                                   write_fd_set,
                                   except_fd_set,
                                   (int) fd_setsize);
}
