#include <stdio.h>
#include <stdarg.h>

/* Probe logger: append textual logs to /tmp/logs/982.txt. */
static void MHD_log_probe(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/982.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fprintf(f, "\n");
  fclose(f);
}

static enum MHD_Result
MHD_select (struct MHD_Daemon *daemon,
            int32_t millisec)
{
  int num_ready;
  fd_set rs;
  fd_set ws;
  fd_set es;
  MHD_socket maxsock;
  struct timeval timeout;
  struct timeval *tv;
  int err_state;
  MHD_socket ls;

  timeout.tv_sec = 0;
  timeout.tv_usec = 0;
  MHD_log_probe("enter: initial timeout set: tv_sec=%lld tv_usec=%lld, millisec=%d, daemon=%p",
                (long long) timeout.tv_sec, (long long) timeout.tv_usec,
                (int) millisec, (void *) daemon);

  if (daemon->shutdown)
  {
    MHD_log_probe("early return: daemon->shutdown true");
    return MHD_NO;
  }

  FD_ZERO (&rs);
  FD_ZERO (&ws);
  FD_ZERO (&es);
  maxsock = MHD_INVALID_SOCKET;
  err_state = MHD_NO;

  if ( (0 != (daemon->options & MHD_TEST_ALLOW_SUSPEND_RESUME)) &&
       (MHD_NO != resume_suspended_connections (daemon)) &&
       (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) )
  {
    MHD_log_probe("suspend/resume path: forcing millisec=0 (was %d)", (int) millisec);
    millisec = 0;
  }

  if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
  {
    /* single-threaded, go over everything */
    if (MHD_NO ==
        internal_get_fdset2 (daemon,
                             &rs,
                             &ws,
                             &es,
                             &maxsock,
                             (int) FD_SETSIZE))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Could not obtain daemon fdsets.\n"));
#endif
      err_state = MHD_YES;
      MHD_log_probe("internal_get_fdset2 failed: err_state set to %d", err_state);
    }
    else
    {
      MHD_log_probe("internal_get_fdset2 succeeded: maxsock=%lld", (long long) maxsock);
    }
  }
  else
  {
    bool itc_added;
    /* accept only, have one thread per connection */
    itc_added = false;
    if (MHD_ITC_IS_VALID_ (daemon->itc))
    {
      itc_added = MHD_add_to_fd_set_ (MHD_itc_r_fd_ (daemon->itc),
                                      &rs,
                                      &maxsock,
                                      (int) FD_SETSIZE);
      if (! itc_added)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon, _ ("Could not add control inter-thread " \
                             "communication channel FD to fdset.\n"));
#endif
        err_state = MHD_YES;
        MHD_log_probe("itc FD add failed: err_state=%d", err_state);
      }
      else
      {
        MHD_log_probe("itc FD added: itc_added=true, maxsock=%lld", (long long) maxsock);
      }
    }
    if ( (MHD_INVALID_SOCKET != (ls = daemon->listen_fd)) &&
         (! daemon->was_quiesced) )
    {
      MHD_log_probe("listen fd present: ls=%lld, was_quiesced=%d, itc_added=%d, connections=%lld, connection_limit=%lld, at_limit=%d",
                    (long long) ls,
                    (int) daemon->was_quiesced,
                    (int) itc_added,
                    (long long) daemon->connections,
                    (long long) daemon->connection_limit,
                    (int) daemon->at_limit);
      /* Stop listening if we are at the configured connection limit */
      /* If we're at the connection limit, no point in really
         accepting new connections; however, make sure we do not miss
         the shutdown OR the termination of an existing connection; so
         only do this optimisation if we have a signaling ITC in
         place. */
      if (! itc_added ||
          ((daemon->connections < daemon->connection_limit) &&
           ! daemon->at_limit))
      {
        if (! MHD_add_to_fd_set_ (ls,
                                  &rs,
                                  &maxsock,
                                  (int) FD_SETSIZE))
        {
#ifdef HAVE_MESSAGES
          MHD_DLOG (daemon,
                    _ ("Could not add listen socket to fdset.\n"));
#endif
          err_state = MHD_YES;
          MHD_log_probe("adding listen fd to set failed: err_state=%d", err_state);
        }
        else
        {
          MHD_log_probe("listen fd added to set: ls=%lld, maxsock=%lld", (long long) ls, (long long) maxsock);
        }
      }
      else
      {
        MHD_log_probe("listen fd skipped due to itc_added/limits");
      }
    }
  }

  if (MHD_NO != err_state)
  {
    MHD_log_probe("err_state non-zero (%d): forcing millisec=0 (was %d)", err_state, (int) millisec);
    millisec = 0;
  }
  if (0 == millisec)
  {
    timeout.tv_usec = 0;
    timeout.tv_sec = 0;
    tv = &timeout;
    MHD_log_probe("millisec==0 branch: tv set to &timeout, timeout.tv_sec=%lld timeout.tv_usec=%lld",
                  (long long) timeout.tv_sec, (long long) timeout.tv_usec);
  }
  else
  {
    uint64_t mhd_tmo;
    uint64_t select_tmo;

    if ( (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) &&
         (MHD_NO != MHD_get_timeout64 (daemon, &mhd_tmo)) )
    {
      if ( (0 < millisec) &&
           (mhd_tmo > (uint64_t) millisec) )
        select_tmo = (uint64_t) millisec;
      else
        select_tmo = mhd_tmo;
      tv = &timeout; /* have timeout value */
      MHD_log_probe("timeout from MHD_get_timeout64: mhd_tmo=%llu, millisec=%d, select_tmo=%llu, tv=&timeout",
                    (unsigned long long) mhd_tmo, (int) millisec, (unsigned long long) select_tmo);
    }
    else if (0 < millisec)
    {
      select_tmo = (uint64_t) millisec;
      tv = &timeout; /* have timeout value */
      MHD_log_probe("millisec>0 fallback: select_tmo=%llu, tv=&timeout",
                    (unsigned long long) select_tmo);
    }
    else
    {
      select_tmo = 0; /* Not actually used, silent compiler warning */
      tv = NULL;
      MHD_log_probe("no timeout: select_tmo=0, tv=NULL");
    }

    if (NULL != tv)
    { /* have timeout value */
#if (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC
      if (select_tmo / 1000 > TIMEVAL_TV_SEC_MAX)
        timeout.tv_sec = TIMEVAL_TV_SEC_MAX;
      else
#endif /* (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC */
      timeout.tv_sec = (_MHD_TIMEVAL_TV_SEC_TYPE) (select_tmo / 1000);

      timeout.tv_usec = ((uint16_t) (select_tmo % 1000)) * ((int32_t) 1000);
      MHD_log_probe("computed timeout: select_tmo=%llu -> tv_sec=%lld tv_usec=%lld",
                    (unsigned long long) select_tmo,
                    (long long) timeout.tv_sec,
                    (long long) timeout.tv_usec);
    }
  }
  MHD_log_probe("about to select: maxsock=%lld, tv=%p, timeout.tv_sec=%lld timeout.tv_usec=%lld",
                (long long) maxsock, (void *) tv,
                (long long) timeout.tv_sec, (long long) timeout.tv_usec);

  num_ready = MHD_SYS_select_ (maxsock + 1,
                               &rs,
                               &ws,
                               &es,
                               tv);

  MHD_log_probe("after select: num_ready=%d", num_ready);

  if (daemon->shutdown)
  {
    MHD_log_probe("post-select: daemon->shutdown true, returning MHD_NO");
    return MHD_NO;
  }
  if (num_ready < 0)
  {
    const int err = MHD_socket_get_error_ ();
    MHD_log_probe("select error: err=%d", err);
    if (MHD_SCKT_ERR_IS_EINTR_ (err))
    {
      MHD_log_probe("select EINTR: returning %s", (MHD_NO == err_state) ? "MHD_YES" : "MHD_NO");
      return (MHD_NO == err_state) ? MHD_YES : MHD_NO;
    }
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("select failed: %s\n"),
              MHD_socket_strerr_ (err));
#endif
    MHD_log_probe("select failed and not EINTR: returning MHD_NO");
    return MHD_NO;
  }
  if (MHD_NO != internal_run_from_select (daemon,
                                          &rs,
                                          &ws,
                                          &es,
                                          (int) FD_SETSIZE))
  {
    MHD_log_probe("internal_run_from_select returned non-MHD_NO: returning %s",
                  (MHD_NO == err_state) ? "MHD_YES" : "MHD_NO");
    return (MHD_NO == err_state) ? MHD_YES : MHD_NO;
  }
  MHD_log_probe("exiting normally: returning MHD_NO");
  return MHD_NO;
}
