#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>

static void mhd_logf(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/1755.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fprintf(f, "\n");
  fclose(f);
}

ssize_t
MHD_send_data_ (struct MHD_Connection *connection,
                const char *buffer,
                size_t buffer_size,
                bool push_data)
{
  MHD_socket s = connection->socket_fd;
  ssize_t ret;
#ifdef HTTPS_SUPPORT
  const bool tls_conn = (connection->daemon->options & MHD_USE_TLS);
#else  /* ! HTTPS_SUPPORT */
  const bool tls_conn = false;
#endif /* ! HTTPS_SUPPORT */

  /* Log initial state relevant to the TLS decision and basic connection info. */
  mhd_logf("MHD_send_data_: entry: connection=%p, socket_fd=%lld, connection->state=%d, buffer_size=%zu, push_data=%d, tls_conn=%d",
            (void *) connection,
            (long long) s,
            (int) connection->state,
            buffer_size,
            (int) push_data,
            (int) tls_conn);

  if ( (MHD_INVALID_SOCKET == s) ||
       (MHD_CONNECTION_CLOSED == connection->state) )
  {
    mhd_logf("MHD_send_data_: early return: not connected (socket=%lld, state=%d)", (long long) s, (int) connection->state);
    return MHD_ERR_NOTCONN_;
  }

  if (buffer_size > SSIZE_MAX)
  {
    buffer_size = SSIZE_MAX; /* Max return value */
    push_data = false; /* Incomplete send */
    mhd_logf("MHD_send_data_: adjusted buffer_size to SSIZE_MAX, push_data set to false");
  }

  /* Log evaluation of the controlling expression for the TLS branch. */
  mhd_logf("MHD_send_data_: evaluating if (tls_conn) with tls_conn=%d", (int) tls_conn);

  if (tls_conn)
  {
    mhd_logf("MHD_send_data_: entering TLS branch (tls_conn=%d)", (int) tls_conn);
#ifdef HTTPS_SUPPORT
    pre_send_setopt (connection, (! tls_conn), push_data);
    ret = gnutls_record_send (connection->tls_session,
                              buffer,
                              buffer_size);
    mhd_logf("MHD_send_data_: gnutls_record_send returned %lld", (long long) ret);
    if (GNUTLS_E_AGAIN == ret)
    {
#ifdef EPOLL_SUPPORT
      connection->epoll_state &=
        ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif
      mhd_logf("MHD_send_data_: TLS EAGAIN -> return MHD_ERR_AGAIN_");
      return MHD_ERR_AGAIN_;
    }
    if (GNUTLS_E_INTERRUPTED == ret)
    {
      mhd_logf("MHD_send_data_: TLS INTERRUPTED -> return MHD_ERR_AGAIN_");
      return MHD_ERR_AGAIN_;
    }
    if ( (GNUTLS_E_ENCRYPTION_FAILED == ret) ||
         (GNUTLS_E_INVALID_SESSION == ret) ||
         (GNUTLS_E_COMPRESSION_FAILED == ret) ||
         (GNUTLS_E_EXPIRED == ret) ||
         (GNUTLS_E_HASH_FAILED == ret) )
    {
      mhd_logf("MHD_send_data_: TLS fatal error %lld -> return MHD_ERR_TLS_", (long long) ret);
      return MHD_ERR_TLS_;
    }
    if ( (GNUTLS_E_PUSH_ERROR == ret) ||
         (GNUTLS_E_INTERNAL_ERROR == ret) ||
         (GNUTLS_E_CRYPTODEV_IOCTL_ERROR == ret) ||
         (GNUTLS_E_CRYPTODEV_DEVICE_ERROR == ret) )
    {
      mhd_logf("MHD_send_data_: TLS pipe error %lld -> return MHD_ERR_PIPE_", (long long) ret);
      return MHD_ERR_PIPE_;
    }
#if defined(GNUTLS_E_PREMATURE_TERMINATION)
    if (GNUTLS_E_PREMATURE_TERMINATION == ret)
    {
      mhd_logf("MHD_send_data_: TLS premature termination -> return MHD_ERR_CONNRESET_");
      return MHD_ERR_CONNRESET_;
    }
#elif defined(GNUTLS_E_UNEXPECTED_PACKET_LENGTH)
    if (GNUTLS_E_UNEXPECTED_PACKET_LENGTH == ret)
    {
      mhd_logf("MHD_send_data_: TLS unexpected packet length -> return MHD_ERR_CONNRESET_");
      return MHD_ERR_CONNRESET_;
    }
#endif /* GNUTLS_E_UNEXPECTED_PACKET_LENGTH */
    if (GNUTLS_E_MEMORY_ERROR == ret)
    {
      mhd_logf("MHD_send_data_: TLS memory error -> return MHD_ERR_NOMEM_");
      return MHD_ERR_NOMEM_;
    }
    if (ret < 0)
    {
      /* Treat any other error as hard error. */
      mhd_logf("MHD_send_data_: TLS other error %lld -> return MHD_ERR_NOTCONN_", (long long) ret);
      return MHD_ERR_NOTCONN_;
    }
#ifdef EPOLL_SUPPORT
    /* Unlike non-TLS connections, do not reset "write-ready" if
     * sent amount smaller than provided amount, as TLS
     * connections may break data into smaller parts for sending. */
#endif /* EPOLL_SUPPORT */
#else  /* ! HTTPS_SUPPORT  */
    ret = MHD_ERR_NOTCONN_;
    mhd_logf("MHD_send_data_: compiled without HTTPS_SUPPORT; setting ret = MHD_ERR_NOTCONN_");
#endif /* ! HTTPS_SUPPORT  */
  }
  else
  {
    mhd_logf("MHD_send_data_: entering plaintext branch (tls_conn=%d)", (int) tls_conn);
    /* plaintext transmission */
    if (buffer_size > MHD_SCKT_SEND_MAX_SIZE_)
    {
      buffer_size = MHD_SCKT_SEND_MAX_SIZE_; /* send() return value limit */
      push_data = false; /* Incomplete send */
      mhd_logf("MHD_send_data_: adjusted buffer_size to MHD_SCKT_SEND_MAX_SIZE_, push_data set to false");
    }

    pre_send_setopt (connection, (! tls_conn), push_data);
#ifdef MHD_USE_MSG_MORE
    ret = MHD_send4_ (s,
                      buffer,
                      buffer_size,
                      push_data ? 0 : MSG_MORE);
#else
    ret = MHD_send4_ (s,
                      buffer,
                      buffer_size,
                      0);
#endif

    mhd_logf("MHD_send_data_: plaintext send returned %lld", (long long) ret);

    if (0 > ret)
    {
      const int err = MHD_socket_get_error_ ();

      mhd_logf("MHD_send_data_: plaintext send error err=%d", err);

      if (MHD_SCKT_ERR_IS_EAGAIN_ (err))
      {
#ifdef EPOLL_SUPPORT
        /* EAGAIN, no longer write-ready */
        connection->epoll_state &=
          ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */
        mhd_logf("MHD_send_data_: plaintext EAGAIN -> return MHD_ERR_AGAIN_");
        return MHD_ERR_AGAIN_;
      }
      if (MHD_SCKT_ERR_IS_EINTR_ (err))
      {
        mhd_logf("MHD_send_data_: plaintext EINTR -> return MHD_ERR_AGAIN_");
        return MHD_ERR_AGAIN_;
      }
      if (MHD_SCKT_ERR_IS_REMOTE_DISCNN_ (err))
      {
        mhd_logf("MHD_send_data_: plaintext remote disconnect -> return MHD_ERR_CONNRESET_");
        return MHD_ERR_CONNRESET_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EPIPE_))
      {
        mhd_logf("MHD_send_data_: plaintext EPIPE -> return MHD_ERR_PIPE_");
        return MHD_ERR_PIPE_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EOPNOTSUPP_))
      {
        mhd_logf("MHD_send_data_: plaintext EOPNOTSUPP -> return MHD_ERR_OPNOTSUPP_");
        return MHD_ERR_OPNOTSUPP_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_ENOTCONN_))
      {
        mhd_logf("MHD_send_data_: plaintext ENOTCONN -> return MHD_ERR_NOTCONN_");
        return MHD_ERR_NOTCONN_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EINVAL_))
      {
        mhd_logf("MHD_send_data_: plaintext EINVAL -> return MHD_ERR_INVAL_");
        return MHD_ERR_INVAL_;
      }
      if (MHD_SCKT_ERR_IS_LOW_RESOURCES_ (err))
      {
        mhd_logf("MHD_send_data_: plaintext low resources -> return MHD_ERR_NOMEM_");
        return MHD_ERR_NOMEM_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EBADF_))
      {
        mhd_logf("MHD_send_data_: plaintext EBADF -> return MHD_ERR_BADF_");
        return MHD_ERR_BADF_;
      }
      /* Treat any other error as a hard error. */
      mhd_logf("MHD_send_data_: plaintext unknown error -> return MHD_ERR_NOTCONN_");
      return MHD_ERR_NOTCONN_;
    }
#ifdef EPOLL_SUPPORT
    else if (buffer_size > (size_t) ret)
      connection->epoll_state &=
        ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */
  }

  /* If there is a need to push the data from network buffers
   * call post_send_setopt(). */
  /* If TLS connection is used then next final send() will be
   * without MSG_MORE support. If non-TLS connection is used
   * it's unknown whether sendfile() will be used or not so
   * assume that next call will be the same, like this call. */
  if ( (push_data) &&
       (buffer_size == (size_t) ret) )
  {
    mhd_logf("MHD_send_data_: calling post_send_setopt (push_data=%d, buffer_size=%zu, ret=%lld)", (int) push_data, buffer_size, (long long) ret);
    post_send_setopt (connection, (! tls_conn), push_data);
  }

  mhd_logf("MHD_send_data_: exit returning ret=%lld", (long long) ret);
  return ret;
}
