#include <stdio.h>
#include <stdarg.h>
#include <time.h>
#include <sys/stat.h>
#include <errno.h>

/* Probe helpers: write relatively small, robust logs to /tmp/logs/1012.txt.
 * These helpers are non-intrusive and try to preserve original behavior.
 */
static void
mhd_log_msg (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/1012.txt", "a");
  if (NULL == f)
  {
    /* Try to create directory and reopen. Ignore failures: logging is best-effort. */
    (void) mkdir("/tmp/logs", 0777);
    f = fopen("/tmp/logs/1012.txt", "a");
    if (NULL == f)
      return;
  }

  time_t t = time (NULL);
  struct tm tmbuf;
#if defined(_WIN32) || defined(__CYGWIN__)
  (void) localtime_s (&t, &tmbuf);
#else
  (void) localtime_r (&t, &tmbuf);
#endif
  char timestr[32];
  if (0 < strftime (timestr, sizeof (timestr), "%Y-%m-%d %H:%M:%S", &tmbuf))
    fprintf (f, "[%s] ", timestr);
  else
    fprintf (f, "[time-unavailable] ");

  va_list ap;
  va_start (ap, fmt);
  (void) vfprintf (f, fmt, ap);
  va_end (ap);
  fprintf (f, "\n");
  (void) fclose (f);
}

/* Log a compact snapshot of frequently-used daemon fields used in assertions.
 * Keep the function small to minimize risk of changing program behaviour.
 */
static void
mhd_log_daemon_state (const char *label, struct MHD_Daemon *daemon)
{
  if (NULL == daemon)
  {
    mhd_log_msg ("%s: daemon=NULL", label);
    return;
  }

  /* Print key fields that appear in assertions. Use casts to pointers/integers
   * to avoid depending on exact types.
   */
  mhd_log_msg (
    "%s: daemon=%p options=0x%08x master=%p shutdown=%d was_quiesced=%d "
    "listen_fd=%ld worker_pool=%p worker_pool_size=%u tid=%p itc=%p "
    "connections_head=%p cleanup_head=%p suspended_connections_head=%p new_connections_head=%p",
    label,
    (void *) daemon,
    (unsigned) daemon->options,
    (void *) daemon->master,
    (int) daemon->shutdown,
    (int) daemon->was_quiesced,
    (long) daemon->listen_fd,
    (void *) daemon->worker_pool,
    (unsigned) daemon->worker_pool_size,
    (void *) daemon->tid,
    (void *) daemon->itc,
    (void *) daemon->connections_head,
    (void *) daemon->cleanup_head,
    (void *) daemon->suspended_connections_head,
    (void *) daemon->new_connections_head);
}

_MHD_EXTERN void
MHD_stop_daemon (struct MHD_Daemon *daemon)
{
  MHD_socket fd;
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  unsigned int i;
#endif

  mhd_log_msg ("MHD_stop_daemon: entered (daemon=%p)", (void *) daemon);

  if (NULL == daemon)
  {
    mhd_log_msg ("MHD_stop_daemon: daemon is NULL, returning");
    return;
  }

  if ( (daemon->shutdown) && (NULL == daemon->master) )
    MHD_PANIC (_ ("MHD_stop_daemon() was called twice."));

  /* Log the state just before each assertion so that callers/builds that
   * disable mhd_assert can be compared against builds that enable it.
   */
  mhd_log_daemon_state ("assert-1: pre", daemon);
  mhd_log_msg ("assert-1: evaluating parts: (0 == (options & MHD_USE_SELECT_INTERNALLY)) -> 0x%08x, "
               "worker_pool=%p, tid=%p",
               (unsigned) (daemon->options & (unsigned) MHD_USE_SELECT_INTERNALLY),
               (void *) daemon->worker_pool,
               (void *) daemon->tid);
  mhd_assert ((0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (NULL != daemon->worker_pool) || \
              (MHD_thread_handle_ID_is_valid_handle_ (daemon->tid)));
  mhd_log_msg ("assert-1: completed");

  mhd_log_daemon_state ("assert-2: pre", daemon);
  mhd_log_msg ("assert-2: evaluating parts: (0 != (options & MHD_USE_SELECT_INTERNALLY)) -> 0x%08x, "
               "worker_pool=%p, tid=%p",
               (unsigned) (daemon->options & (unsigned) MHD_USE_SELECT_INTERNALLY),
               (void *) daemon->worker_pool,
               (void *) daemon->tid);
  mhd_assert (((0 != (daemon->options & MHD_USE_SELECT_INTERNALLY)) &&
               (NULL == daemon->worker_pool)) || \
              (! MHD_thread_handle_ID_is_valid_handle_ (daemon->tid)));
  mhd_log_msg ("assert-2: completed");

  mhd_log_daemon_state ("assert-3: pre", daemon);
  mhd_log_msg ("assert-3: master=%p shutdown=%d", (void *) daemon->master, (int) daemon->shutdown);
  /* Slave daemons must be stopped by master daemon. */
  mhd_assert ( (NULL == daemon->master) || (daemon->shutdown) );
  mhd_log_msg ("assert-3: completed");

  daemon->shutdown = true;
  mhd_log_msg ("daemon->shutdown set to true");

  if (daemon->was_quiesced)
    fd = MHD_INVALID_SOCKET; /* Do not use FD if daemon was quiesced */
  else
    fd = daemon->listen_fd;

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  if (NULL != daemon->worker_pool)
  {   /* Master daemon with worker pool. */
    mhd_log_msg ("entering master-with-worker-pool branch (daemon=%p)", (void *) daemon);
    mhd_log_msg ("assert-4: worker_pool_size=%u MHD_D_IS_USING_THREADS_=%p",
                 (unsigned) daemon->worker_pool_size,
                 (void *) daemon->tid);
    mhd_assert (1 < daemon->worker_pool_size);
    mhd_assert (MHD_D_IS_USING_THREADS_ (daemon));

    /* Let workers shutdown in parallel. */
    for (i = 0; i < daemon->worker_pool_size; ++i)
    {
      daemon->worker_pool[i].shutdown = true;
      mhd_log_msg ("signalled worker[%u].shutdown=true", i);
      if (MHD_ITC_IS_VALID_ (daemon->worker_pool[i].itc))
      {
        if (! MHD_itc_activate_ (daemon->worker_pool[i].itc,
                                 "e"))
          MHD_PANIC (_ ("Failed to signal shutdown via inter-thread " \
                        "communication channel.\n"));
        mhd_log_msg ("activated itc for worker[%u]", i);
      }
      else
        mhd_assert (MHD_INVALID_SOCKET != fd);
    }
#ifdef HAVE_LISTEN_SHUTDOWN
    if (MHD_INVALID_SOCKET != fd)
    {
      (void) shutdown (fd,
                       SHUT_RDWR);
      mhd_log_msg ("shutdown(listen_fd) called in master-with-worker-pool branch");
    }
#endif /* HAVE_LISTEN_SHUTDOWN */
    for (i = 0; i < daemon->worker_pool_size; ++i)
    {
      MHD_stop_daemon (&daemon->worker_pool[i]);
      mhd_log_msg ("returned from MHD_stop_daemon for worker[%u]", i);
    }
    free (daemon->worker_pool);
    mhd_log_msg ("freed daemon->worker_pool");
    mhd_assert (MHD_ITC_IS_INVALID_ (daemon->itc));
#ifdef EPOLL_SUPPORT
    mhd_assert (-1 == daemon->epoll_fd);
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
    mhd_assert (-1 == daemon->epoll_upgrade_fd);
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */
#endif /* EPOLL_SUPPORT */
  }
  else
#endif
  {   /* Worker daemon or single daemon. */
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    if (MHD_D_IS_USING_THREADS_ (daemon))
    {     /* Worker daemon or single daemon with internal thread(s). */
      mhd_log_msg ("entering worker-or-single-with-threads branch (daemon=%p)", (void *) daemon);
      mhd_assert (0 == daemon->worker_pool_size);
      /* Separate thread(s) is used for polling sockets. */
      if (MHD_ITC_IS_VALID_ (daemon->itc))
      {
        if (! MHD_itc_activate_ (daemon->itc,
                                 "e"))
          MHD_PANIC (_ ("Failed to signal shutdown via inter-thread " \
                        "communication channel.\n"));
        mhd_log_msg ("activated itc for daemon (threaded)");
      }
      else
      {
#ifdef HAVE_LISTEN_SHUTDOWN
        if (MHD_INVALID_SOCKET != fd)
        {
          if (NULL == daemon->master)
            (void) shutdown (fd,
                             SHUT_RDWR);
          mhd_log_msg ("shutdown(listen_fd) called in worker-or-single-with-threads branch");
        }
        else
#endif /* HAVE_LISTEN_SHUTDOWN */
        mhd_assert (false); /* Should never happen */
      }

      if (! MHD_thread_handle_ID_join_thread_ (daemon->tid))
      {
        MHD_PANIC (_ ("Failed to join a thread.\n"));
      }
      mhd_log_msg ("joined thread tid=%p", (void *) daemon->tid);
      /* close_all_connections() was called in daemon thread. */
    }
    else
#endif
    {
      /* No internal threads are used for polling sockets. */
      mhd_log_msg ("calling close_all_connections for daemon=%p", (void *) daemon);
      close_all_connections (daemon);
    }
    mhd_assert (NULL == daemon->connections_head);
    mhd_assert (NULL == daemon->cleanup_head);
    mhd_assert (NULL == daemon->suspended_connections_head);
    mhd_assert (NULL == daemon->new_connections_head);
#if defined(UPGRADE_SUPPORT) && defined(HTTPS_SUPPORT)
    mhd_assert (NULL == daemon->urh_head);
#endif /* UPGRADE_SUPPORT && HTTPS_SUPPORT */

    if (MHD_ITC_IS_VALID_ (daemon->itc))
      MHD_itc_destroy_chk_ (daemon->itc);

#ifdef EPOLL_SUPPORT
    if (MHD_D_IS_USING_EPOLL_ (daemon) &&
        (-1 != daemon->epoll_fd) )
      MHD_socket_close_chk_ (daemon->epoll_fd);
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
    if (MHD_D_IS_USING_EPOLL_ (daemon) &&
        (-1 != daemon->epoll_upgrade_fd) )
      MHD_socket_close_chk_ (daemon->epoll_upgrade_fd);
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */
#endif /* EPOLL_SUPPORT */

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_destroy_chk_ (&daemon->cleanup_connection_mutex);
    MHD_mutex_destroy_chk_ (&daemon->new_connections_mutex);
#endif
  }

  if (NULL == daemon->master)
  {   /* Cleanup that should be done only one time in master/single daemon.
       * Do not perform this cleanup in worker daemons. */

    mhd_log_msg ("performing master/single-daemon cleanup for daemon=%p", (void *) daemon);

    if (MHD_INVALID_SOCKET != fd)
    {
      MHD_socket_close_chk_ (fd);
      mhd_log_msg ("closed listen fd=%ld", (long) fd);
    }

    /* TLS clean up */
#ifdef HTTPS_SUPPORT
    if (daemon->have_dhparams)
    {
      gnutls_dh_params_deinit (daemon->https_mem_dhparams);
      daemon->have_dhparams = false;
      mhd_log_msg ("freed dhparams");
    }
    if (0 != (daemon->options & MHD_USE_TLS))
    {
      gnutls_priority_deinit (daemon->priority_cache);
      if (daemon->x509_cred)
        gnutls_certificate_free_credentials (daemon->x509_cred);
      if (daemon->psk_cred)
        gnutls_psk_free_server_credentials (daemon->psk_cred);
      mhd_log_msg ("TLS credentials cleaned up");
    }
#endif /* HTTPS_SUPPORT */

#ifdef DAUTH_SUPPORT
    free (daemon->digest_auth_random_copy);
    free (daemon->nnc);
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_destroy_chk_ (&daemon->nnc_lock);
#endif
    mhd_log_msg ("DAUTH_SUPPORT: freed authentication related memory");
#endif
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_destroy_chk_ (&daemon->per_ip_connection_mutex);
#endif
    free (daemon);
    mhd_log_msg ("freed daemon memory and exiting MHD_stop_daemon");
  }
}
