#include <stdio.h>
#include <stdbool.h>
#include <stddef.h>
#include <sys/stat.h>
#include <sys/types.h>

/* Probe helper: append a compact state/log message to /tmp/logs/1514.txt.
   Attempts to create /tmp/logs directory if necessary. This helper is kept
   small and non-intrusive so as to preserve the original function behaviour. */
static void
mhd_log_probe(const char *label,
              size_t i,
              size_t j,
              size_t quoted_len,
              size_t unquoted_len,
              const char *quoted,
              const char *unquoted)
{
  /* Try to create directory; ignore errors if it already exists. */
  (void)mkdir("/tmp/logs", 0700);

  FILE *f = fopen("/tmp/logs/1514.txt", "a");
  if (f == NULL)
    return;

  fprintf(f, "%s: i=%zu j=%zu quoted_len=%zu unquoted_len=%zu\n",
          label, i, j, quoted_len, unquoted_len);

  if (quoted != NULL && i < quoted_len) {
    unsigned char qc = (unsigned char) quoted[i];
    /* print hex and a printable character (or '.') */
    fprintf(f, "  quoted[i]=0x%02X '%c'\n", qc,
            (qc >= ' ' && qc <= '~') ? (char)qc : '.');
  }
  if (unquoted != NULL && j < unquoted_len) {
    unsigned char uc = (unsigned char) unquoted[j];
    fprintf(f, "  unquoted[j]=0x%02X '%c'\n", uc,
            (uc >= ' ' && uc <= '~') ? (char)uc : '.');
  }

  fclose(f);
}

bool
MHD_str_equal_caseless_quoted_bin_n (const char *quoted,
                                     size_t quoted_len,
                                     const char *unquoted,
                                     size_t unquoted_len)
{
  size_t i;
  size_t j;

  /* Log entry and initial sizes */
  mhd_log_probe("entry", 0u, 0u, quoted_len, unquoted_len, quoted, unquoted);

  if (unquoted_len < quoted_len / 2) {
    mhd_log_probe("early_return_unquoted_short", 0u, 0u, quoted_len, unquoted_len, quoted, unquoted);
    return false;
  }

  j = 0;
  mhd_log_probe("before_loop", 0u, j, quoted_len, unquoted_len, quoted, unquoted);

  for (i = 0; quoted_len > i && unquoted_len > j; ++i, ++j)
  {
    mhd_log_probe("loop_iter_entry", i, j, quoted_len, unquoted_len, quoted, unquoted);

    if ('\\' == quoted[i])
    {
      mhd_log_probe("backslash_found_before_inc", i, j, quoted_len, unquoted_len, quoted, unquoted);

      i++; /* Advance to the next character */

      mhd_log_probe("backslash_after_inc", i, j, quoted_len, unquoted_len, quoted, unquoted);

      if (quoted_len == i) {
        mhd_log_probe("early_return_no_char_after_escape", i, j, quoted_len, unquoted_len, quoted, unquoted);
        return false; /* No character after escaping backslash */
      }
    }

    if (! charsequalcaseless (quoted[i], unquoted[j])) {
      mhd_log_probe("char_mismatch", i, j, quoted_len, unquoted_len, quoted, unquoted);
      return false; /* Different characters */
    }

    mhd_log_probe("loop_iter_exit", i, j, quoted_len, unquoted_len, quoted, unquoted);
  }

  mhd_log_probe("after_loop", i, j, quoted_len, unquoted_len, quoted, unquoted);

  if ((quoted_len != i) || (unquoted_len != j)) {
    mhd_log_probe("final_length_mismatch", i, j, quoted_len, unquoted_len, quoted, unquoted);
    return false; /* The strings have different length */
  }

  mhd_log_probe("final_success", i, j, quoted_len, unquoted_len, quoted, unquoted);
  return true;
}
