#include <stdbool.h>
#include <stddef.h>
#include <stdio.h>

bool
MHD_str_equal_quoted_bin_n (const char *quoted,
                            size_t quoted_len,
                            const char *unquoted,
                            size_t unquoted_len)
{
  size_t i;
  size_t j;
  FILE *log = fopen("/tmp/logs/1524.txt", "a");
  if (log != NULL)
  {
    fprintf(log, "MHD_str_equal_quoted_bin_n entry: quoted_len=%zu unquoted_len=%zu\n", quoted_len, unquoted_len);
  }

  if (unquoted_len < quoted_len / 2)
  {
    if (log != NULL)
    {
      fprintf(log, "early return: unquoted_len < quoted_len/2 (%zu < %zu)\n", unquoted_len, quoted_len / 2);
      fclose(log);
    }
    return false;
  }

  j = 0;
  if (log != NULL)
    fprintf(log, "init: i=0 j=%zu\n", j);

  for (i = 0; quoted_len > i && unquoted_len > j; ++i, ++j)
  {
    if (log != NULL)
      fprintf(log, "loop start: i=%zu j=%zu quoted[%zu]=%d unquoted[%zu]=%d\n",
              i, j, i, (int)(unsigned char)quoted[i], j, (int)(unsigned char)unquoted[j]);

    if ('\\' == quoted[i])
    {
      if (log != NULL)
        fprintf(log, "backslash found at quoted[%zu]\n", i);

      i++; /* Advance to the next character */

      if (log != NULL)
        fprintf(log, "internal increment: i++ in loop body -> i=%zu\n", i);

      if (quoted_len == i)
      {
        if (log != NULL)
        {
          fprintf(log, "return false: escaped backslash with no following character (quoted_len==i==%zu)\n", i);
          fclose(log);
        }
        return false; /* No character after escaping backslash */
      }
    }

    if (quoted[i] != unquoted[j])
    {
      if (log != NULL)
      {
        fprintf(log, "return false: mismatch at quoted[%zu]=%d unquoted[%zu]=%d\n",
                i, (int)(unsigned char)quoted[i], j, (int)(unsigned char)unquoted[j]);
        fclose(log);
      }
      return false; /* Different characters */
    }

    if (log != NULL)
      fprintf(log, "iteration end: i=%zu j=%zu (about to perform for-loop ++i, ++j)\n", i, j);
  }

  if (log != NULL)
    fprintf(log, "after loop: i=%zu j=%zu quoted_len=%zu unquoted_len=%zu\n", i, j, quoted_len, unquoted_len);

  if ((quoted_len != i) || (unquoted_len != j))
  {
    if (log != NULL)
    {
      fprintf(log, "return false: length mismatch quoted_len!=i or unquoted_len!=j -> %zu!=%zu or %zu!=%zu\n",
              quoted_len, i, unquoted_len, j);
      fclose(log);
    }
    return false; /* The strings have different length */
  }

  if (log != NULL)
  {
    fprintf(log, "return true: strings equal (final i=%zu j=%zu)\n", i, j);
    fclose(log);
  }

  return true;
}
