#include <stdio.h>
#include <stdbool.h>
#include <stddef.h>

/* Probe function: append a single-line log entry to /tmp/logs/1524.txt.
   Keeps side effects minimal and does not alter the logic of the function. */
static void MHD_probe_log(const char *event, size_t i, size_t j, size_t quoted_len, size_t unquoted_len)
{
    FILE *f = fopen("/tmp/logs/1524.txt", "a");
    if (f != NULL)
    {
        /* Log event name and relevant size_t variables. */
        fprintf(f, "%s: i=%zu j=%zu quoted_len=%zu unquoted_len=%zu\n",
                event, i, j, quoted_len, unquoted_len);
        fclose(f);
    }
}

bool
MHD_str_equal_quoted_bin_n (const char *quoted,
                            size_t quoted_len,
                            const char *unquoted,
                            size_t unquoted_len)
{
  size_t i;
  size_t j;
  if (unquoted_len < quoted_len / 2)
  {
    MHD_probe_log("early_return_short_unquoted", 0u, 0u, quoted_len, unquoted_len);
    return false;
  }

  /* j is initialized outside the for-statement — probe this state to
     provide evidence for the reported MISRA Rule 14.2 scenario. */
  j = 0;
  MHD_probe_log("init_j_outside_for", 0u, j, quoted_len, unquoted_len);

  for (i = 0; quoted_len > i && unquoted_len > j; ++i, ++j)
  {
    MHD_probe_log("loop_iter_start", i, j, quoted_len, unquoted_len);

    if ('\\' == quoted[i])
    {
      i++; /* Advance to the next character */
      MHD_probe_log("escaped_backslash_incremented_i", i, j, quoted_len, unquoted_len);
      if (quoted_len == i)
      {
        MHD_probe_log("return_false_no_char_after_escape", i, j, quoted_len, unquoted_len);
        return false; /* No character after escaping backslash */
      }
    }
    if (quoted[i] != unquoted[j])
    {
      MHD_probe_log("return_false_mismatch", i, j, quoted_len, unquoted_len);
      return false; /* Different characters */
    }
  }

  MHD_probe_log("after_loop", i, j, quoted_len, unquoted_len);
  if ((quoted_len != i) || (unquoted_len != j))
  {
    MHD_probe_log("return_false_length_mismatch", i, j, quoted_len, unquoted_len);
    return false; /* The strings have different length */
  }

  MHD_probe_log("return_true_match", i, j, quoted_len, unquoted_len);
  return true;
}
