#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <inttypes.h>
#include <sys/stat.h>
#include <sys/types.h>
#include <errno.h>

/* Lightweight logging helper that appends messages to /tmp/logs/1599.txt.
 * It is careful to NOT dereference any string pointers passed to it; it only
 * logs pointer values and numeric sizes to avoid introducing additional
 * out-of-bounds accesses. */
static void
mhd_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/1599.txt", "a");
  if (NULL == f)
  {
    /* Try to create the directory once and retry */
    (void) mkdir("/tmp/logs", 0777);
    f = fopen("/tmp/logs/1599.txt", "a");
    if (NULL == f)
      return; /* Give up silently — don't change program control flow */
  }
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  fprintf(f, "\n");
  va_end(ap);
  fflush(f);
  fclose(f);
}

bool
MHD_str_remove_token_caseless_ (const char *str,
                                size_t str_len,
                                const char *const token,
                                const size_t token_len,
                                char *buf,
                                ssize_t *buf_size)
{
  const char *s1; /**< the "input" string / character */
  char *s2;       /**< the "output" string / character */
  size_t t_pos;   /**< position of matched character in the token */
  bool token_removed;

  /* Log initial parameters (do NOT dereference token/str/buf contents) */
  mhd_log("ENTRY: %s: str=%p str_len=%zu token=%p token_len=%zu buf=%p buf_size_ptr=%p buf_size=*%s?",
          "MHD_str_remove_token_caseless_",
          (void *) str, str_len, (void *) token, token_len, (void *) buf, (void *) buf_size,
          (buf_size ? "yes" : "no"));

  /* Log that memchr assertions are about to be evaluated. These memchr calls
   * are the original checks and may themselves read token_len bytes from
   * token; we only log the intention and parameters (not the token contents)
   * so we do not introduce additional out-of-bounds accesses here. */
  mhd_log("ASSERT_CHECK: memchr(token, 0, token_len) -> token=%p token_len=%zu",
          (void *) token, token_len);
  mhd_log("ASSERT_CHECK: memchr(token, ' ', token_len) -> token=%p token_len=%zu",
          (void *) token, token_len);
  mhd_log("ASSERT_CHECK: memchr(token, '\t', token_len) -> token=%p token_len=%zu",
          (void *) token, token_len);
  mhd_log("ASSERT_CHECK: memchr(token, ',', token_len) -> token=%p token_len=%zu",
          (void *) token, token_len);

  mhd_assert (NULL == memchr (token, 0, token_len));
  mhd_assert (NULL == memchr (token, ' ', token_len));
  mhd_assert (NULL == memchr (token, '\t', token_len));
  mhd_assert (NULL == memchr (token, ',', token_len));
  mhd_assert (0 <= *buf_size);

  mhd_log("ASSERTS_PASSED: buf_size_initial=%lld",
          (long long) *buf_size);

  if (SSIZE_MAX <= ((str_len / 2) * 3 + 3))
  {
    /* The return value may overflow, refuse */
    *buf_size = (ssize_t) -1;
    mhd_log("EARLY_RETURN: overflow risk, setting *buf_size=-1 and returning false");
    return false;
  }
  s1 = str;
  s2 = buf;
  token_removed = false;

  unsigned loop_iter = 0;
  while ((size_t) (s1 - str) < str_len)
  {
    const char *cur_token; /**< the first char of current token */
    size_t copy_size;

    loop_iter++;
    mhd_log("LOOP_ENTER: iter=%u s1=%p s1_offset=%zu",
            loop_iter, (void *) s1, (size_t) (s1 - str));

    /* Skip any initial whitespaces and empty tokens */
    while ( ((size_t) (s1 - str) < str_len) &&
            ((' ' == *s1) || ('\t' == *s1) || (',' == *s1)) )
      s1++;

    /* 's1' points to the first char of token in the input string or
     * points just beyond the end of the input string */

    if ((size_t) (s1 - str) >= str_len)
    {
      mhd_log("LOOP_BREAK: reached end of input: s1_offset=%zu str_len=%zu",
              (size_t) (s1 - str), str_len);
      break; /* Nothing to copy, end of the input string */
    }

    /* 's1' points to the first char of token in the input string */

    cur_token = s1; /* the first char of input token */

    /* Check the token with case-insensetive match */
    t_pos = 0;
    while ( ((size_t) (s1 - str) < str_len) && (token_len > t_pos) &&
            (charsequalcaseless (*s1, token[t_pos])) )
    {
      s1++;
      t_pos++;
    }
    /* s1 may point just beyond the end of the input string */
    mhd_log("MATCH_CHECK: iter=%u t_pos=%zu token_len=%zu s1_offset=%zu",
            loop_iter, t_pos, token_len, (size_t) (s1 - str));

    if ( (token_len == t_pos) && (0 != token_len) )
    {
      /* 'token' matched, check that current input token does not have
       * any suffixes */
      while ( ((size_t) (s1 - str) < str_len) &&
              ((' ' == *s1) || ('\t' == *s1)) )
        s1++;
      /* 's1' points to the first non-whitespace char after the token matched
       * requested token or points just beyond the end of the input string after
       * the requested token */
      if (((size_t) (s1 - str) == str_len) || (',' == *s1))
      {/* full token match, do not copy current token to the output */
        token_removed = true;
        mhd_log("TOKEN_REMOVED: iter=%u cur_token_offset=%zu token_len=%zu s1_offset=%zu",
                loop_iter, (size_t) (cur_token - str), token_len, (size_t) (s1 - str));
        continue;
      }
    }

    /* 's1' points to first non-whitespace char, to some char after
     * first non-whitespace char in the token in the input string, to
     * the ',', or just beyond the end of the input string */
    /* The current token in the input string does not match the token
     * to exclude, it must be copied to the output string */
    /* the current token size excluding leading whitespaces and current char */
    copy_size = (size_t) (s1 - cur_token);

    mhd_log("COPY_PREP: iter=%u cur_token_offset=%zu copy_size=%zu s2_offset=%zu buf_ptr=%p buf_size_ptr=%p *buf_size=%lld",
            loop_iter, (size_t) (cur_token - str), copy_size, (size_t) (s2 - buf), (void *) buf, (void *) buf_size, (long long) *buf_size);

    if (buf == s2)
    { /* The first token to copy to the output */
      if ((size_t) *buf_size < copy_size)
      { /* Not enough space in the output buffer */
        *buf_size = (ssize_t) -1;
        mhd_log("RETURN_BUFFER_TOO_SMALL_FIRST: iter=%u required=%zu available=%lld -> returning false",
                loop_iter, copy_size, (long long) *buf_size);
        return false;
      }
    }
    else
    { /* Some token was already copied to the output buffer */
      mhd_assert (s2 > buf);
      if ((size_t) *buf_size < ((size_t) (s2 - buf)) + copy_size + 2)
      { /* Not enough space in the output buffer */
        *buf_size = (ssize_t) -1;
        mhd_log("RETURN_BUFFER_TOO_SMALL_NOT_FIRST: iter=%u needed=%zu have=%lld -> returning false",
                loop_iter, ((size_t) (s2 - buf)) + copy_size + 2, (long long) *buf_size);
        return false;
      }
      *(s2++) = ',';
      *(s2++) = ' ';
    }
    /* Copy non-matched token to the output */
    if (0 != copy_size)
    {
      mhd_log("ABOUT_TO_MEMCPY: iter=%u dest=%p dest_offset=%zu src=%p src_offset=%zu copy_size=%zu",
              loop_iter, (void *) s2, (size_t) (s2 - buf), (void *) cur_token, (size_t) (cur_token - str), copy_size);
      memcpy (s2, cur_token, copy_size);
      s2 += copy_size;
      mhd_log("AFTER_MEMCPY: iter=%u s2_offset=%zu",
              loop_iter, (size_t) (s2 - buf));
    }

    while ( ((size_t) (s1 - str) < str_len) && (',' != *s1))
    {
      /* 's1' points to first non-whitespace char, to some char after
       * first non-whitespace char in the token in the input string */
      /* Copy all non-whitespace chars from the current token in
       * the input string */
      while ( ((size_t) (s1 - str) < str_len) &&
              (',' != *s1) && (' ' != *s1) && ('\t' != *s1) )
      {
        mhd_assert (s2 >= buf);
        if ((size_t) *buf_size <= (size_t) (s2 - buf)) /* '<= s2' equals '< s2 + 1' */
        { /* Not enough space in the output buffer */
          *buf_size = (ssize_t) -1;
          mhd_log("RETURN_BUFFER_TOO_SMALL_DURING_COPY: iter=%u -> returning false",
                  loop_iter);
          return false;
        }
        *(s2++) = *(s1++);
      }
      /* 's1' points to some whitespace char in the token in the input
       * string, to the ',', or just beyond the end of the input string */
      /* Skip all whitespaces */
      while ( ((size_t) (s1 - str) < str_len) &&
              ((' ' == *s1) || ('\t' == *s1)) )
        s1++;

      /* 's1' points to the first non-whitespace char in the input string
       * after whitespace chars, to the ',', or just beyond the end of
       * the input string */
      if (((size_t) (s1 - str) < str_len) && (',' != *s1))
      { /* Not the end of the current token */
        mhd_assert (s2 >= buf);
        if ((size_t) *buf_size <= (size_t) (s2 - buf)) /* '<= s2' equals '< s2 + 1' */
        { /* Not enough space in the output buffer */
          *buf_size = (ssize_t) -1;
          mhd_log("RETURN_BUFFER_TOO_SMALL_BEFORE_SPACE: iter=%u -> returning false",
                  loop_iter);
          return false;
        }
        *(s2++) = ' ';
      }
    }
  }
  mhd_assert (((ssize_t) (s2 - buf)) <= *buf_size);
  *buf_size = (ssize_t) (s2 - buf);
  mhd_log("EXIT: token_removed=%d final_buf_size=%lld",
          token_removed ? 1 : 0, (long long) *buf_size);
  return token_removed;
}
