#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <stddef.h>
#include <stdbool.h>
#include <limits.h>
#include <string.h>

/* Probe helper - append a formatted log line to /tmp/logs/1599.txt */
static void
mhd_probe_log (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/1599.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fputc('\n', f);
  fclose (f);
}

bool
MHD_str_remove_token_caseless_ (const char *str,
                                size_t str_len,
                                const char *const token,
                                const size_t token_len,
                                char *buf,
                                ssize_t *buf_size)
{
  const char *s1; /**< the "input" string / character */
  char *s2;       /**< the "output" string / character */
  size_t t_pos;   /**< position of matched character in the token */
  bool token_removed;

  /* Log entry and parameters to help reproduce memchr-related issues. */
  mhd_probe_log ("ENTER MHD_str_remove_token_caseless_: str=%p str_len=%zu token=%p token_len=%zu buf=%p buf_size=%p (*buf_size=%lld)",
                 (void *) str, str_len, (void *) token, token_len, (void *) buf, (void *) buf_size,
                 (long long) ((NULL != buf_size) ? *buf_size : (ssize_t) -1));

  /* Log that memchr checks are going to be performed. Do not dereference
   * token here to avoid making out-of-bounds reads; only log the pointer
   * and the length that will be passed to memchr. This helps to reproduce
   * scenarios where token_len exceeds the actual allocated size of token. */
  mhd_probe_log ("About to perform memchr checks on token %p with token_len=%zu (checks: 0, ' ', '\t', ',')",
                 (void *) token, token_len);

  mhd_assert (NULL == memchr (token, 0, token_len));
  mhd_assert (NULL == memchr (token, ' ', token_len));
  mhd_assert (NULL == memchr (token, '\t', token_len));
  mhd_assert (NULL == memchr (token, ',', token_len));
  mhd_assert (0 <= *buf_size);

  if (SSIZE_MAX <= ((str_len / 2) * 3 + 3))
  {
    /* The return value may overflow, refuse */
    mhd_probe_log ("Early return: size overflow condition triggered: str_len=%zu computed_limit=%zu",
                   str_len, (size_t) ((str_len / 2) * 3 + 3));
    *buf_size = (ssize_t) -1;
    mhd_probe_log ("EXIT MHD_str_remove_token_caseless_: returning false, *buf_size=%lld",
                   (long long) *buf_size);
    return false;
  }
  s1 = str;
  s2 = buf;
  token_removed = false;

  while ((size_t) (s1 - str) < str_len)
  {
    const char *cur_token; /**< the first char of current token */
    size_t copy_size;

    mhd_probe_log ("LOOP: offset=%zu str_len=%zu token_removed=%d s1=%p s2=%p",
                   (size_t) (s1 - str), str_len, token_removed, (void *) s1, (void *) s2);

    /* Skip any initial whitespaces and empty tokens */
    while ( ((size_t) (s1 - str) < str_len) &&
            ((' ' == *s1) || ('\t' == *s1) || (',' == *s1)) )
      s1++;

    /* 's1' points to the first char of token in the input string or
     * points just beyond the end of the input string */

    if ((size_t) (s1 - str) >= str_len)
      break; /* Nothing to copy, end of the input string */

    /* 's1' points to the first char of token in the input string */

    cur_token = s1; /* the first char of input token */

    /* Check the token with case-insensetive match */
    t_pos = 0;
    mhd_probe_log ("Checking token match: cur_token=%p token_len=%zu t_pos=%zu",
                   (void *) cur_token, token_len, t_pos);
    while ( ((size_t) (s1 - str) < str_len) && (token_len > t_pos) &&
            (charsequalcaseless (*s1, token[t_pos])) )
    {
      s1++;
      t_pos++;
    }
    /* s1 may point just beyond the end of the input string */

    mhd_probe_log ("After match loop: s1_off=%zu t_pos=%zu token_len=%zu",
                   (size_t) (s1 - str), t_pos, token_len);
    if ( (token_len == t_pos) && (0 != token_len) )
    {
      /* 'token' matched, check that current input token does not have
       * any suffixes */
      while ( ((size_t) (s1 - str) < str_len) &&
              ((' ' == *s1) || ('\t' == *s1)) )
        s1++;
      /* 's1' points to the first non-whitespace char after the token matched
       * requested token or points just beyond the end of the input string after
       * the requested token */
      if (((size_t) (s1 - str) == str_len) || (',' == *s1))
      {/* full token match, do not copy current token to the output */
        token_removed = true;
        mhd_probe_log ("Token matched and removed at offset=%zu token_len=%zu",
                       (size_t) (cur_token - str), token_len);
        continue;
      }
    }

    /* 's1' points to first non-whitespace char, to some char after
     * first non-whitespace char in the token in the input string, to
     * the ',', or just beyond the end of the input string */
    /* The current token in the input string does not match the token
     * to exclude, it must be copied to the output string */
    /* the current token size excluding leading whitespaces and current char */
    copy_size = (size_t) (s1 - cur_token);
    mhd_probe_log ("Preparing to copy token: cur_token=%p copy_size=%zu buf_pos=%zu *buf_size=%lld",
                   (void *) cur_token, copy_size, (size_t) (s2 - buf), (long long) *buf_size);
    if (buf == s2)
    { /* The first token to copy to the output */
      if ((size_t) *buf_size < copy_size)
      { /* Not enough space in the output buffer */
        *buf_size = (ssize_t) -1;
        mhd_probe_log ("EXIT MHD_str_remove_token_caseless_: insufficient buffer (first token). setting *buf_size=-1");
        return false;
      }
    }
    else
    { /* Some token was already copied to the output buffer */
      mhd_assert (s2 > buf);
      if ((size_t) *buf_size < ((size_t) (s2 - buf)) + copy_size + 2)
      { /* Not enough space in the output buffer */
        *buf_size = (ssize_t) -1;
        mhd_probe_log ("EXIT MHD_str_remove_token_caseless_: insufficient buffer (subsequent token). setting *buf_size=-1");
        return false;
      }
      *(s2++) = ',';
      *(s2++) = ' ';
    }
    /* Copy non-matched token to the output */
    if (0 != copy_size)
    {
      memcpy (s2, cur_token, copy_size);
      s2 += copy_size;
    }

    while ( ((size_t) (s1 - str) < str_len) && (',' != *s1))
    {
      /* 's1' points to first non-whitespace char, to some char after
       * first non-whitespace char in the token in the input string */
      /* Copy all non-whitespace chars from the current token in
       * the input string */
      while ( ((size_t) (s1 - str) < str_len) &&
              (',' != *s1) && (' ' != *s1) && ('\t' != *s1) )
      {
        mhd_assert (s2 >= buf);
        if ((size_t) *buf_size <= (size_t) (s2 - buf)) /* '<= s2' equals '< s2 + 1' */
        { /* Not enough space in the output buffer */
          *buf_size = (ssize_t) -1;
          mhd_probe_log ("EXIT MHD_str_remove_token_caseless_: insufficient buffer while copying token chars. setting *buf_size=-1");
          return false;
        }
        *(s2++) = *(s1++);
      }
      /* 's1' points to some whitespace char in the token in the input
       * string, to the ',', or just beyond the end of the input string */
      /* Skip all whitespaces */
      while ( ((size_t) (s1 - str) < str_len) &&
              ((' ' == *s1) || ('\t' == *s1)) )
        s1++;

      /* 's1' points to the first non-whitespace char in the input string
       * after whitespace chars, to the ',', or just beyond the end of
       * the input string */
      if (((size_t) (s1 - str) < str_len) && (',' != *s1))
      { /* Not the end of the current token */
        mhd_assert (s2 >= buf);
        if ((size_t) *buf_size <= (size_t) (s2 - buf)) /* '<= s2' equals '< s2 + 1' */
        { /* Not enough space in the output buffer */
          *buf_size = (ssize_t) -1;
          mhd_probe_log ("EXIT MHD_str_remove_token_caseless_: insufficient buffer while inserting space. setting *buf_size=-1");
          return false;
        }
        *(s2++) = ' ';
      }
    }
  }
  mhd_assert (((ssize_t) (s2 - buf)) <= *buf_size);
  *buf_size = (ssize_t) (s2 - buf);
  mhd_probe_log ("EXIT MHD_str_remove_token_caseless_: returning %d final_buf_size=%lld",
                 token_removed, (long long) *buf_size);
  return token_removed;
}
