#include <stdio.h>
#include <stdint.h>
#include <inttypes.h>
#include <stddef.h>

/* Minimal replacement for the project's assert hook so this test code compiles. */
void mhd_assert (int cond)
{
  /* Keep same semantics: do nothing in release; a real project may abort on false. */
  (void) cond;
}

/* Helper probe: log state to /tmp/logs/1687.txt. */
static void
mhd_log_uint16_state (const char *tag,
                      const uint16_t *val_ptr,
                      uint16_t divisor,
                      int digit,
                      const char *buf_ptr,
                      size_t buf_size)
{
  FILE *f = fopen("/tmp/logs/1687.txt", "a");
  if (NULL == f)
  {
    return; /* best-effort logging only */
  }

  /* Print a compact record with control location and key state. */
  (void) fprintf (f, "[%s] val_ptr=%p val=%" PRIu16 " divisor=%" PRIu16 " digit=%d buf=%p buf_size=%zu\n",
                  tag,
                  (void *) val_ptr,
                  (uint16_t) *val_ptr,
                  (uint16_t) divisor,
                  digit,
                  (const void *) buf_ptr,
                  buf_size);
  (void) fclose (f);
}

size_t
MHD_uint16_to_str (uint16_t val,
                   char *buf,
                   size_t buf_size)
{
  char *chr;  /**< pointer to the current printed digit */
  /* The biggest printable number is 65535 */
  uint16_t divisor = UINT16_C (10000);
  int digit;

  /* Log entry state */
  mhd_log_uint16_state("entry", &val, divisor, -1, buf, buf_size);

  chr = buf;
  digit = (int) (val / divisor);
  mhd_assert (digit < 10);

  /* Do not print leading zeros */
  while ((0 == digit) && (1 < divisor))
  {
    divisor /= 10;
    digit = (int) (val / divisor);
    mhd_assert (digit < 10);
  }

  while (0 != buf_size)
  {
    *chr = (char) ((char) digit + '0');
    chr++;
    buf_size--;
    if (1 == divisor)
    {
      /* Log just before returning (completed) */
      mhd_log_uint16_state("return_complete", &val, divisor, digit, buf, buf_size);
      return (size_t) (chr - buf);
    }

    /* Log before the operation that modifies the function parameter 'val' */
    mhd_log_uint16_state("before_mod", &val, divisor, digit, buf, buf_size);

    /* Violation point: modifies function parameter 'val' */
    val = (uint16_t) (val % divisor);

    /* Log immediately after modification to provide evidence of the change */
    mhd_log_uint16_state("after_mod", &val, divisor, digit, buf, buf_size);

    divisor /= 10;
    digit = (int) (val / divisor);
    mhd_assert (digit < 10);
  }

  /* Log buffer-too-small case */
  mhd_log_uint16_state("return_overflow", &val, divisor, digit, buf, buf_size);
  return 0; /* The buffer is too small */
}
