static void probe_log_state(const char *stage, const char *header, const char *content)
{
  FILE *fp = fopen("/tmp/logs/2179.txt", "a");
  if (fp == NULL)
  {
    /* If logging cannot be performed, silently continue to preserve original behavior. */
    return;
  }

  /* Log control point and pointer values. Use %p to avoid dereferencing pointers. */
  (void)fprintf(fp, "%s: header=%p, content=%p\n", stage, (const void *)header, (const void *)content);
  (void)fflush(fp);
  (void)fclose(fp);
}

static enum MHD_Result
add_response_entry (struct MHD_Response *response,
                    enum MHD_ValueKind kind,
                    const char *header,
                    const char *content)
{
  size_t header_len;
  size_t content_len;

  /* Log entry and pointer state before any checks. This captures the scenario where header may be NULL. */
  probe_log_state("enter", header, content);

  if (NULL == content)
  {
    probe_log_state("return_content_null", header, content);
    return MHD_NO;
  }

  /* Log immediately before calling strlen(header) so callers reproducing the issue can see the pointer state. */
  probe_log_state("before_strlen", header, content);

  header_len = strlen (header);
  content_len = strlen (content);

  probe_log_state("before_add_response_entry_n", header, content);
  return add_response_entry_n (response, kind, header,
                               header_len, content,
                               content_len) ? MHD_YES : MHD_NO;
}
