static void mhd_log_probe(const char *phase,
                              const char *value,
                              size_t value_len,
                              size_t old_value_len,
                              size_t norm_len,
                              size_t buf_size,
                              int flags_auto,
                              bool value_has_close,
                              bool already_has_close,
                              size_t pos,
                              const char *extra)
{
  FILE *f = fopen("/tmp/logs/2192.txt", "a");
  if (NULL == f)
    return;
  fprintf(f, "[add_response_header_connection] phase=%s\n", phase);
  if (NULL != value)
    fprintf(f, "  value=\"%s\", value_len=%zu\n", value, value_len);
  else
    fprintf(f, "  value=(null), value_len=%zu\n", value_len);
  fprintf(f, "  old_value_len=%zu, norm_len=%zu, buf_size=%zu\n",
          old_value_len, norm_len, buf_size);
  fprintf(f, "  flags_auto=0x%08x, value_has_close=%d, already_has_close=%d, pos=%zu\n",
          flags_auto, (int) value_has_close, (int) already_has_close, pos);
  if (NULL != extra)
    fprintf(f, "  extra=%s\n", extra);
  fprintf(f, "\n");
  fclose(f);
}

static enum MHD_Result
add_response_header_connection (struct MHD_Response *response,
                                const char *value)
{
  static const char *key = MHD_HTTP_HEADER_CONNECTION;
  /** the length of the "Connection" key */
  static const size_t key_len =
    MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONNECTION);
  size_t value_len; /**< the length of the @a value */
  size_t old_value_len; /**< the length of the existing "Connection" value */
  size_t buf_size;  /**< the size of the buffer */
  size_t norm_len;  /**< the length of the normalised value */
  char *buf;        /**< the temporal buffer */
  struct MHD_HTTP_Res_Header *hdr; /**< existing "Connection" header */
  bool value_has_close; /**< the @a value has "close" token */
  bool already_has_close; /**< existing "Connection" header has "close" token */
  size_t pos = 0;   /**< position of addition in the @a buf */

  /* Log entry with the provided value */
  mhd_log_probe("entry", value, (value ? strlen(value) : 0),
                0, 0, 0,
                (int) (response ? response->flags_auto : 0),
                false, false, 0, NULL);

  if ( (NULL != strchr (value, '\r')) ||
       (NULL != strchr (value, '\n')) )
    return MHD_NO;

  if (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                       key, key_len);
    already_has_close =
      (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
    mhd_assert (already_has_close == (0 == memcmp (hdr->value, "close", 5)));
    mhd_assert (NULL != hdr);
  }
  else
  {
    hdr = NULL;
    already_has_close = false;
    mhd_assert (NULL == MHD_get_response_element_n_ (response,
                                                     MHD_HEADER_KIND,
                                                     key, key_len));
    mhd_assert (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
  }

  /* Log after header lookup */
  mhd_log_probe("after_hdr_lookup", value, (value ? strlen(value) : 0),
                0, 0, 0,
                (int) response->flags_auto,
                false, already_has_close, 0,
                (hdr ? "hdr_present" : "hdr_null"));

  if (NULL != hdr)
    old_value_len = hdr->value_size + 2; /* additional size for ", " */
  else
    old_value_len = 0;

  value_len = strlen (value);
  if (value_len >= SSIZE_MAX)
    return MHD_NO;
  /* Additional space for normalisation and zero-termination */
  norm_len = value_len + value_len / 2 + 1;
  if (norm_len >= SSIZE_MAX)
    return MHD_NO;
  buf_size = old_value_len + (size_t) norm_len;

  buf = malloc (buf_size);
  if (NULL == buf)
    return MHD_NO;

  /* Log before the local-scope block that uses the constant 'if (1)' */
  mhd_log_probe("before_const_if", value, value_len, old_value_len,
                norm_len, buf_size, (int) response->flags_auto,
                false, already_has_close, pos,
                "About to evaluate constant 'if (1)' block (always true)");

  if (1)
  { /* local scope */
    ssize_t norm_len_s = (ssize_t) norm_len;
    /* Remove "close" token (if any), it will be moved to the front */
    /* Log that the constant condition evaluated to true and we enter the scope */
    mhd_log_probe("const_if_enter", value, value_len, old_value_len,
                  norm_len, buf_size, (int) response->flags_auto,
                  false, already_has_close, pos,
                  "Entered constant-true local scope (if (1))");

    value_has_close = MHD_str_remove_token_caseless_ (value, value_len, "close",
                                                      MHD_STATICSTR_LEN_ ( \
                                                        "close"),
                                                      buf + old_value_len,
                                                      &norm_len_s);
    mhd_assert (0 <= norm_len_s);
    /* Log the result of token removal */
    mhd_log_probe("after_remove_close_token", value, value_len, old_value_len,
                  (size_t) (norm_len_s >= 0 ? (size_t) norm_len_s : 0), buf_size,
                  (int) response->flags_auto,
                  value_has_close, already_has_close, pos,
                  "Result of MHD_str_remove_token_caseless_");

    if (0 > norm_len_s)
    {
      /* Must never happen with realistic sizes */
      free (buf);
      return MHD_NO;
    }
    else
      norm_len = (size_t) norm_len_s;
  }
#ifdef UPGRADE_SUPPORT
  if ( (NULL != response->upgrade_handler) && value_has_close)
  { /* The "close" token cannot be used with connection "upgrade" */
    /* Log the upgrade_handler conflict */
    mhd_log_probe("upgrade_conflict", value, value_len, old_value_len,
                  norm_len, buf_size, (int) response->flags_auto,
                  value_has_close, already_has_close, pos,
                  "upgrade_handler present and value_has_close true -> reject");
    free (buf);
    return MHD_NO;
  }
#endif /* UPGRADE_SUPPORT */
  if (0 != norm_len)
    MHD_str_remove_tokens_caseless_ (buf + old_value_len, &norm_len,
                                     "keep-alive",
                                     MHD_STATICSTR_LEN_ ("keep-alive"));

  /* Log after normalization and keep-alive removal */
  mhd_log_probe("after_normalisation", value, value_len, old_value_len,
                norm_len, buf_size, (int) response->flags_auto,
                value_has_close, already_has_close, pos, NULL);

  if (0 == norm_len)
  { /* New value is empty after normalisation */
    if (! value_has_close)
    { /* The new value had no tokens */
      free (buf);
      /* Log rejection due to empty normalised value and no close token */
      mhd_log_probe("reject_empty_no_close", value, value_len, old_value_len,
                    norm_len, buf_size, (int) response->flags_auto,
                    value_has_close, already_has_close, pos,
                    "New value empty and no close token -> return MHD_NO");
      return MHD_NO;
    }
    if (already_has_close)
    { /* The "close" token is already present, nothing to modify */
      free (buf);
      /* Log no modification required */
      mhd_log_probe("no_mod_needed", value, value_len, old_value_len,
                    norm_len, buf_size, (int) response->flags_auto,
                    value_has_close, already_has_close, pos,
                    "Already has close token -> return MHD_YES");
      return MHD_YES;
    }
  }
  /* Add "close" token if required */
  if (value_has_close && ! already_has_close)
  {
    /* Need to insert "close" token at the first position */
    mhd_assert (buf_size >= old_value_len + norm_len   \
                + MHD_STATICSTR_LEN_ ("close, ") + 1);
    if (0 != norm_len)
      memmove (buf + MHD_STATICSTR_LEN_ ("close, ") + old_value_len,
               buf + old_value_len, norm_len + 1);
    memcpy (buf, "close", MHD_STATICSTR_LEN_ ("close"));
    pos += MHD_STATICSTR_LEN_ ("close");

    /* Log that we added the close token at front */
    mhd_log_probe("added_close_front", value, value_len, old_value_len,
                  norm_len, buf_size, (int) response->flags_auto,
                  value_has_close, already_has_close, pos,
                  "Inserted 'close' token at front");
  }
  /* Add old value tokens (if any) */
  if (0 != old_value_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    memcpy (buf + pos, hdr->value,
            hdr->value_size);
    pos += hdr->value_size;

    /* Log addition of old header tokens */
    mhd_log_probe("added_old_tokens", value, value_len, old_value_len,
                  norm_len, buf_size, (int) response->flags_auto,
                  value_has_close, already_has_close, pos,
                  "Copied old header tokens into buf");
  }
  /* Add new value token (if any) */
  if (0 != norm_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    /* The new value tokens must be already at the correct position */
    mhd_assert ((value_has_close && ! already_has_close) ? \
                (MHD_STATICSTR_LEN_ ("close, ") + old_value_len == pos) : \
                (old_value_len == pos));
    pos += norm_len;

    /* Log addition of new normalized tokens */
    mhd_log_probe("added_new_tokens", value, value_len, old_value_len,
                  norm_len, buf_size, (int) response->flags_auto,
                  value_has_close, already_has_close, pos,
                  "Appended normalized new tokens");
  }
  mhd_assert (buf_size > pos);
  buf[pos] = 0; /* Null terminate the result */

  if (NULL == hdr)
  {
    struct MHD_HTTP_Res_Header *new_hdr; /**< new "Connection" header */
    /* Create new response header entry */
    new_hdr = MHD_calloc_ (1, sizeof (struct MHD_HTTP_Res_Header));
    if (NULL != new_hdr)
    {
      new_hdr->header = malloc (key_len + 1);
      if (NULL != new_hdr->header)
      {
        memcpy (new_hdr->header, key, key_len + 1);
        new_hdr->header_size = key_len;
        new_hdr->value = buf;
        new_hdr->value_size = pos;
        new_hdr->kind = MHD_HEADER_KIND;
        if (value_has_close)
          response->flags_auto = (MHD_RAF_HAS_CONNECTION_HDR
                                  | MHD_RAF_HAS_CONNECTION_CLOSE);
        else
          response->flags_auto = MHD_RAF_HAS_CONNECTION_HDR;
        _MHD_insert_header_first (response, new_hdr);

        /* Log successful insertion of new header */
        mhd_log_probe("insert_new_header", value, value_len, old_value_len,
                      norm_len, buf_size, (int) response->flags_auto,
                      value_has_close, already_has_close, pos,
                      "Inserted new Connection header");

        return MHD_YES;
      }
      free (new_hdr);
    }
    free (buf);
    /* Log failure to allocate new header entries */
    mhd_log_probe("alloc_failed_newhdr", value, value_len, old_value_len,
                  norm_len, buf_size, (int) response->flags_auto,
                  value_has_close, already_has_close, pos,
                  "Failed to allocate new header structures -> MHD_NO");
    return MHD_NO;
  }

  /* Update existing header entry */
  free (hdr->value);
  hdr->value = buf;
  hdr->value_size = pos;
  if (value_has_close && ! already_has_close)
    response->flags_auto |= MHD_RAF_HAS_CONNECTION_CLOSE;

  /* Log update of existing header and final state */
  mhd_log_probe("updated_existing_header", value, value_len, old_value_len,
                norm_len, buf_size, (int) response->flags_auto,
                value_has_close, already_has_close, pos,
                "Updated existing Connection header");
  return MHD_YES;
}
