static void mhd_log_ptr(const char *label, const void *p)
{
  FILE *f = fopen("/tmp/logs/2199.txt", "a");
  if (f)
  {
    fprintf(f, "%s: %p\n", label, p);
    fclose(f);
  }
}

static void mhd_log_msg(const char *label)
{
  FILE *f = fopen("/tmp/logs/2199.txt", "a");
  if (f)
  {
    fprintf(f, "%s\n", label);
    fclose(f);
  }
}

static void mhd_log_sizes(const char *label, size_t a, size_t b)
{
  FILE *f = fopen("/tmp/logs/2199.txt", "a");
  if (f)
  {
    fprintf(f, "%s: %zu, %zu\n", label, a, b);
    fclose(f);
  }
}

static enum MHD_Result
add_response_header_connection (struct MHD_Response *response,
                                const char *value)
{
  static const char *key = MHD_HTTP_HEADER_CONNECTION;
  /** the length of the "Connection" key */
  static const size_t key_len =
    MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONNECTION);
  size_t value_len; /**< the length of the @a value */
  size_t old_value_len; /**< the length of the existing "Connection" value */
  size_t buf_size;  /**< the size of the buffer */
  size_t norm_len;  /**< the length of the normalised value */
  char *buf;        /**< the temporal buffer */
  struct MHD_HTTP_Res_Header *hdr; /**< existing "Connection" header */
  bool value_has_close; /**< the @a value has "close" token */
  bool already_has_close; /**< existing "Connection" header has "close" token */
  size_t pos = 0;   /**< position of addition in the @a buf */

  /* Logging probes: record entry and pointer state. We intentionally do not
     dereference 'value' here so the logs can be produced even when value is
     NULL or not NUL-terminated. This preserves the original control flow
     (including the original risky strchr/strlen calls) while providing
     evidence for reproducing the violation scenario. */
  mhd_log_msg("add_response_header_connection: enter");
  mhd_log_ptr("response", (const void *) response);
  mhd_log_ptr("value (pointer)", (const void *) value);

  /* Preserve original logic: call strchr exactly as the original code did,
     but capture the results in local variables so we can log them. */
  const char *p_cr = strchr (value, '\r');
  const char *p_nl = strchr (value, '\n');
  mhd_log_ptr("strchr(value,'\\r') returned", (const void *) p_cr);
  mhd_log_ptr("strchr(value,'\\n') returned", (const void *) p_nl);

  if ( (NULL != p_cr) || (NULL != p_nl) )
  {
    mhd_log_msg("add_response_header_connection: found CR or NL in value -> returning MHD_NO");
    return MHD_NO;
  }

  if (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                       key, key_len);
    already_has_close =
      (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
    mhd_assert (already_has_close == (0 == memcmp (hdr->value, "close", 5)));
    mhd_assert (NULL != hdr);
    mhd_log_ptr("existing hdr", (const void *) hdr);
    mhd_log_ptr("existing hdr->value", (const void *) (hdr ? hdr->value : NULL));
  }
  else
  {
    hdr = NULL;
    already_has_close = false;
    mhd_assert (NULL == MHD_get_response_element_n_ (response,
                                                     MHD_HEADER_KIND,
                                                     key, key_len));
    mhd_assert (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
    mhd_log_msg("no existing Connection header");
  }
  if (NULL != hdr)
    old_value_len = hdr->value_size + 2; /* additional size for ", " */
  else
    old_value_len = 0;

  mhd_log_sizes("old_value_len and key_len", old_value_len, key_len);

  /* The original code calls strlen(value) without checking value for NULL or
     NUL-termination. We log that we are about to call strlen to capture the
     control flow and the pointer value; the call itself remains unchanged to
     preserve original behaviour for reproduction. */
  mhd_log_msg("about to call strlen(value)");
  value_len = strlen (value);
  mhd_log_sizes("value_len and SSIZE_MAX check", value_len, (size_t) SSIZE_MAX);

  if (value_len >= SSIZE_MAX)
  {
    mhd_log_msg("value_len >= SSIZE_MAX -> returning MHD_NO");
    return MHD_NO;
  }
  /* Additional space for normalisation and zero-termination */
  norm_len = value_len + value_len / 2 + 1;
  if (norm_len >= SSIZE_MAX)
  {
    mhd_log_msg("norm_len >= SSIZE_MAX -> returning MHD_NO");
    return MHD_NO;
  }
  buf_size = old_value_len + (size_t) norm_len;
  mhd_log_sizes("buf_size and norm_len", buf_size, norm_len);

  buf = malloc (buf_size);
  mhd_log_ptr("malloc(buf) returned", (const void *) buf);
  if (NULL == buf)
  {
    mhd_log_msg("malloc failed -> returning MHD_NO");
    return MHD_NO;
  }
  if (1)
  { /* local scope */
    ssize_t norm_len_s = (ssize_t) norm_len;
    /* Remove "close" token (if any), it will be moved to the front */
    value_has_close = MHD_str_remove_token_caseless_ (value, value_len, "close",
                                                      MHD_STATICSTR_LEN_ ( \
                                                        "close"),
                                                      buf + old_value_len,
                                                      &norm_len_s);
    mhd_assert (0 <= norm_len_s);
    if (0 > norm_len_s)
    {
      /* Must never happen with realistic sizes */
      mhd_log_msg("norm_len_s < 0 after MHD_str_remove_token_caseless_ -> freeing buf and returning MHD_NO");
      free (buf);
      return MHD_NO;
    }
    else
      norm_len = (size_t) norm_len_s;
  }
#ifdef UPGRADE_SUPPORT
  if ( (NULL != response->upgrade_handler) && value_has_close)
  { /* The "close" token cannot be used with connection "upgrade" */
    mhd_log_msg("upgrade_handler present and value_has_close -> freeing buf and returning MHD_NO");
    free (buf);
    return MHD_NO;
  }
#endif /* UPGRADE_SUPPORT */
  if (0 != norm_len)
    MHD_str_remove_tokens_caseless_ (buf + old_value_len, &norm_len,
                                     "keep-alive",
                                     MHD_STATICSTR_LEN_ ("keep-alive"));
  if (0 == norm_len)
  { /* New value is empty after normalisation */
    if (! value_has_close)
    { /* The new value had no tokens */
      mhd_log_msg("norm_len == 0 and !value_has_close -> freeing buf and returning MHD_NO");
      free (buf);
      return MHD_NO;
    }
    if (already_has_close)
    { /* The "close" token is already present, nothing to modify */
      mhd_log_msg("already_has_close -> freeing buf and returning MHD_YES");
      free (buf);
      return MHD_YES;
    }
  }
  /* Add "close" token if required */
  if (value_has_close && ! already_has_close)
  {
    /* Need to insert "close" token at the first position */
    mhd_assert (buf_size >= old_value_len + norm_len   \
                + MHD_STATICSTR_LEN_ ("close, ") + 1);
    if (0 != norm_len)
      memmove (buf + MHD_STATICSTR_LEN_ ("close, ") + old_value_len,
               buf + old_value_len, norm_len + 1);
    memcpy (buf, "close", MHD_STATICSTR_LEN_ ("close"));
    pos += MHD_STATICSTR_LEN_ ("close");
    mhd_log_msg("inserted close token");
  }
  /* Add old value tokens (if any) */
  if (0 != old_value_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    memcpy (buf + pos, hdr->value,
            hdr->value_size);
    pos += hdr->value_size;
    mhd_log_sizes("added old value, new pos and hdr->value_size", pos, hdr->value_size);
  }
  /* Add new value token (if any) */
  if (0 != norm_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    /* The new value tokens must be already at the correct position */
    mhd_assert ((value_has_close && ! already_has_close) ? \
                (MHD_STATICSTR_LEN_ ("close, ") + old_value_len == pos) : \
                (old_value_len == pos));
    pos += norm_len;
    mhd_log_sizes("after adding new tokens pos and norm_len", pos, norm_len);
  }
  mhd_assert (buf_size > pos);
  buf[pos] = 0; /* Null terminate the result */
  mhd_log_ptr("final buf (pointer)", (const void *) buf);
  mhd_log_sizes("final pos and buf_size", pos, buf_size);

  if (NULL == hdr)
  {
    struct MHD_HTTP_Res_Header *new_hdr; /**< new "Connection" header */
    /* Create new response header entry */
    new_hdr = MHD_calloc_ (1, sizeof (struct MHD_HTTP_Res_Header));
    if (NULL != new_hdr)
    {
      new_hdr->header = malloc (key_len + 1);
      if (NULL != new_hdr->header)
      {
        memcpy (new_hdr->header, key, key_len + 1);
        new_hdr->header_size = key_len;
        new_hdr->value = buf;
        new_hdr->value_size = pos;
        new_hdr->kind = MHD_HEADER_KIND;
        if (value_has_close)
          response->flags_auto = (MHD_RAF_HAS_CONNECTION_HDR
                                  | MHD_RAF_HAS_CONNECTION_CLOSE);
        else
          response->flags_auto = MHD_RAF_HAS_CONNECTION_HDR;
        _MHD_insert_header_first (response, new_hdr);
        mhd_log_msg("inserted new header and returning MHD_YES");
        return MHD_YES;
      }
      free (new_hdr);
    }
    free (buf);
    mhd_log_msg("failed to allocate new header -> returning MHD_NO");
    return MHD_NO;
  }

  /* Update existing header entry */
  mhd_log_ptr("updating existing hdr->value (old)", (const void *) hdr->value);
  free (hdr->value);
  hdr->value = buf;
  hdr->value_size = pos;
  if (value_has_close && ! already_has_close)
    response->flags_auto |= MHD_RAF_HAS_CONNECTION_CLOSE;
  mhd_log_msg("updated existing header -> returning MHD_YES");
  return MHD_YES;
}
