#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <stdlib.h>

/* Probe logging helpers. Writes logs to /tmp/logs/2199.txt */
static void probe_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/2199.txt", "a");
  if (!f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  fprintf(f, "\n");
  va_end(ap);
  fclose(f);
}

static void probe_log_hex_bytes_limited(const char *label, const char *buf, size_t buf_size, size_t max)
{
  FILE *f = fopen("/tmp/logs/2199.txt", "a");
  if (!f)
    return;
  fprintf(f, "%s: ptr=%p size=%zu show_max=%zu bytes=\"", label, (const void *)buf, buf_size, max);
  if (NULL == buf || 0 == buf_size)
  {
    fprintf(f, "\"");
    fprintf(f, "\n");
    fclose(f);
    return;
  }
  size_t n = buf_size < max ? buf_size : max;
  for (size_t i = 0; i < n; ++i)
  {
    char c = buf[i];
    /* print printable characters, else hex */
    if (c >= 32 && c <= 126)
      fputc(c, f);
    else
      fprintf(f, "\\x%02x", (unsigned char)c);
  }
  fprintf(f, "\"");
  fprintf(f, "\n");
  fclose(f);
}

static enum MHD_Result
add_response_header_connection (struct MHD_Response *response,
                                const char *value)
{
  static const char *key = MHD_HTTP_HEADER_CONNECTION;
  /** the length of the "Connection" key */
  static const size_t key_len =
    MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONNECTION);
  size_t value_len; /**< the length of the @a value */
  size_t old_value_len; /**< the length of the existing "Connection" value */
  size_t buf_size;  /**< the size of the buffer */
  size_t norm_len;  /**< the length of the normalised value */
  char *buf;        /**< the temporal buffer */
  struct MHD_HTTP_Res_Header *hdr; /**< existing "Connection" header */
  bool value_has_close; /**< the @a value has "close" token */
  bool already_has_close; /**< existing "Connection" header has "close" token */
  size_t pos = 0;   /**< position of addition in the @a buf */

  /* Log entry */
  probe_log("enter add_response_header_connection: response=%p value=%p", (void *)response, (const void *)value);

  if ( (NULL != strchr (value, '\r')) ||
       (NULL != strchr (value, '\n')) )
  {
    probe_log("rejecting value containing CR/LF: value_ptr=%p", (const void *)value);
    return MHD_NO;
  }

  if (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                       key, key_len);

    /* Log the header retrieval result before the memcmp/assert */
    if (hdr == NULL)
    {
      probe_log("flags_auto indicates existing Connection header but MHD_get_response_element_n_ returned NULL: response=%p flags_auto=0x%08x", (void *)response, (unsigned int)response->flags_auto);
    }
    else
    {
      probe_log("found existing header hdr=%p hdr->value=%p hdr->value_size=%zu", (void *)hdr, (const void *)hdr->value, (size_t)hdr->value_size);
      /* Log up to first few bytes of hdr->value but never read beyond hdr->value_size */
      probe_log_hex_bytes_limited("hdr->value_preview", hdr->value, hdr->value_size, 16);
    }

    already_has_close =
      (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));

    /* Log intended memcmp usage (5 bytes) and whether hdr->value_size appears large enough */
    if (hdr != NULL)
    {
      probe_log("about to memcmp hdr->value with \"close\" (5 bytes). hdr->value_size=%zu sufficient=%s", hdr->value_size, (hdr->value_size >= 5) ? "yes" : "no");
    }
    else
    {
      probe_log("about to memcmp but hdr is NULL - this will reproduce the unsafe behavior if assertions do not prevent it");
    }

    /* Preserve original memcmp-based assertion logic, but capture the result for logging. */
    {
      int cmp_res = 0;
      if (hdr != NULL)
        cmp_res = memcmp (hdr->value, "close", 5);
      else
        /* Calling memcmp with hdr == NULL would be undefined; however the original code
           also assumes hdr is non-NULL here (assert). We keep the behavior: avoid calling
           memcmp when hdr is NULL but log that fact so reproducer agents can see the
           problematic state that would trigger UB in the original code. */
        cmp_res = 1; /* non-zero to indicate "not equal" in our logged view */

      probe_log("memcmp result (hdr->value vs \"close\",5) = %d (interpreted equal=%s)", cmp_res, (cmp_res == 0) ? "yes" : "no");

      mhd_assert (already_has_close == (0 == cmp_res));
    }

    mhd_assert (NULL != hdr);
  }
  else
  {
    hdr = NULL;
    already_has_close = false;
    mhd_assert (NULL == MHD_get_response_element_n_ (response,
                                                     MHD_HEADER_KIND,
                                                     key, key_len));
    mhd_assert (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
  }

  if (NULL != hdr)
    old_value_len = hdr->value_size + 2; /* additional size for ", " */
  else
    old_value_len = 0;

  value_len = strlen (value);
  if (value_len >= SSIZE_MAX)
  {
    probe_log("value_len >= SSIZE_MAX: value_len=%zu", value_len);
    return MHD_NO;
  }
  /* Additional space for normalisation and zero-termination */
  norm_len = value_len + value_len / 2 + 1;
  if (norm_len >= SSIZE_MAX)
  {
    probe_log("norm_len >= SSIZE_MAX: norm_len=%zu value_len=%zu", norm_len, value_len);
    return MHD_NO;
  }
  buf_size = old_value_len + (size_t) norm_len;

  buf = malloc (buf_size);
  if (NULL == buf)
  {
    probe_log("malloc failed buf_size=%zu", buf_size);
    return MHD_NO;
  }
  probe_log("allocated buf=%p buf_size=%zu old_value_len=%zu", (void *)buf, buf_size, old_value_len);

  if (1)
  { /* local scope */
    ssize_t norm_len_s = (ssize_t) norm_len;
    /* Remove "close" token (if any), it will be moved to the front */
    value_has_close = MHD_str_remove_token_caseless_ (value, value_len, "close",
                                                      MHD_STATICSTR_LEN_ ( \
                                                        "close"),
                                                      buf + old_value_len,
                                                      &norm_len_s);
    mhd_assert (0 <= norm_len_s);
    if (0 > norm_len_s)
    {
      /* Must never happen with realistic sizes */
      probe_log("unexpected negative norm_len_s=%zd", norm_len_s);
      free (buf);
      return MHD_NO;
    }
    else
      norm_len = (size_t) norm_len_s;
  }
#ifdef UPGRADE_SUPPORT
  if ( (NULL != response->upgrade_handler) && value_has_close)
  { /* The "close" token cannot be used with connection "upgrade" */
    probe_log("upgrade_handler present and value_has_close true: response=%p", (void *)response);
    free (buf);
    return MHD_NO;
  }
#endif /* UPGRADE_SUPPORT */
  if (0 != norm_len)
    MHD_str_remove_tokens_caseless_ (buf + old_value_len, &norm_len,
                                     "keep-alive",
                                     MHD_STATICSTR_LEN_ ("keep-alive"));
  if (0 == norm_len)
  { /* New value is empty after normalisation */
    if (! value_has_close)
    { /* The new value had no tokens */
      probe_log("normalized value empty and no close token: rejecting");
      free (buf);
      return MHD_NO;
    }
    if (already_has_close)
    { /* The "close" token is already present, nothing to modify */
      probe_log("already_has_close true and normalized value empty: nothing to do");
      free (buf);
      return MHD_YES;
    }
  }
  /* Add "close" token if required */
  if (value_has_close && ! already_has_close)
  {
    /* Need to insert "close" token at the first position */
    mhd_assert (buf_size >= old_value_len + norm_len   \
                + MHD_STATICSTR_LEN_ ("close, ") + 1);
    if (0 != norm_len)
      memmove (buf + MHD_STATICSTR_LEN_ ("close, ") + old_value_len,
               buf + old_value_len, norm_len + 1);
    memcpy (buf, "close", MHD_STATICSTR_LEN_ ("close"));
    pos += MHD_STATICSTR_LEN_ ("close");
  }
  /* Add old value tokens (if any) */
  if (0 != old_value_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    memcpy (buf + pos, hdr->value,
            hdr->value_size);
    pos += hdr->value_size;
  }
  /* Add new value token (if any) */
  if (0 != norm_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    /* The new value tokens must be already at the correct position */
    mhd_assert ((value_has_close && ! already_has_close) ? \
                (MHD_STATICSTR_LEN_ ("close, ") + old_value_len == pos) : \
                (old_value_len == pos));
    pos += norm_len;
  }
  mhd_assert (buf_size > pos);
  buf[pos] = 0; /* Null terminate the result */

  if (NULL == hdr)
  {
    struct MHD_HTTP_Res_Header *new_hdr; /**< new "Connection" header */
    /* Create new response header entry */
    new_hdr = MHD_calloc_ (1, sizeof (struct MHD_HTTP_Res_Header));
    if (NULL != new_hdr)
    {
      new_hdr->header = malloc (key_len + 1);
      if (NULL != new_hdr->header)
      {
        memcpy (new_hdr->header, key, key_len + 1);
        new_hdr->header_size = key_len;
        new_hdr->value = buf;
        new_hdr->value_size = pos;
        new_hdr->kind = MHD_HEADER_KIND;
        if (value_has_close)
          response->flags_auto = (MHD_RAF_HAS_CONNECTION_HDR
                                  | MHD_RAF_HAS_CONNECTION_CLOSE);
        else
          response->flags_auto = MHD_RAF_HAS_CONNECTION_HDR;
        _MHD_insert_header_first (response, new_hdr);
        probe_log("inserted new header: new_hdr=%p header=%p value=%p value_size=%zu response->flags_auto=0x%08x",
                  (void *)new_hdr, (void *)new_hdr->header, (void *)new_hdr->value, (size_t)new_hdr->value_size, (unsigned int)response->flags_auto);
        return MHD_YES;
      }
      free (new_hdr);
    }
    probe_log("failed to allocate new header entry");
    free (buf);
    return MHD_NO;
  }

  /* Update existing header entry */
  probe_log("updating existing header hdr=%p old_value=%p old_value_size=%zu -> new_value=%p new_value_size=%zu",
            (void *)hdr, (void *)hdr->value, (size_t)hdr->value_size, (void *)buf, pos);
  free (hdr->value);
  hdr->value = buf;
  hdr->value_size = pos;
  if (value_has_close && ! already_has_close)
    response->flags_auto |= MHD_RAF_HAS_CONNECTION_CLOSE;
  probe_log("update complete hdr=%p value=%p value_size=%zu response->flags_auto=0x%08x", (void *)hdr, (void *)hdr->value, (size_t)hdr->value_size, (unsigned int)response->flags_auto);
  return MHD_YES;
}
