#include <stdio.h>
#include <stdbool.h>
#include <stddef.h>
#include <string.h>

/* Probe logging helper - appends control-flow and state information to
   /tmp/logs/217.txt. Keeps logic unchanged; only records state. */
static void
mhd_log_state(const char *event,
              struct MHD_Response *r,
              bool filter_transf_enc,
              bool filter_content_len,
              bool add_close,
              bool add_keep_alive,
              size_t ppos)
{
  FILE *f = fopen("/tmp/logs/217.txt", "a");
  if (!f)
    return;
  /* We print pointer values and several flags; keep formatting simple. */
  fprintf(f, "%s: %s | r=%p | flags_auto=0x%lx | flags=0x%lx | filter_transf_enc=%d | filter_content_len=%d | add_close=%d | add_keep_alive=%d | ppos=%zu\n",
          __func__, event,
          (void *)r,
          (unsigned long)(r ? r->flags_auto : 0UL),
          (unsigned long)(r ? r->flags : 0UL),
          filter_transf_enc ? 1 : 0,
          filter_content_len ? 1 : 0,
          add_close ? 1 : 0,
          add_keep_alive ? 1 : 0,
          ppos);
  fclose(f);
}

static bool
add_user_headers (char *buf,
                  size_t *ppos,
                  size_t buf_size,
                  struct MHD_Response *response,
                  bool filter_transf_enc,
                  bool filter_content_len,
                  bool add_close,
                  bool add_keep_alive)
{
  struct MHD_Response *const r = response; /**< a short alias */
  struct MHD_HTTP_Res_Header *hdr; /**< Iterates through User-specified headers */
  size_t el_size; /**< the size of current element to be added to the @a buf */

  /* Log entry state */
  mhd_log_state("entry", r, filter_transf_enc, filter_content_len, add_close, add_keep_alive, *ppos);

  mhd_assert (! add_close || ! add_keep_alive);

  if (0 == (r->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED)) {
    mhd_log_state("before_clear_has_trans_enc", r, filter_transf_enc, filter_content_len, add_close, add_keep_alive, *ppos);
    filter_transf_enc = false;   /* No such header */
    mhd_log_state("after_clear_has_trans_enc", r, filter_transf_enc, filter_content_len, add_close, add_keep_alive, *ppos);
  }
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH)) {
    mhd_log_state("before_clear_has_content_len", r, filter_transf_enc, filter_content_len, add_close, add_keep_alive, *ppos);
    filter_content_len = false;  /* No such header */
    mhd_log_state("after_clear_has_content_len", r, filter_transf_enc, filter_content_len, add_close, add_keep_alive, *ppos);
  }
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    mhd_log_state("before_clear_has_connection_hdr", r, filter_transf_enc, filter_content_len, add_close, add_keep_alive, *ppos);
    add_close = false;          /* No such header */
    add_keep_alive = false;     /* No such header */
    mhd_log_state("after_clear_has_connection_hdr", r, filter_transf_enc, filter_content_len, add_close, add_keep_alive, *ppos);
  }
  else if (0 != (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE)) {
    mhd_log_state("before_clear_has_connection_close", r, filter_transf_enc, filter_content_len, add_close, add_keep_alive, *ppos);
    add_close = false;          /* "close" token was already set */
    mhd_log_state("after_clear_has_connection_close", r, filter_transf_enc, filter_content_len, add_close, add_keep_alive, *ppos);
  }

  for (hdr = r->first_header; NULL != hdr; hdr = hdr->next)
  {
    size_t initial_pos = *ppos;
    if (MHD_HEADER_KIND != hdr->kind)
      continue;

    if (filter_transf_enc)
    { /* Need to filter-out "Transfer-Encoding" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_TRANSFER_ENCODING) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_TRANSFER_ENCODING,
                                          hdr->header, hdr->header_size)) )
      {
        /* Log before modifying filter_transf_enc */
        mhd_log_state("matched_transfer_encoding_before_clear", r, filter_transf_enc, filter_content_len, add_close, add_keep_alive, *ppos);
        filter_transf_enc = false; /* There is the only one such header */
        mhd_log_state("matched_transfer_encoding_after_clear", r, filter_transf_enc, filter_content_len, add_close, add_keep_alive, *ppos);
        continue; /* Skip "Transfer-Encoding" header */
      }
    }
    if (filter_content_len)
    { /* Need to filter-out "Content-Length" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONTENT_LENGTH) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_CONTENT_LENGTH,
                                          hdr->header, hdr->header_size)) )
      {
        /* Log before modifying related filter flag(s) */
        mhd_log_state("matched_content_length_before_adjust", r, filter_transf_enc, filter_content_len, add_close, add_keep_alive, *ppos);

        /* Reset filter flag if only one header is allowed */
        /* Note: this assigns to filter_transf_enc based on r->flags. */
        filter_transf_enc =
          (0 == (r->flags & MHD_RF_INSANITY_HEADER_CONTENT_LENGTH));

        mhd_log_state("matched_content_length_after_adjust", r, filter_transf_enc, filter_content_len, add_close, add_keep_alive, *ppos);
        continue; /* Skip "Content-Length" header */
      }
    }

    /* Add user header */
    el_size = hdr->header_size + 2 + hdr->value_size + 2;
    if (buf_size < *ppos + el_size)
      return false;
    memcpy (buf + *ppos, hdr->header, hdr->header_size);
    (*ppos) += hdr->header_size;
    buf[(*ppos)++] = ':';
    buf[(*ppos)++] = ' ';
    if (add_close || add_keep_alive)
    {
      /* "Connection:" header must be always the first one */
      mhd_assert (MHD_str_equal_caseless_n_ (hdr->header, \
                                             MHD_HTTP_HEADER_CONNECTION, \
                                             hdr->header_size));

      if (add_close)
      {
        el_size += MHD_STATICSTR_LEN_ ("close, ");
        if (buf_size < initial_pos + el_size)
          return false;
        memcpy (buf + *ppos, "close, ",
                MHD_STATICSTR_LEN_ ("close, "));
        *ppos += MHD_STATICSTR_LEN_ ("close, ");

        /* Log insertion of close token */
        mhd_log_state("inserted_close_token", r, filter_transf_enc, filter_content_len, add_close, add_keep_alive, *ppos);
      }
      else
      {
        el_size += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
        if (buf_size < initial_pos + el_size)
          return false;
        memcpy (buf + *ppos, "Keep-Alive, ",
                MHD_STATICSTR_LEN_ ("Keep-Alive, "));
        *ppos += MHD_STATICSTR_LEN_ ("Keep-Alive, ");

        /* Log insertion of Keep-Alive token */
        mhd_log_state("inserted_keep_alive_token", r, filter_transf_enc, filter_content_len, add_close, add_keep_alive, *ppos);
      }
      add_close = false;
      add_keep_alive = false;
      mhd_log_state("after_clearing_add_close_keep_alive", r, filter_transf_enc, filter_content_len, add_close, add_keep_alive, *ppos);
    }
    if (0 != hdr->value_size)
      memcpy (buf + *ppos, hdr->value, hdr->value_size);
    *ppos += hdr->value_size;
    buf[(*ppos)++] = '\r';
    buf[(*ppos)++] = '\n';
    mhd_assert (initial_pos + el_size == (*ppos));

    /* Log after adding each header */
    mhd_log_state("after_adding_header", r, filter_transf_enc, filter_content_len, add_close, add_keep_alive, *ppos);
  }

  /* Log exit state */
  mhd_log_state("exit", r, filter_transf_enc, filter_content_len, add_close, add_keep_alive, *ppos);

  return true;
}
