#include <stdio.h>
#include <stdarg.h>
#include <time.h>

static void
probe_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/217.txt", "a");
  if (f == NULL)
    return;
  time_t t = time(NULL);
  struct tm tm_buf;
  (void)localtime_r(&t, &tm_buf);
  char timestr[64];
  (void)strftime(timestr, sizeof(timestr), "%Y-%m-%d %H:%M:%S", &tm_buf);
  fprintf(f, "%s: ", timestr);
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fprintf(f, "\n");
  fclose(f);
}

static bool
add_user_headers (char *buf,
                  size_t *ppos,
                  size_t buf_size,
                  struct MHD_Response *response,
                  bool filter_transf_enc,
                  bool filter_content_len,
                  bool add_close,
                  bool add_keep_alive)
{
  struct MHD_Response *const r = response; /**< a short alias */
  struct MHD_HTTP_Res_Header *hdr; /**< Iterates through User-specified headers */
  size_t el_size; /**< the size of current element to be added to the @a buf */

  probe_log("enter add_user_headers: add_close=%d add_keep_alive=%d filter_transf_enc=%d filter_content_len=%d r=%p flags_auto=0x%X flags=0x%X first_header=%p *ppos=%zu buf_size=%zu",
            add_close, add_keep_alive, filter_transf_enc, filter_content_len,
            (void *)r, (unsigned)r->flags_auto, (unsigned)r->flags, (void *)r->first_header, *ppos, buf_size);

  mhd_assert (! add_close || ! add_keep_alive);

  if (0 == (r->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED))
    filter_transf_enc = false;   /* No such header */
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH))
    filter_content_len = false;  /* No such header */
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    probe_log("no connection header present: setting add_close=false, add_keep_alive=false (was %d,%d)", add_close, add_keep_alive);
    add_close = false;          /* No such header */
    add_keep_alive = false;     /* No such header */
  }
  else if (0 != (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE))
  {
    probe_log("connection-close token already set: setting add_close=false (was %d)", add_close);
    add_close = false;          /* "close" token was already set */
  }

  for (hdr = r->first_header; NULL != hdr; hdr = hdr->next)
  {
    size_t initial_pos = *ppos;
    if (MHD_HEADER_KIND != hdr->kind)
      continue;
    if (filter_transf_enc)
    { /* Need to filter-out "Transfer-Encoding" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_TRANSFER_ENCODING) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_TRANSFER_ENCODING,
                                          hdr->header, hdr->header_size)) )
      {
        filter_transf_enc = false; /* There is the only one such header */
        probe_log("skipping Transfer-Encoding user header at pos=%zu", initial_pos);
        continue; /* Skip "Transfer-Encoding" header */
      }
    }
    if (filter_content_len)
    { /* Need to filter-out "Content-Length" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONTENT_LENGTH) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_CONTENT_LENGTH,
                                          hdr->header, hdr->header_size)) )
      {
        /* Reset filter flag if only one header is allowed */
        filter_transf_enc =
          (0 == (r->flags & MHD_RF_INSANITY_HEADER_CONTENT_LENGTH));
        probe_log("skipping Content-Length user header at pos=%zu, next filter_transf_enc=%d", initial_pos, filter_transf_enc);
        continue; /* Skip "Content-Length" header */
      }
    }

    /* Add user header */
    el_size = hdr->header_size + 2 + hdr->value_size + 2;
    if (buf_size < *ppos + el_size)
    {
      probe_log("buffer overflow risk: buf_size=%zu *ppos=%zu el_size=%zu -> returning false", buf_size, *ppos, el_size);
      return false;
    }
    memcpy (buf + *ppos, hdr->header, hdr->header_size);
    (*ppos) += hdr->header_size;
    buf[(*ppos)++] = ':';
    buf[(*ppos)++] = ' ';
    if (add_close || add_keep_alive)
    {
      /* "Connection:" header must be always the first one */
      mhd_assert (MHD_str_equal_caseless_n_ (hdr->header, \
                                             MHD_HTTP_HEADER_CONNECTION, \
                                             hdr->header_size));

      probe_log("connection header encountered at pos=%zu: add_close=%d add_keep_alive=%d initial_pos=%zu hdr->header_size=%zu hdr->value_size=%zu",
                *ppos, add_close, add_keep_alive, initial_pos, hdr->header_size, hdr->value_size);

      if (add_close)
      {
        el_size += MHD_STATICSTR_LEN_ ("close, ");
        if (buf_size < initial_pos + el_size)
        {
          probe_log("buffer overflow when adding 'close' token: buf_size=%zu initial_pos=%zu el_size=%zu -> returning false",
                    buf_size, initial_pos, el_size);
          return false;
        }
        probe_log("adding 'close, ' token at pos=%zu", *ppos);
        memcpy (buf + *ppos, "close, ",
                MHD_STATICSTR_LEN_ ("close, "));
        *ppos += MHD_STATICSTR_LEN_ ("close, ");
      }
      else
      {
        el_size += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
        if (buf_size < initial_pos + el_size)
        {
          probe_log("buffer overflow when adding 'Keep-Alive' token: buf_size=%zu initial_pos=%zu el_size=%zu -> returning false",
                    buf_size, initial_pos, el_size);
          return false;
        }
        probe_log("adding 'Keep-Alive, ' token at pos=%zu", *ppos);
        memcpy (buf + *ppos, "Keep-Alive, ",
                MHD_STATICSTR_LEN_ ("Keep-Alive, "));
        *ppos += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
      }
      probe_log("resetting add_close and add_keep_alive (was %d,%d) after writing connection tokens", add_close, add_keep_alive);
      add_close = false;
      add_keep_alive = false;
    }
    if (0 != hdr->value_size)
      memcpy (buf + *ppos, hdr->value, hdr->value_size);
    *ppos += hdr->value_size;
    buf[(*ppos)++] = '\r';
    buf[(*ppos)++] = '\n';
    mhd_assert (initial_pos + el_size == (*ppos));
    probe_log("wrote user header, new *ppos=%zu expected=%zu", *ppos, initial_pos + el_size);
  }
  probe_log("exit add_user_headers: returning true final *ppos=%zu", *ppos);
  return true;
}
