  (void) upload_data;       /* Unused. Silent compiler warning. */
  (void) upload_data_size;  /* Unused. Silent compiler warning. */

  if (0 != strcmp (method, "GET"))
    return MHD_NO; /* unexpected method */
  if (! *req_cls)
  {
    *req_cls = (void *) 1;
    return MHD_YES;
  }
  *req_cls = NULL;

  body_str = strdup (PAGE);
  if (NULL == body_str)
  {
    return MHD_NO;
  }
  body_len = strlen (body_str);
  /* try to compress the body */
  comp = MHD_NO;
  if (MHD_YES ==
      can_compress (connection))
    comp = body_compress ((void **) &body_str,
                          &body_len);
  response =
    MHD_create_response_from_buffer_with_free_callback (body_len,
                                                        body_str,
                                                        &free);

  if (NULL == response)
  {
    free (body_str);
    return MHD_NO;
  }

  if (MHD_YES == comp)
  {
    /* Need to indicate to client that body is compressed */
    if (MHD_NO ==
        MHD_add_response_header (response,
                                 MHD_HTTP_HEADER_CONTENT_ENCODING,
                                 "deflate"))
    {
      MHD_destroy_response (response);
      return MHD_NO;
    }
  }
  ret = MHD_queue_response (connection,
                            200,
                            response);
  MHD_destroy_response (response);
  return ret;
}
