#include <stdio.h>
#include <stdint.h>
#include <stdbool.h>

/* Probe logging helpers. These functions intentionally use const-qualified
 * parameters so they do not suggest mutation and to avoid introducing the
 * pointer-to-non-const issue in the probes themselves. They append human-
 * readable diagnostic lines to /tmp/logs/243.txt to allow other agents to
 * reproduce and gather evidence for the reported scenario. */
static void
probe_log_state (const struct MHD_Connection *const c,
                 const struct MHD_Response *const r,
                 const char *stage,
                 const char *filename)
{
  FILE *f = fopen (filename, "a");
  if (NULL == f)
    return;

  fprintf (f, "--- %s ---\n", stage);
  fprintf (f, "connection ptr: %p\n", (const void *) c);
  fprintf (f, "response ptr:   %p\n", (const void *) r);
  fprintf (f, "keepalive: %u\n", (unsigned) c->keepalive);
  fprintf (f, "rq.http_ver: %u\n", (unsigned) c->rq.http_ver);
  fprintf (f, "daemon->options: %u\n", (unsigned) c->daemon->options);
  fprintf (f, "rp.props.set: %u\n", (unsigned) c->rp.props.set);
  fprintf (f, "rp.props.chunked: %u\n", (unsigned) c->rp.props.chunked);
  fprintf (f, "rp.props.use_reply_body_headers: %u\n",
           (unsigned) c->rp.props.use_reply_body_headers);

  /* Response-specific fields used in the decision tree */
  fprintf (f, "r->flags: 0x%08x\n", (unsigned) r->flags);
  fprintf (f, "r->flags_auto: 0x%08x\n", (unsigned) r->flags_auto);
  fprintf (f, "r->total_size: %llu\n", (unsigned long long) r->total_size);
#ifdef UPGRADE_SUPPORT
  fprintf (f, "r->upgrade_handler: %p\n", (const void *) r->upgrade_handler);
#endif
  fprintf (f, "\n");
  fclose (f);
}

static void
probe_log_add_user_headers_call (const char *filename,
                                 char *buf,
                                 size_t pos,
                                 size_t buf_size,
                                 const struct MHD_Response *const r,
                                 bool send_headers_for_body,
                                 bool need_content_length_header,
                                 bool use_conn_close,
                                 bool use_conn_k_alive)
{
  FILE *f = fopen (filename, "a");
  if (NULL == f)
    return;

  fprintf (f, "Calling add_user_headers\n");
  fprintf (f, "  buf: %p\n", (void *) buf);
  fprintf (f, "  pos: %zu\n", pos);
  fprintf (f, "  buf_size: %zu\n", buf_size);
  fprintf (f, "  r: %p\n", (const void *) r);
  fprintf (f, "  send_headers_for_body: %u\n", (unsigned) send_headers_for_body);
  fprintf (f, "  need_content_length_header: %u\n", (unsigned) need_content_length_header);
  fprintf (f, "  use_conn_close: %u\n", (unsigned) use_conn_close);
  fprintf (f, "  use_conn_k_alive: %u\n", (unsigned) use_conn_k_alive);
  fprintf (f, "\n");
  fclose (f);
}

static enum MHD_Result
build_header_response (struct MHD_Connection *connection)
{
  struct MHD_Connection *const c = connection; /**< a short alias */
  struct MHD_Response *const r = c->rp.response; /**< a short alias */
  char *buf;                                     /**< the output buffer */
  size_t pos;                                    /**< append offset in the @a buf */
  size_t buf_size;                               /**< the size of the @a buf */
  size_t el_size;                                /**< the size of current element to be added to the @a buf */
  unsigned rcode;                                /**< the response code */
  bool use_conn_close;                           /**< Use "Connection: close" header */
  bool use_conn_k_alive;                         /**< Use "Connection: Keep-Alive" header */

  /* Log entry into the function with initial state */
  probe_log_state (c, r, "enter build_header_response", "/tmp/logs/243.txt");

  mhd_assert (NULL != r);

  /* ** Adjust response properties ** */
  setup_reply_properties (c);

  mhd_assert (c->rp.props.set);
  mhd_assert ((MHD_CONN_MUST_CLOSE == c->keepalive) || \
              (MHD_CONN_USE_KEEPALIVE == c->keepalive) || \
              (MHD_CONN_MUST_UPGRADE == c->keepalive));
#ifdef UPGRADE_SUPPORT
  mhd_assert ((NULL == r->upgrade_handler) || \
              (MHD_CONN_MUST_UPGRADE == c->keepalive));
#else  /* ! UPGRADE_SUPPORT */
  mhd_assert (MHD_CONN_MUST_UPGRADE != c->keepalive);
#endif /* ! UPGRADE_SUPPORT */
  mhd_assert ((! c->rp.props.chunked) || c->rp.props.use_reply_body_headers);
  mhd_assert ((! c->rp.props.send_reply_body) || \
              c->rp.props.use_reply_body_headers);
#ifdef UPGRADE_SUPPORT
  mhd_assert (NULL == r->upgrade_handler || \
              ! c->rp.props.use_reply_body_headers);
#endif /* UPGRADE_SUPPORT */

  check_connection_reply (c);

  rcode = (unsigned) c->rp.responseCode;
  if (MHD_CONN_MUST_CLOSE == c->keepalive)
  {
    /* The closure of connection must be always indicated by header
     * to avoid hung connections */
    use_conn_close = true;
    use_conn_k_alive = false;
  }
  else if (MHD_CONN_USE_KEEPALIVE == c->keepalive)
  {
    use_conn_close = false;
    /* Add "Connection: keep-alive" if request is HTTP/1.0 or
     * if reply is HTTP/1.0
     * For HTTP/1.1 add header only if explicitly requested by app
     * (by response flag), as "Keep-Alive" is default for HTTP/1.1. */
    if ((0 != (r->flags & MHD_RF_SEND_KEEP_ALIVE_HEADER)) ||
        (MHD_HTTP_VER_1_0 == c->rq.http_ver) ||
        (0 != (r->flags & MHD_RF_HTTP_1_0_SERVER)))
      use_conn_k_alive = true;
    else
      use_conn_k_alive = false;
  }
  else
  {
    use_conn_close = false;
    use_conn_k_alive = false;
  }

  /* ** Actually build the response header ** */

  /* Get all space available */
  connection_maximize_write_buffer (c);
  buf = c->write_buffer;
  pos = c->write_buffer_append_offset;
  buf_size = c->write_buffer_size;
  if (0 == buf_size)
    return MHD_NO;
  mhd_assert (NULL != buf);

  /* * The status line * */

  /* The HTTP version */
  if (! c->rp.responseIcy)
  { /* HTTP reply */
    if (0 == (r->flags & MHD_RF_HTTP_1_0_SERVER))
    { /* HTTP/1.1 reply */
      /* Use HTTP/1.1 responses for HTTP/1.0 clients.
       * See https://datatracker.ietf.org/doc/html/rfc7230#section-2.6 */
      if (! buffer_append_s (buf, &pos, buf_size, MHD_HTTP_VERSION_1_1))
        return MHD_NO;
    }
    else
    { /* HTTP/1.0 reply */
      if (! buffer_append_s (buf, &pos, buf_size, MHD_HTTP_VERSION_1_0))
        return MHD_NO;
    }
  }
  else
  { /* ICY reply */
    if (! buffer_append_s (buf, &pos, buf_size, "ICY"))
      return MHD_NO;
  }

  /* The response code */
  if (buf_size < pos + 5) /* space + code + space */
    return MHD_NO;
  buf[pos++] = ' ';
  pos += MHD_uint16_to_str ((uint16_t) rcode, buf + pos,
                            buf_size - pos);
  buf[pos++] = ' ';

  /* The reason phrase */
  el_size = MHD_get_reason_phrase_len_for (rcode);
  if (0 == el_size)
  {
    if (! buffer_append_s (buf, &pos, buf_size, "Non-Standard Status"))
      return MHD_NO;
  }
  else if (! buffer_append (buf, &pos, buf_size,
                            MHD_get_reason_phrase_for (rcode),
                            el_size))
    return MHD_NO;

  /* The linefeed */
  if (buf_size < pos + 2)
    return MHD_NO;
  buf[pos++] = '\r';
  buf[pos++] = '\n';

  /* * The headers * */

  /* Main automatic headers */

  /* The "Date:" header */
  if ( (0 == (r->flags_auto & MHD_RAF_HAS_DATE_HDR)) &&
       (0 == (c->daemon->options & MHD_USE_SUPPRESS_DATE_NO_CLOCK)) )
  {
    /* Additional byte for unused zero-termination */
    if (buf_size < pos + 38)
      return MHD_NO;
    if (get_date_header (buf + pos))
      pos += 37;
  }
  /* The "Connection:" header */
  mhd_assert (! use_conn_close || ! use_conn_k_alive);
  mhd_assert (! use_conn_k_alive || ! use_conn_close);
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    if (use_conn_close)
    {
      if (! buffer_append_s (buf, &pos, buf_size,
                             MHD_HTTP_HEADER_CONNECTION ": close\r\n"))
        return MHD_NO;
    }
    else if (use_conn_k_alive)
    {
      if (! buffer_append_s (buf, &pos, buf_size,
                             MHD_HTTP_HEADER_CONNECTION ": Keep-Alive\r\n"))
        return MHD_NO;
    }
  }

  /* User-defined headers */

  /* Compute the boolean arguments for add_user_headers explicitly so we can
   * log them. Keeping the exact expressions preserves program logic. */
  bool arg_send_headers_for_body = ! c->rp.props.chunked;
  bool arg_need_content_length_header = (! c->rp.props.use_reply_body_headers) &&
                                        (0 == (r->flags & MHD_RF_INSANITY_HEADER_CONTENT_LENGTH));

  /* Log state immediately before calling add_user_headers to capture the
   * fact that 'r' (a struct MHD_Response *const) is being passed to the
   * function. This provides runtime evidence of the call and the relevant
   * state that supports the violation scenario analysis. */
  probe_log_state (c, r, "before add_user_headers", "/tmp/logs/243.txt");
  probe_log_add_user_headers_call ("/tmp/logs/243.txt",
                                  buf,
                                  pos,
                                  buf_size,
                                  r,
                                  arg_send_headers_for_body,
                                  arg_need_content_length_header,
                                  use_conn_close,
                                  use_conn_k_alive);

  if (! add_user_headers (buf, &pos, buf_size, r,
                          arg_send_headers_for_body,
                          arg_need_content_length_header,
                          use_conn_close,
                          use_conn_k_alive))
  {
    /* Log failure of add_user_headers to aid reproduction */
    FILE *f = fopen ("/tmp/logs/243.txt", "a");
    if (NULL != f)
    {
      fprintf (f, "add_user_headers returned failure. pos=%zu, buf_size=%zu\n\n",
               pos, buf_size);
      fclose (f);
    }
    return MHD_NO;
  }

  /* Log after successful add_user_headers call */
  probe_log_state (c, r, "after add_user_headers", "/tmp/logs/243.txt");

  /* Other automatic headers */

  if ( (c->rp.props.use_reply_body_headers) &&
       (0 == (r->flags & MHD_RF_HEAD_ONLY_RESPONSE)) )
  {
    /* Body-specific headers */

    if (c->rp.props.chunked)
    { /* Chunked encoding is used */
      if (0 == (r->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED))
      { /* No chunked encoding header set by user */
        if (! buffer_append_s (buf, &pos, buf_size,
                               MHD_HTTP_HEADER_TRANSFER_ENCODING ": " \
                               "chunked\r\n"))
          return MHD_NO;
      }
    }
    else /* Chunked encoding is not used */
    {
      if (MHD_SIZE_UNKNOWN != r->total_size)
      { /* The size is known */
        if (0 == (r->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH))
        { /* The response does not have "Content-Length" header */
          if (! buffer_append_s (buf, &pos, buf_size,
                                 MHD_HTTP_HEADER_CONTENT_LENGTH ": "))
            return MHD_NO;
          el_size = MHD_uint64_to_str (r->total_size, buf + pos,
                                       buf_size - pos);
          if (0 == el_size)
            return MHD_NO;
          pos += el_size;

          if (buf_size < pos + 2)
            return MHD_NO;
          buf[pos++] = '\r';
          buf[pos++] = '\n';
        }
      }
    }
  }

  /* * Header termination * */
  if (buf_size < pos + 2)
    return MHD_NO;
  buf[pos++] = '\r';
  buf[pos++] = '\n';

  c->write_buffer_append_offset = pos;

  /* Log exit state */
  probe_log_state (c, r, "exit build_header_response", "/tmp/logs/243.txt");

  return MHD_YES;
}
