#include <stdio.h>
#include <stdarg.h>
#include <inttypes.h>
#include <stdint.h>
#include <stddef.h>
#include <string.h>

/* Probe logging helper: append textual diagnostics to /tmp/logs/2649.txt */
static void
mhd_probe_log (const char *fmt, ...)
{
  FILE *fp = fopen ("/tmp/logs/2649.txt", "a");
  if (fp == NULL)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (fp, fmt, ap);
  va_end (ap);
  fputc ('\n', fp);
  fclose (fp);
}

static bool
calculate_add_nonce_with_retry (struct MHD_Connection *const connection,
                                const char *realm,
                                struct DigestAlgorithm *da,
                                char *nonce)
{
  const uint64_t timestamp1 = MHD_monotonic_msec_counter ();
  const size_t realm_len = strlen (realm);
  mhd_assert (! da->hashing);

  /* Log entry information */
  mhd_probe_log ("enter calculate_add_nonce_with_retry: connection=%p realm=%s realm_len=%zu timestamp1=%" PRIu64,
                 (void *) connection, realm ? realm : "(null)", realm_len, timestamp1);

#ifdef HAVE_MESSAGES
  if (0 == MHD_get_master (connection->daemon)->digest_auth_rand_size)
    MHD_DLOG (connection->daemon,
              _ ("Random value was not initialised by " \
                 "MHD_OPTION_DIGEST_AUTH_RANDOM or " \
                 "MHD_OPTION_DIGEST_AUTH_RANDOM_COPY, generated nonces " \
                 "are predictable.\n"));
#endif

  if (! calculate_add_nonce (connection, timestamp1, realm, realm_len, da,
                             nonce))
  {
    mhd_probe_log ("calculate_add_nonce: first attempt failed for timestamp1=%" PRIu64, timestamp1);

    const size_t digest_size = digest_get_size (da);
    /* record the actual sizes involved in the potential copy */
    char nonce2[NONCE_STD_LEN (MAX_DIGEST) + 1];
    const size_t nonce2_buf_len = sizeof (nonce2);
    const size_t requested_copy_len = NONCE_STD_LEN (digest_size);

    mhd_probe_log ("digest_size=%zu nonce2_buf_len=%zu requested_copy_len=%zu",
                   digest_size, nonce2_buf_len, requested_copy_len);

#ifdef MHD_DIGEST_HAS_EXT_ERROR
    if (digest_ext_error (da))
    {
      mhd_probe_log ("digest_ext_error returned true: aborting retry");
      return false; /* No need to re-try */
    }
#endif /* MHD_DIGEST_HAS_EXT_ERROR */
    if (0 == MHD_get_master (connection->daemon)->nonce_nc_size)
    {
      mhd_probe_log ("nonce_nc_size == 0: aborting retry");
      return false; /* No need to re-try */
    }

    uint64_t timestamp2;

    timestamp2 = MHD_monotonic_msec_counter ();
    mhd_probe_log ("timestamp2 initial=%" PRIu64, timestamp2);

    if (timestamp1 == timestamp2)
    {
      mhd_probe_log ("timestamps equal: timestamp1==timestamp2==%" PRIu64, timestamp1);
      /* The timestamps are equal, need to generate some arbitrary
       * difference for nonce. */
      /* As the number is needed only to differentiate clients, weak
       * pseudo-random generators could be used. Seeding is not needed. */
      uint64_t base1;
      uint32_t base2;
      uint16_t base3;
      uint8_t base4;
#ifdef HAVE_RANDOM
      base1 = ((uint64_t) random ()) ^ UINT64_C (0x54a5acff5be47e63);
      base4 = 0xb8;
#elif defined(HAVE_RAND)
      base1 = ((uint64_t) rand ()) ^ UINT64_C (0xc4bcf553b12f3965);
      base4 = 0x92;
#else
      /* Monotonic msec counter alone does not really help here as it is already
         known that this value is not unique. */
      base1 = ((uint64_t) (uintptr_t) nonce2) ^ UINT64_C (0xf2e1b21bc6c92655);
      base2 = ((uint32_t) (base1 >> 32)) ^ ((uint32_t) base1);
      base2 = _MHD_ROTR32 (base2, 4);
      base3 = ((uint16_t) (base2 >> 16)) ^ ((uint16_t) base2);
      base4 = ((uint8_t) (base3 >> 8)) ^ ((uint8_t) base3);
      base1 = ((uint64_t) MHD_monotonic_msec_counter ())
              ^ UINT64_C (0xccab93f72cf5b15);
#endif
      base2 = ((uint32_t) (base1 >> 32)) ^ ((uint32_t) base1);
      base2 = _MHD_ROTL32 (base2, (((base4 >> 4) ^ base4) % 32));
      base3 = ((uint16_t) (base2 >> 16)) ^ ((uint16_t) base2);
      base4 = ((uint8_t) (base3 >> 8)) ^ ((uint8_t) base3);
      /* Use up to 127 ms difference */
      timestamp2 -= (base4 & DAUTH_JUMPBACK_MAX);
      mhd_probe_log ("timestamp2 adjusted by base4=%u -> timestamp2=%" PRIu64, (unsigned) base4, timestamp2);
      if (timestamp1 == timestamp2)
      {
        timestamp2 -= 2; /* Fallback value */
        mhd_probe_log ("timestamp2 equal after adjustment, fallback timestamp2=%" PRIu64, timestamp2);
      }
    }
    digest_reset (da);
    if (! calculate_add_nonce (connection, timestamp2, realm, realm_len, da,
                               nonce2))
    {
      mhd_probe_log ("calculate_add_nonce: second attempt failed for timestamp2=%" PRIu64, timestamp2);
      /* No free slot has been found. Re-tries are expensive, just use
       * the generated nonce. As it is not stored in nonce-nc map array,
       * the next request of the client will be recognized as valid, but 'stale'
       * so client should re-try automatically. */
      return false;
    }

    /* Before copying, log buffer addresses and lengths to help reproduce
     * potential out-of-bounds usage. */
    mhd_probe_log ("about to memcpy: dest_nonce=%p src_nonce2=%p requested_copy_len=%zu nonce2_buf_len=%zu",
                   (void *) nonce, (void *) nonce2, requested_copy_len, nonce2_buf_len);
    if (requested_copy_len > nonce2_buf_len)
    {
      mhd_probe_log ("WARNING: requested_copy_len > nonce2_buf_len (possible OOB read): %zu > %zu",
                     requested_copy_len, nonce2_buf_len);
    }

    memcpy (nonce, nonce2, NONCE_STD_LEN (digest_size));
    mhd_probe_log ("memcpy completed: copied %zu bytes", NONCE_STD_LEN (digest_size));
  }
  mhd_probe_log ("exit calculate_add_nonce_with_retry: returning true");
  return true;
}
