#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>

/* Probe logging helper: append formatted messages to /tmp/logs/2655.txt */
static void
probe_log (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/2655.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fprintf (f, "\n");
  fclose (f);
}

static void
probe_log_initial_state (uint64_t nonce_time,
                         enum MHD_HTTP_Method mthd_e,
                         const char *method,
                         const char *rnd,
                         size_t rnd_size,
                         const struct sockaddr_storage *saddr,
                         size_t saddr_size,
                         const char *uri,
                         size_t uri_len,
                         const struct MHD_HTTP_Req_Header *first_header,
                         const char *realm,
                         size_t realm_len,
                         unsigned int bind_options,
                         struct DigestAlgorithm *da)
{
  /* Log the entry and key parameters to allow reproducing control flow */
  probe_log ("calculate_nonce entry: nonce_time=%llu, mthd_e=%d, method_ptr=%p, rnd_size=%zu, saddr_size=%zu, uri_len=%zu, realm_len=%zu, bind_options=0x%08x, da_ptr=%p",
             (unsigned long long) nonce_time,
             (int) mthd_e,
             (const void *) method,
             rnd_size,
             saddr_size,
             uri_len,
             realm_len,
             (unsigned int) bind_options,
             (void *) da);
  if (NULL != da)
  {
    /* Attempt to log da->hashing if available (as in original code used by mhd_assert) */
    probe_log ("da->hashing (as int read) = %d", (int) da->hashing);
    /* If digest_get_size is available in the build, log its result; keep call read-only */
    probe_log ("digest_get_size(da) = %d", (int) digest_get_size (da));
  }
}

static void
probe_log_branch (const char *msg)
{
  probe_log ("branch: %s", msg);
}

static void
probe_log_timestamp_info (uint64_t nonce_time, struct DigestAlgorithm *da)
{
  probe_log ("timestamp block executed (if (1)): nonce_time=%llu, digest_size=%d", (unsigned long long) nonce_time, (int) digest_get_size (da));
}

static void
probe_log_hash_info_pre (struct DigestAlgorithm *da)
{
  probe_log ("hash calculation block entered (if (1)): da_ptr=%p, digest_size=%d", (void *) da, (int) digest_get_size (da));
}

static void
probe_log_hash_info_post (uint8_t *hash, int digest_size)
{
  /* Log first 8 bytes of the binary hash for evidence (if available) */
  int i, lim = digest_size < 8 ? digest_size : 8;
  char buf[3 * 8 + 1];
  char *p = buf;
  for (i = 0; i < lim; ++i)
    p += sprintf (p, "%02x", (unsigned) hash[i]);
  *p = '\0';
  probe_log ("hash computed: first_bytes_hex=%s, digest_size=%d", buf, digest_size);
}

static void
calculate_nonce (uint64_t nonce_time,
                 enum MHD_HTTP_Method mthd_e,
                 const char *method,
                 const char *rnd,
                 size_t rnd_size,
                 const struct sockaddr_storage *saddr,
                 size_t saddr_size,
                 const char *uri,
                 size_t uri_len,
                 const struct MHD_HTTP_Req_Header *first_header,
                 const char *realm,
                 size_t realm_len,
                 unsigned int bind_options,
                 struct DigestAlgorithm *da,
                 char *nonce)
{
  /* Log initial state to reproduce control flow */
  probe_log_initial_state (nonce_time, mthd_e, method, rnd, rnd_size, saddr, saddr_size, uri, uri_len, first_header, realm, realm_len, bind_options, da);

  mhd_assert (! da->hashing);
  if (1)
  {
    probe_log_timestamp_info (nonce_time, da);
    /* Add the timestamp to the hash calculation */
    uint8_t timestamp[TIMESTAMP_BIN_SIZE];
    /* If the nonce_time is milliseconds, then the same 48 bit value will repeat
     * every 8 919 years, which is more than enough to mitigate a replay attack */
#if TIMESTAMP_BIN_SIZE != 6
#error The code needs to be updated here
#endif
    timestamp[0] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 0)));
    timestamp[1] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 1)));
    timestamp[2] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 2)));
    timestamp[3] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 3)));
    timestamp[4] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 4)));
    timestamp[5] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 5)));
    MHD_bin_to_hex (timestamp,
                    sizeof (timestamp),
                    nonce + digest_get_size (da) * 2);
    digest_update (da,
                   timestamp,
                   sizeof (timestamp));
  }
  if (rnd_size > 0)
  {
    probe_log_branch ("rnd_size > 0 taken");
    /* Add the unique random value to the hash calculation */
    digest_update_with_colon (da);
    digest_update (da,
                   rnd,
                   rnd_size);
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) &&
       (0 != saddr_size) )
  {
    probe_log_branch ("bind_options == MHD_DAUTH_BIND_NONCE_NONE && saddr_size != 0 taken");
    /* Add full client address including source port to make unique nonces
     * for requests received exactly at the same time */
    digest_update_with_colon (da);
    digest_update (da,
                   saddr,
                   saddr_size);
  }
  if ( (0 != (bind_options & MHD_DAUTH_BIND_NONCE_CLIENT_IP)) &&
       (0 != saddr_size) )
  {
    probe_log_branch ("bind_options & MHD_DAUTH_BIND_NONCE_CLIENT_IP taken");
    /* Add the client's IP address to the hash calculation */
    digest_update_with_colon (da);
    if (AF_INET == saddr->ss_family)
      digest_update (da,
                     &((const struct sockaddr_in *) saddr)->sin_addr,
                     sizeof(((const struct sockaddr_in *) saddr)->sin_addr));
#ifdef HAVE_INET6
    else if (AF_INET6 == saddr->ss_family)
      digest_update (da,
                     &((const struct sockaddr_in6 *) saddr)->sin6_addr,
                     sizeof(((const struct sockaddr_in6 *) saddr)->sin6_addr));
#endif /* HAVE_INET6 */
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) ||
       (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI)))
  {
    probe_log_branch ("include method in hash (bind_options condition) taken");
    /* Add the request method to the hash calculation */
    digest_update_with_colon (da);
    if (MHD_HTTP_MTHD_OTHER != mthd_e)
    {
      uint8_t mthd_for_hash;
      if (MHD_HTTP_MTHD_HEAD != mthd_e)
        mthd_for_hash = (uint8_t) mthd_e;
      else /* Treat HEAD method in the same way as GET method */
        mthd_for_hash = (uint8_t) MHD_HTTP_MTHD_GET;
      digest_update (da,
                     &mthd_for_hash,
                     sizeof(mthd_for_hash));
    }
    else
      digest_update_str (da, method);
  }

  if (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI))
  {
    probe_log_branch ("bind_options & MHD_DAUTH_BIND_NONCE_URI taken");
    /* Add the request URI to the hash calculation */
    digest_update_with_colon (da);

    digest_update (da,
                   uri,
                   uri_len);
  }
  if (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI_PARAMS))
  {
    probe_log_branch ("bind_options & MHD_DAUTH_BIND_NONCE_URI_PARAMS taken");
    /* Add the request URI parameters to the hash calculation */
    const struct MHD_HTTP_Req_Header *h;

    digest_update_with_colon (da);
    for (h = first_header; NULL != h; h = h->next)
    {
      if (MHD_GET_ARGUMENT_KIND != h->kind)
        continue;
      digest_update (da, "\0", 2);
      if (0 != h->header_size)
        digest_update (da, h->header, h->header_size);
      digest_update (da, "", 1);
      if (0 != h->value_size)
        digest_update (da, h->value, h->value_size);
    }
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) ||
       (0 != (bind_options & MHD_DAUTH_BIND_NONCE_REALM)))
  {
    probe_log_branch ("include realm in hash (bind_options condition) taken");
    /* Add the realm to the hash calculation */
    digest_update_with_colon (da);
    digest_update (da,
                   realm,
                   realm_len);
  }
  if (1)
  {
    probe_log_hash_info_pre (da);
    uint8_t hash[MAX_DIGEST];
    digest_calc_hash (da, hash);
    probe_log_hash_info_post (hash, digest_get_size (da));
    MHD_bin_to_hex (hash,
                    digest_get_size (da),
                    nonce);
  }
}
