#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <stddef.h>

/* Probe logging helper - appends logs to /tmp/logs/2655.txt */
static void
probe_log (const char *fmt, ...)
{
  FILE *f = fopen ("/tmp/logs/2655.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fputc ('\n', f);
  fclose (f);
}

static void
probe_log_hex (const char *label, const uint8_t *data, size_t len)
{
  FILE *f = fopen ("/tmp/logs/2655.txt", "a");
  size_t i;

  if (NULL == f)
    return;
  fprintf (f, "%s:", label);
  for (i = 0; i < len; ++i)
    fprintf (f, " %02x", (unsigned) data[i]);
  fprintf (f, "\n");
  fclose (f);
}

static void
calculate_nonce (uint64_t nonce_time,
                 enum MHD_HTTP_Method mthd_e,
                 const char *method,
                 const char *rnd,
                 size_t rnd_size,
                 const struct sockaddr_storage *saddr,
                 size_t saddr_size,
                 const char *uri,
                 size_t uri_len,
                 const struct MHD_HTTP_Req_Header *first_header,
                 const char *realm,
                 size_t realm_len,
                 unsigned int bind_options,
                 struct DigestAlgorithm *da,
                 char *nonce)
{
  mhd_assert (! da->hashing);

  probe_log ("calculate_nonce: entry: nonce_time=%llu, rnd_size=%zu, saddr_size=%zu, uri_len=%zu, realm_len=%zu, bind_options=0x%x, mthd_e=%d",
             (unsigned long long) nonce_time,
             rnd_size,
             saddr_size,
             uri_len,
             realm_len,
             bind_options,
             (int) mthd_e);

  if (1)
  {
    probe_log ("calculate_nonce: taken branch if(1) /* timestamp block */");

    /* Add the timestamp to the hash calculation */
    uint8_t timestamp[TIMESTAMP_BIN_SIZE];
    /* If the nonce_time is milliseconds, then the same 48 bit value will repeat
     * every 8 919 years, which is more than enough to mitigate a replay attack */
#if TIMESTAMP_BIN_SIZE != 6
#error The code needs to be updated here
#endif
    timestamp[0] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 0)));
    timestamp[1] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 1)));
    timestamp[2] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 2)));
    timestamp[3] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 3)));
    timestamp[4] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 4)));
    timestamp[5] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 5)));

    /* Log the computed timestamp bytes and nonce_time */
    probe_log ("calculate_nonce: nonce_time=%llu -> timestamp bytes computed:", (unsigned long long) nonce_time);
    probe_log_hex ("timestamp", timestamp, sizeof (timestamp));

    {
      size_t hex_offset = (size_t) digest_get_size (da) * 2u;
      probe_log ("calculate_nonce: digest_get_size(da)=%u, hex offset in nonce=%zu",
                 (unsigned) digest_get_size (da), hex_offset);
    }

    MHD_bin_to_hex (timestamp,
                    sizeof (timestamp),
                    nonce + digest_get_size (da) * 2);

    probe_log ("calculate_nonce: called MHD_bin_to_hex for timestamp -> written at nonce+%u",
               (unsigned) (digest_get_size (da) * 2u));

    probe_log ("calculate_nonce: calling digest_update for timestamp (size=%zu)", sizeof (timestamp));
    digest_update (da,
                   timestamp,
                   sizeof (timestamp));
    probe_log ("calculate_nonce: returned from digest_update for timestamp");
  }

  if (rnd_size > 0)
  {
    /* Add the unique random value to the hash calculation */
    digest_update_with_colon (da);
    probe_log ("calculate_nonce: rnd_size > 0 -> adding rnd of size %zu to hash", rnd_size);
    digest_update (da,
                   rnd,
                   rnd_size);
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) &&
       (0 != saddr_size) )
  {
    /* Add full client address including source port to make unique nonces
     * for requests received exactly at the same time */
    digest_update_with_colon (da);
    probe_log ("calculate_nonce: binding NONE and saddr_size != 0 -> adding full saddr of size %zu to hash", saddr_size);
    digest_update (da,
                   saddr,
                   saddr_size);
  }
  if ( (0 != (bind_options & MHD_DAUTH_BIND_NONCE_CLIENT_IP)) &&
       (0 != saddr_size) )
  {
    /* Add the client's IP address to the hash calculation */
    digest_update_with_colon (da);
    probe_log ("calculate_nonce: binding CLIENT_IP and saddr_size != 0 -> adding client IP bytes to hash (family=%d)", (int) saddr->ss_family);
    if (AF_INET == saddr->ss_family)
      digest_update (da,
                     &((const struct sockaddr_in *) saddr)->sin_addr,
                     sizeof(((const struct sockaddr_in *) saddr)->sin_addr));
#ifdef HAVE_INET6
    else if (AF_INET6 == saddr->ss_family)
      digest_update (da,
                     &((const struct sockaddr_in6 *) saddr)->sin6_addr,
                     sizeof(((const struct sockaddr_in6 *) saddr)->sin6_addr));
#endif /* HAVE_INET6 */
  }
  if ( (MHD_DAUTHBind_NONCE_NONE == bind_options) ||
       (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI)))
  {
    /* Add the request method to the hash calculation */
    digest_update_with_colon (da);
    probe_log ("calculate_nonce: adding request method to hash (mthd_e=%d)", (int) mthd_e);
    if (MHD_HTTP_MTHD_OTHER != mthd_e)
    {
      uint8_t mthd_for_hash;
      if (MHD_HTTP_MTHD_HEAD != mthd_e)
        mthd_for_hash = (uint8_t) mthd_e;
      else /* Treat HEAD method in the same way as GET method */
        mthd_for_hash = (uint8_t) MHD_HTTP_MTHD_GET;
      digest_update (da,
                     &mthd_for_hash,
                     sizeof(mthd_for_hash));
    }
    else
      digest_update_str (da, method);
  }

  if (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI))
  {
    /* Add the request URI to the hash calculation */
    digest_update_with_colon (da);

    probe_log ("calculate_nonce: adding URI to hash (uri_len=%zu)", uri_len);
    digest_update (da,
                   uri,
                   uri_len);
  }
  if (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI_PARAMS))
  {
    /* Add the request URI parameters to the hash calculation */
    const struct MHD_HTTP_Req_Header *h;

    digest_update_with_colon (da);
    for (h = first_header; NULL != h; h = h->next)
    {
      if (MHD_GET_ARGUMENT_KIND != h->kind)
        continue;
      digest_update (da, "\0", 2);
      if (0 != h->header_size)
        digest_update (da, h->header, h->header_size);
      digest_update (da, "", 1);
      if (0 != h->value_size)
        digest_update (da, h->value, h->value_size);
    }
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) ||
       (0 != (bind_options & MHD_DAUTH_BIND_NONCE_REALM)))
  {
    /* Add the realm to the hash calculation */
    digest_update_with_colon (da);
    probe_log ("calculate_nonce: adding realm to hash (realm_len=%zu)", realm_len);
    digest_update (da,
                   realm,
                   realm_len);
  }
  if (1)
  {
    probe_log ("calculate_nonce: taken branch if(1) /* final hash block */");
    uint8_t hash[MAX_DIGEST];
    digest_calc_hash (da, hash);
    probe_log_hex ("final_hash_bytes", hash, (size_t) digest_get_size (da));
    MHD_bin_to_hex (hash,
                    digest_get_size (da),
                    nonce);
    probe_log ("calculate_nonce: MHD_bin_to_hex called for final hash -> nonce (hex) written");
  }

  probe_log ("calculate_nonce: exit");
}
