#include <stdio.h>
#include <stdint.h>
#include <stddef.h>
#include <inttypes.h>

/* Probe helper: log safe information about saddr and callers to /tmp/logs/2659.txt */
static void
log_probe_saddr(const struct sockaddr_storage *saddr,
                size_t saddr_size,
                unsigned int bind_options,
                const char *note)
{
  FILE *f = fopen("/tmp/logs/2659.txt", "a");
  if (NULL == f)
    return;

  fprintf(f, "calculate_nonce probe: %s\n", note ? note : "(null)");
  fprintf(f, "  saddr=%p saddr_size=%zu bind_options=0x%x\n",
          (const void *) saddr, saddr_size, bind_options);

  if (NULL == saddr)
  {
    fprintf(f, "  saddr pointer is NULL\n");
    fclose(f);
    return;
  }

  /* Safely attempt to read ss_family only if enough bytes were provided */
  if (saddr_size >= sizeof(((const struct sockaddr_storage *)0)->ss_family))
  {
    /* Access is safe only when saddr_size guarantees the family field exists */
    fprintf(f, "  ss_family=%u\n", (unsigned int) saddr->ss_family);
  }
  else
  {
    fprintf(f, "  ss_family=UNAVAILABLE (saddr_size too small to read)\n");
  }

  /* Dump up to a reasonable number of bytes that are actually available */
  size_t to_dump = saddr_size;
  if (to_dump > 128)
    to_dump = 128;
  const unsigned char *p = (const unsigned char *) saddr;
  fprintf(f, "  saddr bytes (first %zu):", to_dump);
  for (size_t i = 0; i < to_dump; ++i)
    fprintf(f, " %02x", p[i]);
  fprintf(f, "\n");

#ifdef HAVE_INET6
  if (saddr_size < sizeof(struct sockaddr_in6))
  {
    fprintf(f, "  NOTE: saddr_size (%zu) < sizeof(struct sockaddr_in6) (%zu) - potential OOB when reading sin6_addr\n",
            saddr_size, (size_t) sizeof(struct sockaddr_in6));
  }
#endif

  fclose(f);
}

static void
log_probe_entry(uint64_t nonce_time,
                size_t rnd_size,
                const struct sockaddr_storage *saddr,
                size_t saddr_size,
                size_t uri_len,
                size_t realm_len,
                unsigned int bind_options)
{
  FILE *f = fopen("/tmp/logs/2659.txt", "a");
  if (NULL == f)
    return;
  fprintf(f, "\n=== calculate_nonce ENTRY ===\n");
  fprintf(f, "  nonce_time=%" PRIu64 " rnd_size=%zu uri_len=%zu realm_len=%zu bind_options=0x%x saddr=%p saddr_size=%zu\n",
          nonce_time, rnd_size, uri_len, realm_len, bind_options, (const void *) saddr, saddr_size);
  fclose(f);
}

static void
calculate_nonce (uint64_t nonce_time,
                 enum MHD_HTTP_Method mthd_e,
                 const char *method,
                 const char *rnd,
                 size_t rnd_size,
                 const struct sockaddr_storage *saddr,
                 size_t saddr_size,
                 const char *uri,
                 size_t uri_len,
                 const struct MHD_HTTP_Req_Header *first_header,
                 const char *realm,
                 size_t realm_len,
                 unsigned int bind_options,
                 struct DigestAlgorithm *da,
                 char *nonce)
{
  /* Log entry parameters for later analysis */
  log_probe_entry(nonce_time, rnd_size, saddr, saddr_size, uri_len, realm_len, bind_options);

  mhd_assert (! da->hashing);
  if (1)
  {
    /* Add the timestamp to the hash calculation */
    uint8_t timestamp[TIMESTAMP_BIN_SIZE];
    /* If the nonce_time is milliseconds, then the same 48 bit value will repeat
     * every 8 919 years, which is more than enough to mitigate a replay attack */
#if TIMESTAMP_BIN_SIZE != 6
#error The code needs to be updated here
#endif
    timestamp[0] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 0)));
    timestamp[1] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 1)));
    timestamp[2] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 2)));
    timestamp[3] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 3)));
    timestamp[4] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 4)));
    timestamp[5] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 5)));
    MHD_bin_to_hex (timestamp,
                    sizeof (timestamp),
                    nonce + digest_get_size (da) * 2);
    digest_update (da,
                   timestamp,
                   sizeof (timestamp));
  }
  if (rnd_size > 0)
  {
    /* Add the unique random value to the hash calculation */
    digest_update_with_colon (da);
    digest_update (da,
                   rnd,
                   rnd_size);
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) &&
       (0 != saddr_size) )
  {
    /* Log the saddr before using the raw buffer */
    log_probe_saddr(saddr, saddr_size, bind_options, "before full saddr digest_update");

    /* Add full client address including source port to make unique nonces
     * for requests received exactly at the same time */
    digest_update_with_colon (da);
    digest_update (da,
                   saddr,
                   saddr_size);
  }
  if ( (0 != (bind_options & MHD_DAUTH_BIND_NONCE_CLIENT_IP)) &&
       (0 != saddr_size) )
  {
    /* Add the client's IP address to the hash calculation */
    digest_update_with_colon (da);

    /* Probe prior to the family-specific access to detect potential truncation */
    log_probe_saddr(saddr, saddr_size, bind_options, "before client IP (AF_INET/AF_INET6) digest_update");

    if (AF_INET == saddr->ss_family)
      digest_update (da,
                     &((const struct sockaddr_in *) saddr)->sin_addr,
                     sizeof(((const struct sockaddr_in *) saddr)->sin_addr));
#ifdef HAVE_INET6
    else if (AF_INET6 == saddr->ss_family)
      digest_update (da,
                     &((const struct sockaddr_in6 *) saddr)->sin6_addr,
                     sizeof(((const struct sockaddr_in6 *) saddr)->sin6_addr));
#endif /* HAVE_INET6 */
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) ||
       (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI)))
  {
    /* Add the request method to the hash calculation */
    digest_update_with_colon (da);
    if (MHD_HTTP_MTHD_OTHER != mthd_e)
    {
      uint8_t mthd_for_hash;
      if (MHD_HTTP_MTHD_HEAD != mthd_e)
        mthd_for_hash = (uint8_t) mthd_e;
      else /* Treat HEAD method in the same way as GET method */
        mthd_for_hash = (uint8_t) MHD_HTTP_MTHD_GET;
      digest_update (da,
                     &mthd_for_hash,
                     sizeof(mthd_for_hash));
    }
    else
      digest_update_str (da, method);
  }

  if (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI))
  {
    /* Add the request URI to the hash calculation */
    digest_update_with_colon (da);

    digest_update (da,
                   uri,
                   uri_len);
  }
  if (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI_PARAMS))
  {
    /* Add the request URI parameters to the hash calculation */
    const struct MHD_HTTP_Req_Header *h;

    digest_update_with_colon (da);
    for (h = first_header; NULL != h; h = h->next)
    {
      if (MHD_GET_ARGUMENT_KIND != h->kind)
        continue;
      digest_update (da, "\0", 2);
      if (0 != h->header_size)
        digest_update (da, h->header, h->header_size);
      digest_update (da, "", 1);
      if (0 != h->value_size)
        digest_update (da, h->value, h->value_size);
    }
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) ||
       (0 != (bind_options & MHD_DAUTH_BIND_NONCE_REALM)))
  {
    /* Add the realm to the hash calculation */
    digest_update_with_colon (da);
    digest_update (da,
                   realm,
                   realm_len);
  }
  if (1)
  {
    uint8_t hash[MAX_DIGEST];
    digest_calc_hash (da, hash);
    MHD_bin_to_hex (hash,
                    digest_get_size (da),
                    nonce);
  }
}
