#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <inttypes.h>
#include <stddef.h>
#include <sys/socket.h>
#include <netinet/in.h>

/* Probe logging helper. Appends formatted messages to /tmp/logs/2659.txt */
static void
log_probe (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/2659.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  fprintf (f, "\n");
  va_end (ap);
  fclose (f);
}

static void
calculate_nonce (uint64_t nonce_time,
                 enum MHD_HTTP_Method mthd_e,
                 const char *method,
                 const char *rnd,
                 size_t rnd_size,
                 const struct sockaddr_storage *saddr,
                 size_t saddr_size,
                 const char *uri,
                 size_t uri_len,
                 const struct MHD_HTTP_Req_Header *first_header,
                 const char *realm,
                 size_t realm_len,
                 unsigned int bind_options,
                 struct DigestAlgorithm *da,
                 char *nonce)
{
  /* Log entry and key parameters to allow reproducing the violation scenario */
  log_probe ("calculate_nonce ENTRY: nonce_time=%" PRIu64 ", mthd_e=%d, method=%p, rnd=%p, rnd_size=%zu, saddr=%p, saddr_size=%zu, uri=%p, uri_len=%zu, first_header=%p, realm=%p, realm_len=%zu, bind_options=0x%x, da=%p, nonce=%p",
             nonce_time,
             (int) mthd_e,
             (const void *) method,
             (const void *) rnd,
             rnd_size,
             (const void *) saddr,
             saddr_size,
             (const void *) uri,
             uri_len,
             (const void *) first_header,
             (const void *) realm,
             realm_len,
             bind_options,
             (void *) da,
             (void *) nonce);

  /* Safely log saddr family if we can read that field without exceeding the
   * provided saddr_size. We compare against the size of the family member so
   * we don't dereference into memory that might not be present. */
  if (NULL != saddr && saddr_size >= sizeof (((const struct sockaddr_storage *) 0)->ss_family))
  {
    log_probe ("calculate_nonce: saddr->ss_family=%d (logged because saddr_size >= %zu)",
               (int) saddr->ss_family,
               (size_t) sizeof (((const struct sockaddr_storage *) 0)->ss_family));
  }
  else if (NULL != saddr)
  {
    log_probe ("calculate_nonce: saddr present but saddr_size (%zu) < sizeof(ss_family) (%zu); not reading ss_family", saddr_size, (size_t) sizeof (((const struct sockaddr_storage *) 0)->ss_family));
  }

  mhd_assert (! da->hashing);
  if (1)
  {
    /* Add the timestamp to the hash calculation */
    uint8_t timestamp[TIMESTAMP_BIN_SIZE];
    /* If the nonce_time is milliseconds, then the same 48 bit value will repeat
     * every 8 919 years, which is more than enough to mitigate a replay attack */
#if TIMESTAMP_BIN_SIZE != 6
#error The code needs to be updated here
#endif
    timestamp[0] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 0)));
    timestamp[1] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 1)));
    timestamp[2] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 2)));
    timestamp[3] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 3)));
    timestamp[4] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 4)));
    timestamp[5] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 5)));
    MHD_bin_to_hex (timestamp,
                    sizeof (timestamp),
                    nonce + digest_get_size (da) * 2);
    digest_update (da,
                   timestamp,
                   sizeof (timestamp));
    log_probe ("calculate_nonce: timestamp appended and digest_update called with %zu bytes", sizeof (timestamp));
  }
  if (rnd_size > 0)
  {
    /* Add the unique random value to the hash calculation */
    digest_update_with_colon (da);
    log_probe ("calculate_nonce: adding rnd to hash; rnd=%p, rnd_size=%zu", (const void *) rnd, rnd_size);
    digest_update (da,
                   rnd,
                   rnd_size);
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) &&
       (0 != saddr_size) )
  {
    /* Add full client address including source port to make unique nonces
     * for requests received exactly at the same time */
    digest_update_with_colon (da);

    /* Log the exact call that is potentially unsafe: digest_update with
     * saddr and saddr_size. This is the call reported in the violation
     * scenario when saddr_size may exceed the actual object size pointed to
     * by saddr. */
    log_probe ("calculate_nonce: BIND_NONCE_NONE branch taken: will call digest_update with saddr=%p, saddr_size=%zu", (const void *) saddr, saddr_size);

    digest_update (da,
                   saddr,
                   saddr_size);
  }
  if ( (0 != (bind_options & MHD_DAUTH_BIND_NONCE_CLIENT_IP)) &&
       (0 != saddr_size) )
  {
    /* Add the client's IP address to the hash calculation */
    digest_update_with_colon (da);
    if (AF_INET == saddr->ss_family)
    {
      size_t fam_size = sizeof(((const struct sockaddr_in *) saddr)->sin_addr);
      log_probe ("calculate_nonce: BIND_NONCE_CLIENT_IP branch: AF_INET detected (saddr=%p); using sin_addr size=%zu", (const void *) saddr, fam_size);
      digest_update (da,
                     &((const struct sockaddr_in *) saddr)->sin_addr,
                     fam_size);
    }
#ifdef HAVE_INET6
    else if (AF_INET6 == saddr->ss_family)
    {
      size_t fam6_size = sizeof(((const struct sockaddr_in6 *) saddr)->sin6_addr);
      log_probe ("calculate_nonce: BIND_NONCE_CLIENT_IP branch: AF_INET6 detected (saddr=%p); using sin6_addr size=%zu", (const void *) saddr, fam6_size);
      digest_update (da,
                     &((const struct sockaddr_in6 *) saddr)->sin6_addr,
                     fam6_size);
    }
#endif /* HAVE_INET6 */
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) ||
       (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI)))
  {
    /* Add the request method to the hash calculation */
    digest_update_with_colon (da);
    if (MHD_HTTP_MTHD_OTHER != mthd_e)
    {
      uint8_t mthd_for_hash;
      if (MHD_HTTP_MTHD_HEAD != mthd_e)
        mthd_for_hash = (uint8_t) mthd_e;
      else /* Treat HEAD method in the same way as GET method */
        mthd_for_hash = (uint8_t) MHD_HTTP_MTHD_GET;
      log_probe ("calculate_nonce: adding method (numeric) to hash: mthd_for_hash=%u", (unsigned) mthd_for_hash);
      digest_update (da,
                     &mthd_for_hash,
                     sizeof(mthd_for_hash));
    }
    else
    {
      log_probe ("calculate_nonce: adding method (string) to hash: method=%p", (const void *) method);
      digest_update_str (da, method);
    }
  }

  if (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI))
  {
    /* Add the request URI to the hash calculation */
    digest_update_with_colon (da);

    log_probe ("calculate_nonce: adding URI to hash: uri=%p, uri_len=%zu", (const void *) uri, uri_len);
    digest_update (da,
                   uri,
                   uri_len);
  }
  if (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI_PARAMS))
  {
    /* Add the request URI parameters to the hash calculation */
    const struct MHD_HTTP_Req_Header *h;

    digest_update_with_colon (da);
    for (h = first_header; NULL != h; h = h->next)
    {
      if (MHD_GET_ARGUMENT_KIND != h->kind)
        continue;
      log_probe ("calculate_nonce: processing URI param header: header=%p, header_size=%zu, value=%p, value_size=%zu", (const void *) h->header, h->header_size, (const void *) h->value, h->value_size);
      digest_update (da, "\0", 2);
      if (0 != h->header_size)
        digest_update (da, h->header, h->header_size);
      digest_update (da, "", 1);
      if (0 != h->value_size)
        digest_update (da, h->value, h->value_size);
    }
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) ||
       (0 != (bind_options & MHD_DAUTH_BIND_NONCE_REALM)))
  {
    /* Add the realm to the hash calculation */
    digest_update_with_colon (da);
    log_probe ("calculate_nonce: adding realm to hash: realm=%p, realm_len=%zu", (const void *) realm, realm_len);
    digest_update (da,
                   realm,
                   realm_len);
  }
  if (1)
  {
    uint8_t hash[MAX_DIGEST];
    digest_calc_hash (da, hash);
    MHD_bin_to_hex (hash,
                    digest_get_size (da),
                    nonce);
    log_probe ("calculate_nonce: final hash computed and hex written to nonce=%p", (void *) nonce);
  }

  log_probe ("calculate_nonce EXIT: nonce buffer at %p", (void *) nonce);
}
