#include <stdio.h>
#include <stdint.h>

/* Probe logging function to record control flow and key state.  */
static void digest_probe_log_state(const char *tag,
                                   struct MHD_Connection *connection,
                                   unsigned int nonce_timeout,
                                   uint32_t max_nc)
{
  FILE *f = fopen("/tmp/logs/2697.txt", "a");
  if (f != NULL)
    {
      /* Log basic entry with pointers and numeric values. */
      fprintf(f, "[%s] connection=%p daemon=%p nonce_timeout=%u max_nc=%lu\n",
              tag,
              (void *) connection,
              (void *) (connection ? connection->daemon : ((void *)0)),
              nonce_timeout,
              (unsigned long) max_nc);

      /* Log daemon defaults (same fields accessed by original code). */
      if (connection && connection->daemon)
        {
          fprintf(f, "[%s] daemon->dauth_def_nonce_timeout=%u daemon->dauth_def_max_nc=%lu\n",
                  tag,
                  connection->daemon->dauth_def_nonce_timeout,
                  (unsigned long) connection->daemon->dauth_def_max_nc);
        }

      fclose(f);
    }
}

static enum MHD_DigestAuthResult
digest_auth_check_all (struct MHD_Connection *connection,
                       const char *realm,
                       const char *username,
                       const char *password,
                       const uint8_t *userdigest,
                       unsigned int nonce_timeout,
                       uint32_t max_nc,
                       enum MHD_DigestAuthMultiQOP mqop,
                       enum MHD_DigestAuthMultiAlgo3 malgo3)
{
  enum MHD_DigestAuthResult res;
  char *buf;
  struct DigestAlgorithm da;

  /* Log entry state */
  digest_probe_log_state("entry", connection, nonce_timeout, max_nc);

  buf = NULL;
  digest_setup_zero (&da);

  /* Log before applying defaults */
  digest_probe_log_state("before_defaults", connection, nonce_timeout, max_nc);

  if (0 == nonce_timeout)
    nonce_timeout = connection->daemon->dauth_def_nonce_timeout;

  if (0 == max_nc)
    {
      /* Log the modification about to occur to the by-value parameter 'max_nc'. */
      FILE *f = fopen("/tmp/logs/2697.txt", "a");
      if (f != NULL)
        {
          fprintf(f, "[modify_max_nc] max_nc (old)=%lu daemon->dauth_def_max_nc=%lu\n",
                  (unsigned long) max_nc,
                  (unsigned long) connection->daemon->dauth_def_max_nc);
          fclose(f);
        }

      max_nc = connection->daemon->dauth_def_max_nc;

      /* Log after modification */
      digest_probe_log_state("after_modify_max_nc", connection, nonce_timeout, max_nc);
    }
  else
    {
      /* If no modification happened, still log the state */
      digest_probe_log_state("no_modify_max_nc", connection, nonce_timeout, max_nc);
    }

  res = digest_auth_check_all_inner (connection, realm, username, password,
                                     userdigest,
                                     nonce_timeout,
                                     max_nc, mqop, malgo3,
                                     &buf, &da);

  /* Log the result and buffer/algorithm state for traceability */
  {
    FILE *f = fopen("/tmp/logs/2697.txt", "a");
    if (f != NULL)
      {
        fprintf(f, "[after_inner] res=%d buf=%p da_addr=%p\n",
                (int) res, (void *) buf, (void *) &da);
        fclose(f);
      }
  }

  digest_deinit (&da);
  if (NULL != buf)
    free (buf);

  /* Final log before returning */
  digest_probe_log_state("exit", connection, nonce_timeout, max_nc);

  return res;
}
