#include <stdio.h>
#include <stddef.h>

/* Probe helper: write logs about the DigestAlgorithm state to /tmp/logs/2733.txt.
   Keep the helper read-only with respect to the pointed object (takes const pointer).
*/
static void digest_log_state(const struct DigestAlgorithm *da, const char *stage, unsigned int result)
{
    FILE *f = fopen("/tmp/logs/2733.txt", "a");
    if (NULL == f)
        return;

    /* Log pointer, stage, and key state fields used by digest_get_size. Casting to unsigned
       for booleans/enums so this compiles regardless of the exact types of these members. */
    fprintf(f, "[%s] digest_get_size called: da=%p, uninitialised=%u, algo_selected=%u, algo=%u, result=%u\n",
            stage,
            (void *)da,
            (unsigned)(da ? da->uninitialised : 0u),
            (unsigned)(da ? da->algo_selected : 0u),
            (unsigned)(da ? da->algo : 0u),
            result);

    fclose(f);
}

_MHD_static_inline unsigned int
digest_get_size (struct DigestAlgorithm *da)
{
  /* Log entry state before assertions */
  digest_log_state(da, "entry-before-asserts", 0u);

  mhd_assert (! da->uninitialised);
  mhd_assert (da->algo_selected);

#ifdef MHD_MD5_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_MD5 == da->algo) {
    /* Log decision and returned size */
    digest_log_state(da, "branch-md5", MD5_DIGEST_SIZE);
    return MD5_DIGEST_SIZE;
  }
#endif /* MHD_MD5_SUPPORT */
#ifdef MHD_SHA256_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_SHA256 == da->algo) {
    digest_log_state(da, "branch-sha256", SHA256_DIGEST_SIZE);
    return SHA256_DIGEST_SIZE;
  }
#endif /* MHD_SHA256_SUPPORT */
#ifdef MHD_SHA512_256_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_SHA512_256 == da->algo) {
    digest_log_state(da, "branch-sha512_256", SHA512_256_DIGEST_SIZE);
    return SHA512_256_DIGEST_SIZE;
  }
#endif /* MHD_SHA512_256_SUPPORT */

  /* Log the unexpected path before asserting */
  digest_log_state(da, "unexpected-path", 0u);
  mhd_assert (0); /* May not happen */
  return 0;
}
